-- ----------------------------
-- Table structure for hxb_admin
-- ----------------------------
DROP TABLE IF EXISTS `hxb_admin`;
CREATE TABLE `hxb_admin` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `username` varchar(255) CHARACTER SET utf8mb4 NOT NULL COMMENT '管理员登录名',
  `userpass` varchar(255) CHARACTER SET utf8mb4 NOT NULL COMMENT '管理员秘密码',
  `nickname` varchar(255) CHARACTER SET utf8mb4 DEFAULT '管理员' COMMENT '管理员昵称',
  `mobile` varchar(255) CHARACTER SET utf8mb4 DEFAULT '0' COMMENT '管理员电话',
  `ctime` int(11) DEFAULT '0' COMMENT '创建时间',
  `status` tinyint(1) DEFAULT '1' COMMENT '状态:0为不可用，1为可用',
  `headimg` varchar(255) NOT NULL COMMENT '管理员头像',
  `email` varchar(100) DEFAULT NULL COMMENT '电子邮箱',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='管理员表';

-- ----------------------------
-- Records of hxb_admin
-- ----------------------------
INSERT INTO `hxb_admin` VALUES ('1', 'hxb', '72b6ba460073969243c24a440b7a29d4', '黄晓滨', '15606001624', '1509883801', '1', '2', '1010920955@qq.com');

-- ----------------------------
-- Table structure for hxb_book
-- ----------------------------
DROP TABLE IF EXISTS `hxb_book`;
CREATE TABLE `hxb_book` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `user_id` int(11) DEFAULT NULL COMMENT '作家id',
  `username` varchar(33) DEFAULT NULL COMMENT '作家姓名',
  `name` varchar(33) DEFAULT NULL COMMENT '作品名称',
  `class_id` int(11) DEFAULT NULL COMMENT '类型id',
  `class_name` varchar(33) DEFAULT NULL COMMENT '分类名称',
  `commend` tinyint(1) DEFAULT '1' COMMENT '是否推荐,1为否,2为是',
  `count` int(11) DEFAULT '0' COMMENT '字数',
  `collection` int(11) DEFAULT '0' COMMENT '收藏数',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '作品状态:1代表连载中,2代表已完结',
  `label` text COMMENT '作品标签',
  `intro` text COMMENT '作品介绍',
  `ctime` int(11) DEFAULT NULL COMMENT '创建时间',
  `outh` int(11) DEFAULT '13' COMMENT '阅读权限,13代表13章节前面内容无需登录可以观看，后面需要登录',
  `utime` int(11) DEFAULT NULL COMMENT '更新时间',
  `img` varchar(255) DEFAULT NULL COMMENT '封面图片',
  `word` varchar(50) DEFAULT NULL COMMENT '扉页寄语',
  `on_sale` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0表示下架，1表示上架',
  `on_sale_time` int(11) NOT NULL DEFAULT '0' COMMENT '上架时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=42 DEFAULT CHARSET=utf8mb4 COMMENT='作品表';

-- ----------------------------
-- Records of hxb_book
-- ----------------------------
INSERT INTO `hxb_book` VALUES ('1', '1', '鹅是老五', '不朽凡人', '1', '玄幻', '1', '133000', '6553621', '1', '', '在这里，拥有灵根才能修仙，所有凡根注定只是凡人。', '1515599780', '13', '1510203669', 'img/2018-01-10/5a5637a4befaf.jpg', '阿狸咖喱log倪敏江农民km哦哦咯哦明明啦啦辅导费热让他热', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('2', '2', '天蚕土豆', '斗破苍穹', '1', '玄幻', '1', '330000', '10086', '0', '热血', '心潮澎湃，无限幻想，迎风挥击千层浪，少年不败热血！', '1515079299', '13', '1515079299', 'img/2017-11-11/33e6938dc363d.jpg', '斗破苍穹', '1', '1515849465');
INSERT INTO `hxb_book` VALUES ('3', '3', '辰东', '圣墟', '1', '玄幻', '1', '1230000', '520013', '1', '热血', '在破败中崛起，在寂灭中复苏。沧海成尘，雷电枯竭……', '151507929', '13', '151507929', 'img/2017-11-11/33e6938dc3644.jpg', '在破败中崛起，在寂灭中复苏。', '1', '1515849691');
INSERT INTO `hxb_book` VALUES ('4', '4', '九灯和善', '超品巫师', '5', '都事', '0', '500000', '520033', '1', '励志,法师,玄学,练功流', '重生过去、畅想未来、梦幻现实，再塑传奇人生！', '151507929', '13', '151507929', 'img/2017-11-11/33e6938dc3655.jpg', '系统请排队', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('5', '5', '梨花白石', '系统请排队', '1', '玄幻', '0', '350000', '0', '1', '宅男,系统流,轻松', '沈枫穿越异界半年，迟迟等不到系统，一直过着咸鱼的生活，有一天，他鬼使神差地对着夜空划过的流星许愿——“给我一个系统吧！”', '151507929', '13', '151507929', 'img/2018-01-05/1.jpg', '我被系统轮着上系统请排队', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('6', '6', '铁牛仙', '最道士', '1', '玄幻', '1', '660000', '0', '1', '系统流,豪门,铁血', '天地规则重置，一切都变了模样。\r\n　　在这个觉醒的时代，只有强者才有话语权。\r\n　　杨豪：一个人，一只狗，天下任我行走。\r\n　　狗：我是神兽！', '151507929', '13', '151507929', 'img/2018-01-05/2.jpg', '铁牛仙新书最道士', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('7', '7', '十三楼的猪', '最强原始霸主', '1', '玄幻', '1', '1330000', '0', '1', '异兽流,杀手,热血', '穿越史前文明，成为最强霸主！\r\n　　程序猿林超带着《原始霸主》游戏程序穿越到史前一亿年，凶兽、暴龙、巨虫和原始人并存的原始世界。坑爹的是，他一睁开眼竟是发现自己穿越成了一头刚出生的霸王龙宝宝！\r\n　　“终有一日，我会成为称霸史前世界的最强霸主，嗷呜~”', '15150792', '13', '15150792', 'img/2018-01-05/3.jpg', '穿越史前文明，成最强霸主', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('8', '8', '山海成空', '盗尽诸天世界', '1', '玄幻', '1', '2100000', '0', '1', '位面,冷酷,孤儿,无限流', '漫漫长生路，无尽世界旅，以诸天万界为供养，成就无上大道。窃天地之万物，取众生之智慧，此为我道，方成大盗。武林外传，四大名捕，大唐双龙，神话蜀山，凡人星辰，吞噬莽荒，阳神永生，神墓遮天……', '15150792', '13', '15150792', 'img/2018-01-05/4.jpg', '盗尽诸天世界，神墓遮天', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('9', '9', '嗷星小领主 ', '傀儡皇帝的人形机甲修炼手册', '2', '奇幻', '0', '330000', '0', '1', '争霸流', '我的名字是伊文，美尼斯帝国的太阳王，穿越者大军的一员，人民视我为神的化身，统御着数百万平方公里，两亿人口的封建大帝国。但我的对手是星际文明，他们的星际战舰就停泊在王都上空，按个按钮就能爆了这颗星球。宗族认为我不配登基，王庭被配偶把持，地方总督听调不听宣。\r\n　　我想知道我还能活多久，能不能删号重练？', '15150792', '13', '15150792', 'img/2018-01-05/5.jpg', '异界傀儡皇帝的内忧外患', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('10', '10', '煮水饺', '\r\n聊斋求道', '3', '仙侠', '1', '450000', '0', '1', '淡定,玄学', '炼过尸，撞过狐，扮过人间王侯戏白蛇。\r\n也当僧，也作婿，拜入青丘龙宫娶道种。\r\n尔来通天建木八万岁，不及元神证长生！', '15150792', '13', '15150792', 'img/2018-01-05/6.jpg', '炼尸撞狐，聊斋证长生！', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('11', '11', '不装纯也甜', '不朽大圣人', '3', '仙侠', '1', '550000', '0', '1', '无敌文,法师,轻松,鬼怪', '楚飞，一个只想靠忽悠腹黑打酱油的咸鱼，没想到有一天会穿越到一个古典神话世界。\r\n　　诛妖魔鬼怪，斩魑魅魍魉，斗阴司，下地府，闹龙宫，伐天庭，结圣果，封神应劫，踏破万道。\r\n　　眉心一只怪眼，上窥苍冥，下探九幽，仙庭圣果，一一呈现。\r\n　　最不像圣人的他，无奈的踏上了一条独一无二的大圣人之路。', '15150792', '13', '15150792', 'img/2018-01-05/7.jpg', '腹黑穿仙侠，不朽大圣人！', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('12', '12', '来不及忧伤', '二青', '3', '仙侠', '0', '660000', '0', '1', '穿越,鬼怪', '岑青穿越了，重生成一条青蛇，名叫二青。\r\n　　后来，他知道这个世界有猴子，有白蛇。\r\n　　游遍青山蛇未老，蓦然回首已成妖！', '15150792', '13', '15150792', 'img/2018-01-05/8.jpg', '重生青蛇，名叫二青！', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('13', '13', '牙齿', '左道之士', '3', '仙侠', '0', '660000', '0', '1', '穿越', '路漫漫其修远兮，吾将上下而求索！\r\n　　道之所在，虽万千人逆之，吾往矣！', '15150792', '13', '15150792', 'img/2018-01-05/9.jpg', '漫漫大道，左道修士！', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('14', '14', '九华居士', '\r\n极道魔主', '4', '武侠', '0', '660000', '0', '1', '勇猛,练功流,鬼怪', '天地如烘炉，人命为草芥。\r\n　　在这扭曲、狰狞的世界中，想要活下去，唯有以杀止杀！\r\n　　世道颓丧，武道末世，如何跨越武道的通天之途？\r\n　　小城捕快穿越觉醒古怪异能，从此走上一条前无古人的武道之路。', '15150792', '13', '15150792', 'img/2018-01-05/10.jpg', '小城捕快穿越觉醒古怪异能', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('15', '15', '小毒君', '向着娱乐圈开炮 ', '5', '都市', '0', '660000', '0', '1', '位面,明星,系统流,轻松', '“欢迎收听本期娱乐大吐槽：前几天某小鲜肉结婚，疯狂的粉丝打赏上亿华币，这件事向我们证明了：长得帅是可以当饭吃的！”\r\n　　‘叮，吐槽成功，本次吐槽获得一千三百万人认同，获得一百三十万兑换点。’\r\n　　听到系统提示，杨智泪流满面：该死的吐槽系统，我这是要把娱乐圈的人得罪完的节奏啊！', '15150792', '13', '15150792', 'img/2018-01-05/11.jpg', '欢迎收听本期娱乐大吐槽', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('16', '16', '蒸炸', '第36号当铺 ', '5', '都市', '0', '560000', '0', '1', '猥琐,重生', '亲情，不行，人活着不能没有亲情！\r\n　　爱情，算了，虽然这辈子活了三十多年也没有找到真爱，但是这东西不能拿来交换。\r\n　　友情、健康，那就更不行了，人活着没有友情，失去健康，那活着还有什么意思，这些一个都不能少。', '15150792', '13', '15150792', 'img/2018-01-05/12.jpg', '节操这东西也能用来交换？', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('17', '17', '茗夜', '两界真武', '5', '都市', '0', '652300', '0', '1', '位面,学生,练功流', '从小练武的姜真武突然得到了能穿梭到符文之地瓦罗兰大陆的能力，从此，他走上了两界强者的道路！\r\n　　这里是一片雪原，这里是一片冰霜，这里猛兽横行，这里是弗雷尔卓德，这里是瓦罗兰大陆！\r\n　　这里有正义之地，这里有战争学院，这里有上百英雄人物……\r\n　　而一开始，姜真武比普通人还弱。', '15150792', '13', '15150792', 'img/2018-01-05/13.jpg', '这里是瓦罗兰大陆！', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('18', '18', '泥白佛 ', '文骚', '5', '都市', '0', '100860', '0', '1', '学生,赚钱,轻松,重生', '重生了，怎么办，先抄《悟空传》吧。\r\n　　为了抄《悟空传》，先要写《西游记》！\r\n　　为了写《西游记》，先要熟读《封神演义》！', '15150792', '13', '15150792', 'img/2018-01-05/14.jpg', '重生不抄悟空传给你好看！', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('19', '19', '步枪打蚊子', '海洋修士', '5', '都市', '0', '100120', '0', '1', '护短,法师,赚钱,重生', '沉船、宝藏、神秘的大海究竟隐藏了多少秘密？\r\n　　重生、奇遇、重走人生路又会掀开何等惊涛骇浪？\r\n　　混迹世俗之中的海洋修士，上演一段人与海洋的和谐共存之旅！', '15150792', '13', '15150792', 'img/2018-01-05/15.jpg', '混迹世俗之中的海洋修士', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('20', '20', '名武', '三国之熙皇', '7', '历史', '0', '255133', '0', '1', '争霸流,冷酷', '我为袁绍二子，袁熙。\r\n　　醒掌天下权，醉卧美人膝，英雄无悔，枭雄无泪，乱世之中，唯有兵戈才能赢得尊严，唯有冷血才能取得胜利，这天下只能有一个声音。', '15150792', '13', '15150792', 'img/2018-01-05/16.jpg', '唯有兵戈才能赢得尊严', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('21', '21', '非玩家角色', '穷鬼的上下两千年', '7', '历史', '0', '520013', '0', '1', '变身,穿越,轻松', '她曾经历了长平之战，见证了数十万人的坑杀。她曾率领大秦铁骑，与六国逐鹿天下。她见过天下三分，山河破碎。也听过那袅袅的隆中琴音。贞观盛世她曾一醉今朝，那千古女帝又是如何芳华？她鲜衣怒马过，也曾羽扇纶巾。做过田舍农，也为过教书生。却没人知道，这么一个人，活了两千年。嘛，比较轻松悠哉的历史文吧，因为个人原因可能并不能做到完全符合历史，经得起考证。但我会尽力查全资料来写的。第一次写这种文章，我还是希望写的有趣一些，哈哈。最后，变身慎入哈，单身向的。', '15150792', '13', '15150792', 'img/2018-01-05/17.jpg', '这么一个人，活了两千年。', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('22', '22', '灰头小宝2', '\r\n北宋最强大少爷', '7', '历史', '0', '520046', '0', '1', '种田文,腹黑,胖子,豪门', '“总有刁民想害朕”\r\n　　“凛冬将至”\r\n　　“下雨收衣服啦！”\r\n　　本书讲述，迫害妄想风格的天才少爷，做成了许多神奇大事、以猥琐风格获得满堂喝彩的故事。\r\n　　顺便，开局一只萝莉，还有个宰相老爸……\r\n　　~\r\n　　已有三百万字完本精品书《大宋的最强纨绔子弟》，请大家放心收藏阅读', '15150792', '13', '15150792', 'img/2018-01-05/18.jpg', '开局一只萝莉，还有个老爸', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('23', '23', '汉风雄烈', '长风万里尽汉歌', '7', '历史', '0', '552222', '0', '1', '勇猛,系统流', '穿越水浒第一背信弃义之人陆谦，从杀高衙内亡命江湖开始……\r\n　　……\r\n　　力挽天倾，砸碎赵宋，镇压女真，复我华夏浩荡雄风。直叫那：天威卷地过黄河，万里羌人尽汉歌。', '15150792', '13', '15150792', 'img/2018-01-05/19.jpg', '复我华夏浩荡雄风。', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('24', '24', '秋刀鱼的汁味', '\r\n狼烟起', '7', '历史', '0', '6699952', '0', '1', '特工,铁血', '在村民们眼中，他就是个不自量力的大傻子。在弟弟妹妹眼中，他是大哥，如大山一般坚韧。', '15150792', '13', '15150792', 'img/2018-01-05/20.jpg', '狼烟起：血染山河！', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('25', '25', '紫墟', '传奇心灵术士', '9', '游戏', '0', '52000', '0', '1', '亡灵,冷酷,法师,系统流', '和平与繁荣眷顾着亚南这个庞大的主世界，奥术帝国威慑着整个多元宇宙；诸神与传奇强者共同见证着这整个主位面的强盛与昌隆。\r\n但危机仍然存在，掩藏在安稳的奥术帝国与诸神统治下的亚南世界。\r\n法师与诸神之间的裂痕似乎无从弥补，外位面的目光从来不曾从这片广袤的大地上移开，甚至就连最为可怕的邪物，也被有心人悄然的掌握在了手中。\r\n在这逐渐动荡的世界中，有弱小者偏安一隅，有强大者乘势崛起。而在这片大陆上一个不起眼的小地方。\r\n一个弱小的心灵术士却逐步登上了这最辉煌的舞台。\r\n……\r\n另，请勿被开头欺骗，这并非是网游小说，而是一篇伪dnd规则的游戏异界小说。', '15150792', '13', '15150792', 'img/2018-01-05/21.jpg', '传奇心灵术士，天地我为王', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('26', '26', '猎魔猫', '\r\n虫临暗黑', '9', '游戏', '0', '66000', '0', '1', '随身流', '地狱之内，三魔神带着四小弟缩到熔岩河的角落边瑟瑟发抖，在它们面前，无穷无尽的虫群分开了一条道路，一个身穿印花睡衣的家伙睡眼蓬松的走到他们面前。\r\n　　“嗯，不错，一觉睡醒，该搞定的都搞定了...那么接下来......”他的目光转向天空，脸上露出了不怀好意的笑容。\r\n　　高阶天堂之上，英普瑞斯突然浑身一抖，由心底子里发出了一丝寒意。\r\n　　“不好！莫非那万界之灾要上天？!”\r\n　　（新书新人~求关爱~求照顾~求包养~）', '15150792', '13', '15150792', 'img/2018-01-05/22.jpg', '无尽虫群，降临暗黑世界。', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('27', '27', '楚留先', '我是妖皇 ', '9', '游戏', '0', '425000', '0', '1', '位面,学生,热血,穿越', '地球突然遭到毁灭性攻击，攻击到来之前，所有人类裸穿到了异界的边荒之地。\r\n　　从信息时代回归原始社会，人类踏遍荆棘，在血与火中摸索着前行，从部落到城邦，从城邦到帝国。\r\n　　六年后，守护人类的结界破碎，异界某个强大种族发现了人类踪迹，并果断发动战争，那一战，人类惨败，尽数沦为猪狗般的奴隶。\r\n　　此时人类方才知道，原来异界还有一个自称“人族”的种族，并将地球人当成“妖物”看待。\r\n　　柳直，万千穿越者中的一员，在经历了四年的奴隶生涯后，一梦十年，带着修炼功法回到了穿越当天……', '15150792', '13', '15150792', 'img/2018-01-05/23.jpg', '一梦十年，带功法回归！', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('28', '28', '吐槽是福', '帝国之全面战争', '9', '游戏', '0', '133000', '0', '1', '争霸流,位面,嚣张,宅男', '女人，权利，生杀予夺为所欲为是他毕生的追求。\r\n　　历史由胜利者书写，真理在炮火中建立，只有死人才不会造反！\r\n　　只有当你站在别人头顶时，你的话语才会让众人用心去聆听，去服从！', '15150792', '13', '15150792', 'img/2018-01-05/24.jpg', '天之大，铁骑必踏', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('29', '29', '不枯萎的水草', '篮坛超级巨星', '10', '体育', '0', '333000', '0', '1', '技术流,机智', '刚进入NBA的时候，很多人认为他只是一个角色球员，很难生存下去。\r\n后来，人们觉得他有可能成为球队首发。\r\n又过了一段时间，他成为了全明星。\r\n到最后，他成为了篮球史上的超级巨星。', '15150792', '13', '15150792', 'img/2018-01-05/25.jpg', '篮球史上的超级巨星！！', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('30', '30', '望月声', '\r\n诸天记行', '11', '科幻', '0', '133330', '0', '1', '位面,无线流,轻松', '电视剧、电影、动漫、小说……当熟悉的旋律响起，童年记忆涌上心头：放学冲回家中、与父母抢遥控器、在父母保护眼睛的教训下、搬着小凳子坐在电视机前追剧……蓦然回首，李杨不敢相信自己竟站在湖心小筑，而面前任性的女孩叫幽若……而故事，要从一场你绝对想不到的奇葩面试说起……', '15150792', '13', '15150792', 'img/2018-01-05/26.jpg', '穿越各种影视、动漫、小说', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('31', '31', '不灭灯芯', '炼金师的太空堡垒', '11', '科幻', '0', '660000', '0', '1', '争霸流,学生,轻松', '一个致力于建造超级太空堡垒的末世星球。\r\n　　一个铜字学院的丙级学生偶获异位面传奇炼金师传承。\r\n　　一场炼金术和超级科技的碰撞。\r\n　　反物质装备、量子武器、纳米虫、减负光环，再配一波毒剂添加、物质形态改进、融入稀有炼金元素、加上1000个单位术能引导，攻防效果不要太好！\r\n　　融合了炼金术和超级科技的太空堡垒建成后！这个星域，我可以横推了——伟大的圣级炼金师罗伦。', '15150792', '13', '15150792', 'img/2018-01-05/27.jpg', '获得传奇炼金师传承', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('32', '32', '青山铁杉', '六万年之后', '11', '科幻', '0', '660000', '0', '1', '位面,特工,铁血,魔王附体', '六万年后，林格在一颗宜居带星球上降生，在这个太空中有着庞大舰队，医学上纳米机器人已经应用，人身体素质极大提升的时代，他眉头微微一皱觉得事情不这么简单。\r\n　　因为思想和观念的不同，人类在慢慢分化。\r\n　　“我一直以为我是不同的，我见过数不清的智慧物种，各种天体现象，本身有强大的力量。还有什么能让感觉到奇怪？”林格扪心自问自己太屌。\r\n　　捏碎窃听器的女孩，是在受不了，还有这么自吹自擂的人，撇着嘴呐呐自语，“土包子，见过共产主义联盟么？”', '15150792', '13', '15150792', 'img/2018-01-05/28.jpg', '六万年后的宇宙时代', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('33', '33', '狂猎', '无限世界守门人', '11', '科幻', '0', '660000', '0', '1', '无限流,轻松', '成为通往无限世界大门的守门人，最终任务居然是对抗两个主神空间的主神？这让黄叶颇有压力。\r\n　　什么！？轮回者打算杀了李逍遥，抢走赵灵儿？还要帮助灭霸统治漫威宇宙？\r\n　　他们还打算抢saber的咖喱棒，杀了伊莉雅当做圣杯！？\r\n　　最后还要杀了我，崩裂整个无限世界？\r\n　　轮回者啊，不作死就不会死，你们为什么就不懂！\r\n　　于是，黄叶伙同死侍，李逍遥，saber，伊利丹等人物，开始了一条对抗两大主神空间的不归路。\r\n　　（主角与死侍是狐朋狗友，小说中会出现很多十分有“素质”话语，读者老爷请有所准备。）\r\n　　听读者反应说前三章有毒，后边很不错，希望观众老爷们能坚持到十章左右，在决定弃不弃，狂猎拜谢！', '15150792', '13', '15150792', 'img/2018-01-05/29.jpg', '通往无限世界大门的守门人', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('34', '34', '纸皮青蛙', '百鬼众魅图', '12', '灵异', '0', '660000', '0', '1', '无限流,法师,轻松,鬼怪', '啥？\r\n　　拿本破图还能穿越鬼片了？\r\n　　楚人美，灭了！\r\n　　伽椰子，灭了！\r\n　　聂小倩，灭……等等，像小倩这种邪妖恶灵，非得贴身渡化不可！\r\n　　以上，便是小镇青年齐子桓的捉鬼修道人生。', '15150792', '13', '15150792', 'img/2018-01-05/30.jpg', '看我贴身度化恶灵聂小倩', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('35', '35', '王筱蛟', '我真的不想穿越', '14', '二次元', '0', '660000', '0', '1', '无限流,爆笑', '伍德真的不想穿越。\r\n　　为什么？\r\n　　因为伍德很怕死，穿越者绝对是一个高危职业，动不动就会死的好嘛？\r\n　　可为什么一个啥功能没有，只能穿越坑人的系统会落到我的头上？\r\n　　为什么上来就把我送到了满级才能去的一拳超人？\r\n　　伍德有句骂娘的话，不知该对谁去讲……', '15150792', '13', '15150792', 'img/2018-01-05/31.jpg', '一拳莽穿综漫世界', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('36', '36', '水晶荔栀', '这个海贼不太冷', '14', '二次元', '0', '660000', '0', '1', '热血', '七位鬼神，相当于七种恶魔果实能力。\r\n　　残影、冰霜、冥炎……\r\n　　即便是最强大的海王类，在第七鬼神怖修拉的面前也只能俯首称臣。\r\n　　在这个千帆竞渡、弱肉强食的世界之中，抱歉，有实力真的可以为所欲为。', '15150792', '13', '15150792', 'img/2018-01-05/32.jpg', '携鬼神之力穿越海贼', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('37', '37', '蛇草花露水', '海贼之火龙咆哮', '14', '二次元', '0', '660000', '0', '1', '热血,穿越,龙', '时值大海贼时代，伟大航路千帆竞渡，群雄并起，吃下了火龙果实的男人，夏洛特家族的叛逆者，开始了他的传说！\r\n“能杀死我的话就尽管来试试看吧，如果死在这里那也只是说明我不过如此！”\r\n　　“甜...甜点？谁喜欢甜点了啊？甜点不能算沉迷......甜点.....海贼的事，能算沉迷吗？”\r\n　　这是贪食火龙的航海之旅！\r\n　　简介无力，还是点进去看几章吧！\r\n群号：678801420，有兴趣的可以加一加！', '15150792', '13', '15150792', 'img/2018-01-05/33.jpg', '贪食火龙的海贼之旅', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('38', '38', '三眼的哞伽罗', '无限从龙骑士开始', '14', '二次元', '0', '660000', '0', '1', '轻松,重生', '一觉醒来，发现自己成了“龙骑士”。\r\n　　小龙女、桂言叶、冴子学姐……\r\n　　到底是人性的丧尸，还是道德的沦丧？\r\n　　不信抬头看，苍天饶过谁！', '15150792', '13', '15150792', 'img/2018-01-05/34.jpg', '化身龙骑士穿越二次元', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('39', '39', '笔聊梦月', '不想搞事的漩涡鸣人', '14', '二次元', '0', '660000', '0', '1', '热血', '大千世界，穿越重生就是为了搞事，可是对于获得穿越者记忆的漩涡鸣人来说，安稳的日常才是王道。\r\n　　仇恨？和平？战争？\r\n　　大家能不能别这么中二？\r\n　　虽然不喜欢搞事，可是漩涡鸣人决定改变未来！\r\n　　鸣人：“明明是我先来的！凭什么把小樱让给佐助？！”\r\n　　雏田：“未来明明是我和鸣人君在一起，你们这些妖艳货色想干嘛！”\r\n　　一名穿越者失败的穿越，让火影世界走向另一个方向。', '15150792', '13', '15150792', 'img/2018-01-05/35.jpg', '漩涡鸣人不想搞事', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('40', '1', '鹅是老五', '最强弃少', '3', '仙侠', '1', '720000', '0', '1', '重生', '叶默蓦然清醒过来的时候，才发现周围的一切似乎都变了，美女师父也不见了。他也发现了自己成了被世家抛弃的弃子，被别人退婚的苦逼,还是被女人站在讲台上拿着他情书羞辱的对象......\r\n　　但是这些都不重要，最重要的是他还记忆起了另外一件原本不属于他的可怕的事情。', '1515424411', '13', '1515424411', 'img/2018-01-05/36.jpg', '修仙觅长生，热血任逍遥', '1', '1510203669');
INSERT INTO `hxb_book` VALUES ('41', '1', '鹅是老五', '造化之门', '3', '仙侠', '1', '2550000', '0', '1', '重生', '这是一个支离破碎的角落，这是一个被遗忘的世界。\r\n　　那一天，是宁城最失意的时候，那一天，他被硬生生的带到了这里。\r\n　　这个角落因为有了宁城，将在浩瀚星空中留名，这个世界也因为有了宁城，将在无尽宇宙中精彩！', '1515424411', '13', '1515424411', 'img/2018-01-05/37.jpg', '踏莲曳波涤剑骨，凭虚御风塑圣魂！', '1', '1510203669');

-- ----------------------------
-- Table structure for hxb_book_chapter
-- ----------------------------
DROP TABLE IF EXISTS `hxb_book_chapter`;
CREATE TABLE `hxb_book_chapter` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `title` varchar(33) DEFAULT NULL COMMENT '章节标题',
  `content` text COMMENT '章节内容',
  `ctime` int(11) DEFAULT NULL COMMENT '创建时间',
  `status` tinyint(1) DEFAULT '0' COMMENT '章节审核状态：0代表审核中，1代表通过，2代表通过',
  `book_id` int(11) DEFAULT NULL COMMENT '书籍id',
  `book_name` varchar(33) DEFAULT '' COMMENT '作品名称',
  `trash` tinyint(1) DEFAULT '0' COMMENT '章节是否加入回收站，0代表否，1代表是',
  `utime` int(11) DEFAULT NULL COMMENT '修改时间',
  `count` int(11) DEFAULT NULL COMMENT '字数',
  `reason` varchar(100) DEFAULT NULL COMMENT '不通过原因',
  `publish` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0表示未发布,1表示已发布',
  `publish_time` int(11) NOT NULL DEFAULT '0' COMMENT '发布时间',
  `update_time` int(11) DEFAULT '0' COMMENT '更新时间',
  `author_speak` varchar(100) DEFAULT NULL COMMENT '作者的话',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8mb4 COMMENT='作品-章节表';

-- ----------------------------
-- Records of hxb_book_chapter
-- ----------------------------
INSERT INTO `hxb_book_chapter` VALUES ('1', '第一章 落魄王子', '“呱……”一声略带凄切的叫声惊醒了莫无忌，他抬起头，正好看见一只乌鸦孤单的从头顶飞过，很快就和那凄厉的叫声一起消失的无影无踪。\n\n“呱……”一声略带凄切的叫声惊醒了莫无忌，他抬起头，正好看见一只乌鸦孤单的从头顶飞过，很快就和那凄厉的叫声一起消失的无影无踪。\n\n    我这是在哪里？莫无忌突然感觉到有些怪异。他似乎坐在一个新堆积起来的低矮坟包之上，在他的身前还东倒西歪的跪了七八个小孩。一名身穿碎花蓝裙的少女，正挎着一个竹篮站在他的身边。\n\n    就在莫无忌惊疑不定的时候，忽然听到身边那名少女柔声的说道，“大家真乖，今天糖果没了，我们就玩到这里，记得明天继续来玩啊。”\n\n    这是在玩皇帝上朝的游戏？这场景怎么这么熟悉？\n\n    莫无忌心头大惊，这不是和小说中那个慕容复最后的下场一摸一样吗？慕容复为了复国最后疯了，青梅竹马的漂亮表妹王语嫣跟别人跑了，最后只有一个丫鬟阿碧陪在他的身边。眼前这个场景，正是慕容复复国无望最后疯掉，阿碧叫了许多小孩来陪莫容复玩复国后的小儿游戏。\n\n    “我王万岁，我王再见，明天再来吃糖果哦……”众小儿一阵乱七八糟的话语之后，纷纷散去。\n\n    莫无忌目光所及之处，看见几名年轻俊俏的男女正从不远处走过。当他的目光落在其中那名紫裙女子身上的时候，一时间甚至忘记了自己的处境，心里只是惊叹，居然有如此美女。\n\n    那名紫裙少女的目光也落在了慕容复的身上，眼里透露出复杂的表情，有同情也有一些几乎看不出来的失望。另外几名年轻俊俏的男女似乎对着他议论了几句，这才一边笑着一边渐渐走远。\n\n    不对……\n\n    莫无忌突然想到了一个可怕的结果，难道我死后复活，然后真的重生到了慕容复的身上？这世界真的有灵魂穿越的事情？\n\n    还有我的灵魂为什么会穿越？在这之前我又在做什么？\n\n    想到这里，莫无忌心头就是一阵抽搐的痛楚。他终于回忆了起来，在他研究出开拓经脉的药液后，那个他可以为之付出生命的恋人夏若茵从背后给了他一刀。想到夏若茵那一刀，悲伤充彻了莫无忌的身心……\n\n    一阵剧烈的头痛让他无法继续想下去，乱七八糟的信息塞满了莫无忌的脑海。足足一两个小时过去，莫无忌才弄明白了是怎么回事。\n\n    这并不是宋朝，他也不是重生到了慕容复的身上。\n\n    这里甚至不是地球，他所在的地方是承宇国的国都饶州，他原本的身份是一个叫北秦的郡国王子，名字叫莫星河。之所以被他老爹起了一个莫星河的名字，因为承宇国是星汉帝国下的一个领主国，这个名字完全是向往星汉帝国而起的。\n\n    这一片地方到底有多大，莫星河的记忆中没有任何概念。只知道星汉帝国并不是唯一的帝国，而每一个帝国下都有许多领主国，每一个领主国下又有许多的郡主国。\n\n    他原本所在的北秦郡国就是承宇领主国所属，承宇领主国又隶属于星汉帝国。\n\n    十九年前，北秦郡国的国主，莫星河的爷爷莫天城到承宇国都饶州后，就再也没有消息。北秦郡国自然不可无主，但一个郡国的国主需要领主国君认同才行。\n\n    如果莫天城不是突然失踪，他可以直接将王位传给儿孙，然后再向所在的领主国报备一下就可以了。现在莫天城失踪，还没有来得及传位，那北秦郡国下一位王位继承者就必须要亲自到所在领主国，由所在的领主国君当面承认。\n\n    正因为这样，莫星河的父母就带着刚刚出生的莫星河来到了饶州，一个是为了查访莫天城的下落，还有就是莫星河的父亲莫光远为了得到承宇领主国的承认，继承北秦郡国之王位。\n\n    原本很简单的继承，没想到受到了多方阻碍。莫星河的父母散尽了无数钱财，经历了十多年的奔波，依然没有能继承北秦郡国国主之位。\n\n    莫星河父母最后都病死在了饶州城，莫星河继承了父亲莫光远的执念，一心要继承北秦郡国。可随着莫星河的父母死去，莫家在饶州的人情用尽。年少的莫星河又奔波几年无果，在得知北秦郡国被承宇国主收走后就彻底疯掉，最后被莫无忌重生回来。\n\n    莫无忌也知道了之前看见的那个紫裙女子是谁了，紫裙女子叫闻曼珠，她的父亲是承宇国的一个侯爷，和莫星河的父母是至交好友。莫星河父母刚来承宇国的时候，还是有一定身份地位的。所以两家来往非常密切，莫星河和闻曼珠算是青梅竹马，虽然没有定下娃娃亲，大家都默认了两人长大了会在一起。\n\n    随着莫家继承郡国的机会消失，莫星河的父母病逝，最后莫星河疯掉，闻家就忘记了莫星河。长大后的闻曼珠也慢慢的疏远了莫星河，转而和别的世家公子走近。\n\n    两滴泪水落在了莫无忌放在膝盖的手背上，莫无忌抬起头，看见了带着一道刀疤的凄然脸庞，这个落泪的少女年龄似乎比他现在的身体还要小。\n\n    这个少女自然不是阿碧，而是叫着烟儿。这是唯一直跟在他身边的人，身份仅仅是一个婢女而已。可以说如果不是烟儿，根本就等不到他莫无忌重生过来，原本的那个莫星河就不知道死去多久了。\n\n    烟儿除了脸上有一道刀疤之外，面色苍白，头发疏黄，完全没有青春少女的那种活力。很明显，这是营养不良造成的。\n\n    还是不对啊，莫无忌打了个激灵。莫家好歹也是一个郡王家族，就算是莫星河的父亲没有办法继承到郡王之位，也不至于穷困潦倒病亡在饶州。难道不会离开饶州先回去？或者说难道北秦郡国没有人来护卫或者送钱财？\n\n    这其中绝对有问题。\n\n    看见莫无忌抬头看向自己，烟儿擦了擦略红的眼睛柔声说道，“王上，我们可以退朝了吗？”\n\n    莫无忌低头看了看身下的黄土包，心里不但在为烟儿叹息，也为这个前身叹息。这种小儿游戏，烟儿每次带他离开还必须要得到这种退朝的认同。\n\n    不过莫无忌很快就将这种叹息丢在了一边，他最应该叹息的是他自己。他不知道应该庆幸自己被如此暗算也没有死掉，还是悲哀暗算他的人是他最重要的恋人，或者是悲哀他再也无法回到地球。\n\n    “王上，天已经黑了……”见莫无忌久久没有说一句话，站在莫无忌身边的烟儿再次小心的说了一句。\n\n    莫无忌看了看已从远处树梢落下去的残阳，不知道是想到之前的莫星河，还是感叹自己的命运，叹息一声道，“回去吧……”\n\n    他看见烟儿惊诧的眼光，没有任何解释的心情，只好又叹息了一句，“退朝吧……”\n\n    说完他想要站起来拍拍身上的黄土，长久的盘膝坐在地上，让他的双腿有些发麻，好在身边的烟儿及时扶住了他。\n\n    被烟儿扶住慢慢的走出稀疏的树林，莫无忌也在整理着脑海中仅存的一些东西。\n\n    “也不知道这是一个什么样的世界……”两人默默走了数分钟后，莫无忌自言自语的说了一句。\n\n    “王上，刚才你说什么？”烟儿没有听明白莫无忌的话，赶紧问了一句。\n\n    莫无忌摇了摇头，“烟儿，以后不要叫我王上了，就叫我的名字吧。”\n\n    以后要和烟儿一起生活，终究还是要解释的。\n\n    “少爷，你身体大好了？”提着竹篮的烟儿惊喜的抓住莫无忌的手问道，她眼眶中转动的泪珠和那发白却颤抖的手指告诉了莫无忌，此刻她到底有多激动。\n\n    莫无忌略一犹豫，笑了笑说道，“也许还没有好彻底，很多东西都忘记了。但我不会再和之前一样，做一些白痴才做的梦了。”\n\n    莫无忌怕自己会露出马脚，索性说还没有好彻底。\n\n    “那……”烟儿声音颤抖，似乎想要说什么，又不敢说出来。\n\n    莫无忌自然知道烟儿的意思，她是想要问他明天还要不要来玩这个儿童游戏，又怕一旦提起上朝会再次让他复发。\n\n    拍了拍烟儿的手背，莫无忌笑了笑，“王上我已经做过，觉得也没什么意思，明天不用来了，明天还是想想怎么活下去吧。”\n\n    烟儿忽地丢下手中的竹篮，‘扑通’一下跪在地上，泪如泉涌，合什对着远处不断喃喃自语。', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '800', '', '1', '0', '1515411053', null);
INSERT INTO `hxb_book_chapter` VALUES ('2', '第二章 活着的艰难', '﻿莫无忌没有去拉烟儿，他能够感受到莫星河疯了后，烟儿承受的压力和苦难。此刻他只是看着远处影影绰绰的高楼大厦，暗自握紧了拳头，就算是从头再来，哪又如何？\n\n    这里虽类似君主制，科技却也并不是一无所有。有类似地球的公交系统，也有普通的电子机械设备，他还怕活不下去？\n\n    “烟儿，我们先回去吧。”莫无忌拉起了难以自己的烟儿，看着远处的高楼说道。\n\n    就算是他从地球重生过来，估计也无法夺回属于他的北秦郡国。\n\n    不去想做什么郡王梦，想要在这个地方立足，莫无忌还是有十足的把握。前世他好歹也是一个顶级的生物和植物学家，正是因为他发现了可以从数种植物中提取精华，合成一种拓展人体经脉的药液。这才被恋人暗算，最后重生到了这个地方。\n\n    从植物中提取出拓展人体经脉这种东西，其中的珍贵他并不是不清楚。经脉一直存在于有和无的界限之间，尽管中医中经常说到经脉。但事实上，有几个人能真正找到经脉所在，并且在经脉上做文章？\n\n    可以想象，一旦人体经脉被拓展到自身都可以感觉到的地步，那实力将会如何强大？估计随便一个拓展了经脉的人去参加奥运会长跑或者举重，也有获奖的希望。\n\n    唯一让他没有想到的是，生死以之的恋人会暗算他。直到现在，他也不明白为何曾经生死相随的恋人，要在他成功的那一刻，用匕首从背后刺入他的心脏。\n\n    “是，少爷…….”烟儿总算是平静了下来，眼里也多了一些光彩。\n\n    莫无忌无奈说道，“烟儿，你看我像少爷吗？以后叫我的名字吧。过去的就过去了，今天是一个新的开始，以后我的名字不再是莫星河，而是莫无忌。”\n\n    “是，少爷。”烟儿连忙应道。\n\n    莫无忌没有再劝说，有些习惯和等级概念不是说改就可以改掉的，只好说道，“天要黑了，我们先回去吧，明天我想办法去找个事情做做。”\n\n    尽管莫无忌还没有回到家，他心里早已有了一些模糊的概念。莫星河的父母双亡，家财散尽后，莫家早就穷困潦倒。后来莫星河疯掉，烟儿每天除了出去做事，还要带莫无忌来玩这种无聊的游戏，能活下来算是不错了。\n\n    “少爷，你不要去找事做。以后不用每天出来，我可以多兼一份事情，足够了。”听到莫无忌说要出来找事做，烟儿忙不迭的摆手。\n\n    莫无忌看了看烟儿身上洗的有些褪色的衣裙，还有黄发上最简单的发饰，没有再说什么。有些事情，不是用嘴可以说清楚的。那个莫星河到死了怕都不明白，烟儿带着他活到今天有多么艰难。\n\n    ……\n\n    饶州城虽然也有城门和城墙，却并没有夜禁，无论白天黑夜，都可以随意进出。\n\n    或者说，饶州城的城门和城墙，更多的是一种象征意义，而不是为了战争建立的防御。\n\n    莫星河一心要复国，对饶州城的繁华与否并不在意，莫无忌只能从莫星河隐约的记忆中知道饶州很繁华。\n\n    在跟着烟儿走进饶州城后，莫无忌立即就感受到了这种繁华。宽敞街道中密集的人流和街道两边灯火通明的店铺，让莫无忌甚至怀疑这里其实只是地球上的一个现代都市。\n\n    这种繁华的地方自然不是莫无忌住的起的，两人绕过繁华的街道，走了将近一个小时，这才来到了一片显得有些杂乱的居住区。到了这里，灯火一下就昏黄起来。\n\n    莫无忌远远就看见了他和烟儿居住的杂货间，哪怕房租便宜的几乎等于没有，也不是烟儿和他能负担的起的。他和烟儿之所以还能住在这里，显然有房东的同情在其中，否则的话，他和烟儿也许连睡觉的地方都没有。\n\n    “哎呦，王上回来了，本大爷赶紧让路。”一个突兀的声音打断了莫无忌观察的思绪。\n\n    “胡飞，你让开。”原本落后莫无忌半步低着头的烟儿忽然上前一步，就好像一头发怒的小豹子一般，将莫无忌拦在了身后。\n\n    借助昏黄的灯光，莫无忌看见一名头发梳到油光可鉴的青年，他嘴里说着赶紧让开，事实上他人正站在路当中，根本就没有半点让开的意思。\n\n    “烟儿妹子，你胡大哥今天特意去买了半斤猪头肉，正准备给你送去。你这样对我，我有些伤心啊。”这叫胡飞的青年男子扬了扬手中的荷叶包。\n\n    莫无忌的肚子不争气的咕咕叫了一声，原本想继续让胡飞让开的烟儿看着胡飞手中的荷叶包，有些犹豫起来。\n\n    “这就对了吗，你胡大哥和你又不是外人……”胡飞说话间，已经走了过来，另外一只手摸向了烟儿的手背。尽管烟儿脸上有一道疤痕，因为营养不好，身材没有长开，依然不能完全遮去她的俏丽容颜。\n\n    烟儿眼里有些挣扎，如果只是她一个人，她根本就不会理睬胡飞，可今天少爷一整天都没有吃东西，刚才肚子都饿的叫了。而且家里连一粒米都没有，回去又能怎么办？\n\n    莫无忌岂能不知道烟儿心里所想，他根本就不等胡飞的手摸到烟儿的手背，抬脚就是一下。\n\n    胡飞根本就没有想到莫无忌会在这个时候动手，被莫无忌一脚正踹在胸口。\n\n    莫无忌就感觉自己踹到了一块钢板一般，强大的力量反震回来，接连退后了数步。\n\n    “少爷，你没事吧……”烟儿急忙跑了过来，将莫无忌扶住。\n\n    莫无忌看着仅仅被他踹退了一步的胡飞，心里震惊不已。他这个身体现在的确很虚弱，但也不至于在偷袭的情况下，还没有一脚踹倒胡飞。难道这个叫胡飞的家伙会武？\n\n    “你找死……”胡飞没想到一直做着郡王梦，懦弱不堪的莫星河敢突然对他动手，顿时勃然大怒。手在腰际一抹，拔出一把尺长尖刀就冲向了莫无忌。\n\n    远处几名旁观者看着胡飞冲向莫无忌，并没有人上前阻拦，甚至连说话的都没有。\n\n    “胡飞，赶紧住手。光天化日之下，你敢行凶？”烟儿急的脸色更是发白，却根本没有注意到现在天色已晚。\n\n    “哈哈，老子早就想干掉这个白痴了。今天这个白痴先对我动手，我就算是杀了他，最多也只是罚点钱而已。烟儿，我这是为你好，你以后就解脱了，跟着我有吃有穿……”胡飞显然没有任何住手的意思。\n\n    烟儿焦急万分，没有任何办法，只能将莫无忌拦在了身后。\n\n    此刻的莫无忌完全冷静下来，在他的印象中，承宇国的确有这么一条律法。不管对错，那就是在对方先动手的情况下，将对方杀了，也只是受到一点钱财损失。\n\n    后悔已是来不及，莫无忌再次将烟儿拉在一边，冷静的盯着胡飞说道，“胡飞，今天你敢动我一根汗毛，你将死的很难看。”\n\n    胡飞就好像听到了最好笑的笑话一般，狂笑道，“老子不敢动你？那你就睁开眼睛看看，老子敢不敢动你……”', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '1033', null, '1', '0', '1515418053', null);
INSERT INTO `hxb_book_chapter` VALUES ('3', '第三章 只是凡根', '莫无忌不紧不慢的继续说道，“我好歹也是北秦郡王一族，哪怕王位还没有拿回来，我的贵族爵位还在。你敢对一个贵族动手？胡飞，我告诉你，五马分尸那是便宜你了，千刀万剐剥皮抽筋也是等闲。”\n\n    胡飞的脚步为之一顿，他这才突然想起哪怕最弱的郡王，落魄到了极致，也是有贵族身份的，绝对不是他这样市井蝼蚁能动。\n\n    至于莫无忌的贵族身份是不是被剥夺了，他胡飞是没有资格知道的。但有一点莫无忌没有说错，对贵族动手，被五马分尸也算是最简单的处罚。\n\n    “王上说笑了，我只是和您开个玩笑而已，岂能对您动手。”胡飞在最短的时间就明白了对莫无忌动手的后果，略带谄媚的说道。\n\n    要干掉这个姓莫的也不急在一时，等他调查清楚了对方的爵位还在不在就行了，他胡飞有的是时间。\n\n    莫无忌不慌不忙的走到胡飞身边，将胡飞已经垂下的尖刀捏住，轻松一抽就拿在了手中。\n\n    好刀，刀一到莫无忌的手中，莫无忌就知道这刀很锋利。\n\n    将自己手中的刀松手后，胡飞下意识的退后了数步，这才小心谨慎的看着莫无忌。\n\n    烟儿紧张的看着莫无忌和胡飞，就算是莫无忌抽走了胡飞手中的刀，烟儿心里依然紧张不已。她作为莫无忌身边的婢女，自然清楚莫无忌现在就是一个平民，根本就不是什么贵族身份。\n\n    换句话说，刚才莫无忌先对胡飞动手了，如果胡飞杀了莫无忌，胡飞最多也只是罚一点钱而已。\n\n    莫无忌将手中的刀仔细看了一遍后，这才看着眼珠乱转的胡飞说道，“胡飞，不要说我是贵族的身份就碾压死你。就算是我不是贵族了，我祖上曾经是郡王这个身份，你敢动我，也足以让你剥皮抽筋。一个郡王的后裔你也敢动，岂不是要将我承宇国的尊严踏在脚下了？”\n\n    说到最后，莫无忌更是冷哼一声。\n\n    胡飞心里正在想着莫无忌到底是不是贵族，只要莫无忌不是贵族，哪怕刀在莫无忌手中，他也有十足的把握杀掉莫无忌。在听到莫无忌这句话后，胡飞那一点点念头顿时消失的无影无踪，立即说道，“王上，小人真是开了一个玩笑。”\n\n    他心里也在疑惑莫无忌为何变化这么大。\n\n    “我现在已经不是王上了，滚吧，别让我改变主意。”莫无忌随手将胡飞的尖刀插在了靴筒中。\n\n    “是，是，莫少爷走好。”胡飞心疼的看了一眼莫无忌收起了的尖刀，这才退去。\n\n    那把尖刀是也是他偶然得到的，一直贴身收藏，没想到今天被人拿走了，说不心疼那是假的。\n\n    “少爷，你现在不是……”看见胡飞走远，烟儿这才走过来小心的说道。\n\n    莫无忌打断了烟儿的话，“我知道，我们先回去再说。”\n\n    烟儿不提醒，莫无忌也能猜测出来他应该不是贵族身份了。\n\n    ……\n\n    两人住的杂货间很小，两个木板床中间只有一道破旧的帘布隔开。家里没有任何值钱的东西，莫无忌也清楚，稍微有些值钱的东西都被烟儿变卖了，换成一些便宜的糖果去哄骗那些小孩。\n\n    他的床前挂着一面有些划痕的镜子，莫无忌从镜子当中看见了自己的模样。和前世有几分相似，长发很是干涩，却被烟儿收拾的整整齐齐。脸色同样略微苍白，比起烟儿的面黄肌瘦要好的多了。除了眼神有些疲惫外，剑眉和高挺的鼻梁让他看起来还算是一个帅哥。\n\n    “少爷，我去路姨家借点米来……”一进家门，烟儿就说道。她还有些可惜胡飞的那一包猪头肉，如果是她的话，她肯定不会要那把尖刀，而是要将那一包肉要过来。\n\n    “等等……”莫无忌叫住了烟儿。\n\n    见烟儿回头疑惑的看着自己，莫无忌才问道，“烟儿，那胡飞似乎会武啊，比我强很多。他一个混混，从什么地方学来的？”\n\n    在莫无忌的记忆中，这个世界似乎并不是什么高手满天飞的地方。他作为郡王的后裔都没有学武，那胡飞就一混混，凭什么学武？\n\n    烟儿露出不屑的表情说道，“那胡飞不过是跟着别人学了几招而已，一个凡根，连开灵都不能，算什么会武。我偶尔听老爷说过，当初太爷爷可是筑灵强者呢。”\n\n    “什么是开灵?”莫无忌急切的问道，他的记忆当中，除了复国还是复国，竟然没有半点别的记忆。难不成自己想错了？这里还是一个可以修炼的地方？\n\n    想到这里，莫无忌心里热切起来，如果真的是一个可以修炼的仙侠世界，他一定要疯狂修炼，将来有一天可以回到地球。他要当她的面亲口问一句，为什么。\n\n    对莫无忌不知道开灵，烟儿并不觉得惊讶。她惊讶的是少爷之前对这些完全不关心，为何现在表现的这么急切？\n\n    她还是将自己知道的说了出来，“开灵就是帮助有灵根的人激发灵根，开辟灵络，只有激发了灵根和开辟了灵络的人才可以修炼。听说第一次开辟出来的灵络越多，灵根资质就越好。”\n\n    莫无忌立即就从烟儿的话中听出来了两个问题，要修炼首先必须要有灵根。其次，还要将自身拥有的灵络激发出来。\n\n    “烟儿，老爷为何没有让我去开灵？”莫无忌语气略带一些激动的问道。\n\n    烟儿语气有些低落起来，“老爷来饶州后，一直在奔波王位继承的事情。后来才知道事不可为，这才想要让你走修炼的路。老爷凑齐了让你测灵和开灵的钱，最后检测出来你和老爷一样，都是凡根。凡根一般情况下是无法开辟出来灵络，也是没有办法修炼的……”\n\n    “什么是凡根？”莫无忌心里一沉，依然问了出来。\n\n    死亡的事情都经历过，还有什么能让他无法接受的？\n\n    烟儿可以感受到莫无忌那种极度的失落，她心里叹息一声说道，“我听老爷说，修炼的资质决定了将来修炼的成就。一般来说，没有灵根的就是凡根，也叫做废根。所有的凡根拥有者，都是凡人。\n\n    有灵根的人才可以修炼，而有灵根的人其灵根等级又有区分。分为低品灵根，中品灵根，高品灵根和极品灵根。听说还有人的灵根比极品还要好，至于是什么样的，我就不知道了。”\n\n    “我原来是凡根……”莫无忌终究无法忍受住心中的失落，在烟儿说完后，自嘲的说了一遍。\n\n    烟儿安慰道，“少爷，就算是承宇领主国，拥有灵根的人也是极少数。大多数人都是只有凡根的凡人，那么多凡人都可以过的很好，我们肯定也可以过的很好。”\n\n    莫无忌握紧了拳头，“烟儿，明天我就去找一个事情做做。我要积攒一些钱财，准备再次开一下灵。”\n\n    “啊……”烟儿似乎被莫无忌的决定惊住，随即就明白了莫无忌的话是什么意思，赶紧劝道，“少爷，千万不要啊。当初老爷就是为了积攒钱财帮助少爷开灵，哪怕少爷测试出来是凡根，老爷依然坚持为少爷开灵，只是凡根是开辟不出来灵络的。少爷开灵失败后没有多久，老爷就病故……”\n\n    烟儿说的很是隐晦，莫无忌也明白了烟儿话的意思。当初老爷如果不是为了帮他开灵，虽然穷困，也许还不至于病故。可见，开灵需要的钱财不是一个小数目。但莫无忌经历两世，自然不是烟儿如此单纯。莫光远早不死晚不死，就是帮他检测了灵根后就死，不一定就是因为病故这么简单。看样子，他就是要去开灵，也要小心一些。\n\n    “烟儿，你放心，我有把握赚到钱。今天你也不要去路姨家借米了，明天开始，我来养你。”莫无忌走到烟儿身边，伸手抚了抚烟儿那营养不良略显发黄的头发。\n\n    烟儿也不过才十几岁而已，可以想象的出来，当初莫星河父母双亡，她要照顾疯了的莫星河，付出了多大的代价？\n\n    路姨是他们的房东，路姨对他们已是很照顾。路姨只是一个寡妇，自己过得同样不是很好。经常还要去找她借米，这对路姨来说，也是一个负担。\n\n    他莫无忌好歹也是一个顶级的生物学家，在一个也算是有科技的地方，岂能被一日三餐拦住？', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '1000', null, '1', '0', '1515418053', null);
INSERT INTO `hxb_book_chapter` VALUES ('4', '第四章 一饭之恩', '命运的转换本来就被够催悲的了，但他并不在意。哪怕是成了一个落魄王子，莫无忌依然没有放在心上。最让他失落和不甘的是他只是一个凡人，一个只有凡根的凡人。\n\n    在地球上，大家都是一样，哪怕不能修炼，他还可以从别的地方击败对手。在这里，不能修炼，那就意味着永远不会再有机会。\n\n    就算是他铁定没有灵根，在没有亲自去测试一下，他还是不会放弃的。怀着这种患得患失的心情，莫无忌也不知道自己是什么时候睡着的。\n\n    莫无忌是被饭香诱醒的，睁开眼睛发现天早已大亮。他一坐起来，就看见了门口破旧的方桌上摆着一大碗米饭，还有一碟咸菜和半根黄瓜。\n\n    “少爷，你醒了，洗漱后来吃饭吧。”不等莫无忌说话，一直关注着他的烟儿就连忙欣喜叫道。\n\n    “你昨晚没睡？”莫无忌看着烟儿苍白的脸色，还有那黑眼圈和疲惫的眼神，就知道烟儿一晚上都没有睡觉。\n\n    “我昨晚帮路姨去摆地摊了，昨天生意很好呢。”尽管烟儿很疲惫，莫无忌依然可以感受到烟儿的欣喜。\n\n    莫无忌明白这是什么欣喜，因为生意很好，路姨给她的报酬也多。\n\n    从木板床上走下来，莫无忌走到烟儿的身边，伸手摸了摸烟儿还是有些凌乱的头发，良久没有说话。\n\n    他肯定烟儿这样通宵出去摆摊赚钱养活他，不是第一次，她都已经习惯。那个白痴莫星河简直是一头猪，靠一个十几岁的小姑娘养活不说，还每天做着郡王梦。在烟儿做事回来后，还要用辛苦钱去买糖果陪他玩上朝游戏。\n\n    “少爷，你昨天没有吃多少，赶紧去洗洗吃饭吧。”烟儿感觉到少爷这次醒来后，是真的有了很多的改变，这让她感到欣喜。\n\n    “你先吃吧，我马上就去洗。”莫无忌心里升起一种怜惜和触动，两世为人，从未有人如此对他过。前世恋人因为性格冷漠，所以对他虽好，却没有这种触动，最后还暗算了他。\n\n    烟儿急忙说道，“我刚刚吃过，少爷你……”\n\n    烟儿的话没有继续说下去，他看见少爷走到她的木板床边，慢慢的弯下腰，从枕头边拿起一个被啃了一小半的漆黑馒头。\n\n    莫无忌没有说话，他捏着这漆黑坚硬的馒头，心里堵得慌。刚才烟儿说吃过的时候，他就看见了烟儿嘴角边的馒头碎屑。\n\n    他缓缓将馒头送到鼻下，一股淡淡的馊味传来。这散发出馊味的发黑馒头，和那一大碗白米饭形成了鲜明的对比。难怪这丫头正在长身体的时候，还是一头黄发了。\n\n    也许是那馊味刺激了莫无忌的鼻子，他的鼻子有些酸，眼眶也有些发涩。\n\n    “少爷，那你不能吃……”烟儿还以为莫无忌要吃黑馒头，赶紧叫道。\n\n    莫无忌轻轻的抓住了烟儿比他更加粗糙的双手，缓慢的说道，“烟儿，以后只要有大哥一口吃的，就不会饿着你。今天起就不要再出去通宵摆地摊了，记住大哥的话，从现在开始有大哥养你。”\n\n    这一饭之恩，让莫无忌永世不会忘却。\n\n    “少爷……”烟儿惶恐的叫了一句，她有些担心少爷今天是怎么了。\n\n    莫无忌不敢再说下去，他拍了拍烟儿的手，走到了外面擦去了眼角的潮湿，急忙洗漱。\n\n    ……\n\n    吃过早饭，莫无忌走出住处。尽管他强迫烟儿和他一起分了早餐，也强迫烟儿上床休息后才出来，莫无忌心里并不好受。他要尽快找到一份工作，好让烟儿过的轻松一些。\n\n    ……\n\n    在整个承宇领主国，饶州城是最大最繁华的一个城市。走在这繁华的街道上，莫无忌可以感受到饶州城的生活节奏并不会比地球上的大都市慢。\n\n    饶州公会，这里是莫无忌要来找工作的地方。在这里无论是找工作，还是雇工，大家都会选择饶州公会。\n\n    走进公会，莫无忌就可以看见众多招聘的窗口。有些是一些大的工坊长期招工的地方，还有一些是临时招工的地方。除此之外，还有许多招人海报，以及各类求聘的信息。相对地球来说，这里就是一个多功能的人才市场。\n\n    公会实在是太大了，一千多人在其中转悠，依然让人感觉到很是空旷。\n\n    莫无忌稍微转悠了半圈，就看出来了，在公会里面两种人最吃香。栽种药材的，还有一种是勘探矿物的。\n\n    莫无忌摇了摇头，难怪这里虽然也算是科技世界，到现在还没有各种高级家电产品。敢情这里对电子科技人才并不重视，从聘请报酬就能看出。一个专门从事机械的技术工，酬劳是从事栽种药材酬劳的三分之一，是从事勘探矿物酬劳的五分之一。\n\n    莫无忌对这个并不在意，在地球他是生物学专家，又精通植物学。想在这里找一份工作，对他来说，那是轻松无比。\n\n    简单对比了一下，莫无忌很快就发现有很多工作都适合他。\n\n    饶都植药场，招收一名药材嫁接师，酬劳是每月三十银币。招收药园勤工若干，每月酬劳十枚银币，要求精通药材，有经验者优先。铜山矿坊招收探矿师一名，矿物分解师一名，酬劳均是每月五十银币。\n\n    作为一个顶级生物学家，莫无忌认为他完全可以应聘矿物分解师。现在他找工作，并不是看这个工作是不是适合他，也不是看这个工坊是好是坏。对他来说，酬劳最高的就是最好的。因为这些工作，他都不会做很长时间。\n\n    就是矿物分解师了，莫无忌走向了铜山矿坊。就在他要站在后面排队应聘的时候，在铜山矿坊旁边的一个窗口突然挂出一个新的牌子，“承灵极丹工坊，紧急招收炼药助理师数名，酬劳每月十枚金币……”\n\n    莫无忌顿时止住了脚步，他知道在这里通用的货币就是金币、银币和铜币。一枚金币可是能换取一百枚银币，一万枚铜币。一个月酬劳十枚金币，甩掉了别的工种几十条街道了。这种好工作不去做，那实在是对不住他这个一流生物学家。\n\n    炼药不就是制药吗？在地球上，因为病毒抵抗能力增强，西药渐渐被富豪们放弃，相反各种中药，或者是植物提取的药液更受青睐。作为一个顶级生物学家和制药高手，他不知道为多少家公司制过成品药材。他每次出手都是以指导者身份出现的，哪一次不是上百万的报酬？\n\n    莫无忌近水楼台，在承灵极丹工坊的招收告示刚刚挂出，就顺势在承灵极丹工坊的招聘窗口前一坐，同时面带微笑，尽量让自己有气场一些的说道，“我要应聘贵公司……呃，是贵工坊的工作。”\n\n    承灵极丹工坊招聘的人是一名中年妇女，看起来很是精明能干。她看见莫无忌就这样坐在窗口前，并没有拿出什么东西，略微有些疑惑的问道，“请问你要应聘我们工坊的哪一个工种？”\n\n    “我就应聘贵工坊刚刚挂出来的炼药助理师……”\n\n    莫无忌话还没有说完，就感觉到了不对，因为他说出这句话后，周围明显的寂静了起来。这一刻，似乎所有的目光都集中在了他的身上。\n\n    那名中年妇女也一脸震撼的看着莫无忌，好一会才醒悟过来，语气恭谨的问道，“请您出示炼药助理师资格证书原件……”\n命运的转换本来就被够催悲的了，但他并不在意。哪怕是成了一个落魄王子，莫无忌依然没有放在心上。最让他失落和不甘的是他只是一个凡人，一个只有凡根的凡人。\n\n    在地球上，大家都是一样，哪怕不能修炼，他还可以从别的地方击败对手。在这里，不能修炼，那就意味着永远不会再有机会。\n\n    就算是他铁定没有灵根，在没有亲自去测试一下，他还是不会放弃的。怀着这种患得患失的心情，莫无忌也不知道自己是什么时候睡着的。\n\n    莫无忌是被饭香诱醒的，睁开眼睛发现天早已大亮。他一坐起来，就看见了门口破旧的方桌上摆着一大碗米饭，还有一碟咸菜和半根黄瓜。\n\n    “少爷，你醒了，洗漱后来吃饭吧。”不等莫无忌说话，一直关注着他的烟儿就连忙欣喜叫道。\n\n    “你昨晚没睡？”莫无忌看着烟儿苍白的脸色，还有那黑眼圈和疲惫的眼神，就知道烟儿一晚上都没有睡觉。\n\n    “我昨晚帮路姨去摆地摊了，昨天生意很好呢。”尽管烟儿很疲惫，莫无忌依然可以感受到烟儿的欣喜。\n\n    莫无忌明白这是什么欣喜，因为生意很好，路姨给她的报酬也多。\n\n    从木板床上走下来，莫无忌走到烟儿的身边，伸手摸了摸烟儿还是有些凌乱的头发，良久没有说话。\n\n    他肯定烟儿这样通宵出去摆摊赚钱养活他，不是第一次，她都已经习惯。那个白痴莫星河简直是一头猪，靠一个十几岁的小姑娘养活不说，还每天做着郡王梦。在烟儿做事回来后，还要用辛苦钱去买糖果陪他玩上朝游戏。\n\n    “少爷，你昨天没有吃多少，赶紧去洗洗吃饭吧。”烟儿感觉到少爷这次醒来后，是真的有了很多的改变，这让她感到欣喜。\n\n    “你先吃吧，我马上就去洗。”莫无忌心里升起一种怜惜和触动，两世为人，从未有人如此对他过。前世恋人因为性格冷漠，所以对他虽好，却没有这种触动，最后还暗算了他。\n\n    烟儿急忙说道，“我刚刚吃过，少爷你……”\n\n    烟儿的话没有继续说下去，他看见少爷走到她的木板床边，慢慢的弯下腰，从枕头边拿起一个被啃了一小半的漆黑馒头。\n\n    莫无忌没有说话，他捏着这漆黑坚硬的馒头，心里堵得慌。刚才烟儿说吃过的时候，他就看见了烟儿嘴角边的馒头碎屑。\n\n    他缓缓将馒头送到鼻下，一股淡淡的馊味传来。这散发出馊味的发黑馒头，和那一大碗白米饭形成了鲜明的对比。难怪这丫头正在长身体的时候，还是一头黄发了。\n\n    也许是那馊味刺激了莫无忌的鼻子，他的鼻子有些酸，眼眶也有些发涩。\n\n    “少爷，那你不能吃……”烟儿还以为莫无忌要吃黑馒头，赶紧叫道。\n\n    莫无忌轻轻的抓住了烟儿比他更加粗糙的双手，缓慢的说道，“烟儿，以后只要有大哥一口吃的，就不会饿着你。今天起就不要再出去通宵摆地摊了，记住大哥的话，从现在开始有大哥养你。”\n\n    这一饭之恩，让莫无忌永世不会忘却。\n\n    “少爷……”烟儿惶恐的叫了一句，她有些担心少爷今天是怎么了。\n\n    莫无忌不敢再说下去，他拍了拍烟儿的手，走到了外面擦去了眼角的潮湿，急忙洗漱。\n\n    ……\n\n    吃过早饭，莫无忌走出住处。尽管他强迫烟儿和他一起分了早餐，也强迫烟儿上床休息后才出来，莫无忌心里并不好受。他要尽快找到一份工作，好让烟儿过的轻松一些。\n\n    ……\n\n    在整个承宇领主国，饶州城是最大最繁华的一个城市。走在这繁华的街道上，莫无忌可以感受到饶州城的生活节奏并不会比地球上的大都市慢。\n\n    饶州公会，这里是莫无忌要来找工作的地方。在这里无论是找工作，还是雇工，大家都会选择饶州公会。\n\n    走进公会，莫无忌就可以看见众多招聘的窗口。有些是一些大的工坊长期招工的地方，还有一些是临时招工的地方。除此之外，还有许多招人海报，以及各类求聘的信息。相对地球来说，这里就是一个多功能的人才市场。\n\n    公会实在是太大了，一千多人在其中转悠，依然让人感觉到很是空旷。\n\n    莫无忌稍微转悠了半圈，就看出来了，在公会里面两种人最吃香。栽种药材的，还有一种是勘探矿物的。\n\n    莫无忌摇了摇头，难怪这里虽然也算是科技世界，到现在还没有各种高级家电产品。敢情这里对电子科技人才并不重视，从聘请报酬就能看出。一个专门从事机械的技术工，酬劳是从事栽种药材酬劳的三分之一，是从事勘探矿物酬劳的五分之一。\n\n    莫无忌对这个并不在意，在地球他是生物学专家，又精通植物学。想在这里找一份工作，对他来说，那是轻松无比。\n\n    简单对比了一下，莫无忌很快就发现有很多工作都适合他。\n\n    饶都植药场，招收一名药材嫁接师，酬劳是每月三十银币。招收药园勤工若干，每月酬劳十枚银币，要求精通药材，有经验者优先。铜山矿坊招收探矿师一名，矿物分解师一名，酬劳均是每月五十银币。\n\n    作为一个顶级生物学家，莫无忌认为他完全可以应聘矿物分解师。现在他找工作，并不是看这个工作是不是适合他，也不是看这个工坊是好是坏。对他来说，酬劳最高的就是最好的。因为这些工作，他都不会做很长时间。\n\n    就是矿物分解师了，莫无忌走向了铜山矿坊。就在他要站在后面排队应聘的时候，在铜山矿坊旁边的一个窗口突然挂出一个新的牌子，“承灵极丹工坊，紧急招收炼药助理师数名，酬劳每月十枚金币……”\n\n    莫无忌顿时止住了脚步，他知道在这里通用的货币就是金币、银币和铜币。一枚金币可是能换取一百枚银币，一万枚铜币。一个月酬劳十枚金币，甩掉了别的工种几十条街道了。这种好工作不去做，那实在是对不住他这个一流生物学家。\n\n    炼药不就是制药吗？在地球上，因为病毒抵抗能力增强，西药渐渐被富豪们放弃，相反各种中药，或者是植物提取的药液更受青睐。作为一个顶级生物学家和制药高手，他不知道为多少家公司制过成品药材。他每次出手都是以指导者身份出现的，哪一次不是上百万的报酬？\n\n    莫无忌近水楼台，在承灵极丹工坊的招收告示刚刚挂出，就顺势在承灵极丹工坊的招聘窗口前一坐，同时面带微笑，尽量让自己有气场一些的说道，“我要应聘贵公司……呃，是贵工坊的工作。”\n\n    承灵极丹工坊招聘的人是一名中年妇女，看起来很是精明能干。她看见莫无忌就这样坐在窗口前，并没有拿出什么东西，略微有些疑惑的问道，“请问你要应聘我们工坊的哪一个工种？”\n\n    “我就应聘贵工坊刚刚挂出来的炼药助理师……”\n\n    莫无忌话还没有说完，就感觉到了不对，因为他说出这句话后，周围明显的寂静了起来。这一刻，似乎所有的目光都集中在了他的身上。\n\n    那名中年妇女也一脸震撼的看着莫无忌，好一会才醒悟过来，语气恭谨的问道，“请您出示炼药助理师资格证书原件……”\n命运的转换本来就被够催悲的了，但他并不在意。哪怕是成了一个落魄王子，莫无忌依然没有放在心上。最让他失落和不甘的是他只是一个凡人，一个只有凡根的凡人。\n\n    在地球上，大家都是一样，哪怕不能修炼，他还可以从别的地方击败对手。在这里，不能修炼，那就意味着永远不会再有机会。\n\n    就算是他铁定没有灵根，在没有亲自去测试一下，他还是不会放弃的。怀着这种患得患失的心情，莫无忌也不知道自己是什么时候睡着的。\n\n    莫无忌是被饭香诱醒的，睁开眼睛发现天早已大亮。他一坐起来，就看见了门口破旧的方桌上摆着一大碗米饭，还有一碟咸菜和半根黄瓜。\n\n    “少爷，你醒了，洗漱后来吃饭吧。”不等莫无忌说话，一直关注着他的烟儿就连忙欣喜叫道。\n\n    “你昨晚没睡？”莫无忌看着烟儿苍白的脸色，还有那黑眼圈和疲惫的眼神，就知道烟儿一晚上都没有睡觉。\n\n    “我昨晚帮路姨去摆地摊了，昨天生意很好呢。”尽管烟儿很疲惫，莫无忌依然可以感受到烟儿的欣喜。\n\n    莫无忌明白这是什么欣喜，因为生意很好，路姨给她的报酬也多。\n\n    从木板床上走下来，莫无忌走到烟儿的身边，伸手摸了摸烟儿还是有些凌乱的头发，良久没有说话。\n\n    他肯定烟儿这样通宵出去摆摊赚钱养活他，不是第一次，她都已经习惯。那个白痴莫星河简直是一头猪，靠一个十几岁的小姑娘养活不说，还每天做着郡王梦。在烟儿做事回来后，还要用辛苦钱去买糖果陪他玩上朝游戏。\n\n    “少爷，你昨天没有吃多少，赶紧去洗洗吃饭吧。”烟儿感觉到少爷这次醒来后，是真的有了很多的改变，这让她感到欣喜。\n\n    “你先吃吧，我马上就去洗。”莫无忌心里升起一种怜惜和触动，两世为人，从未有人如此对他过。前世恋人因为性格冷漠，所以对他虽好，却没有这种触动，最后还暗算了他。\n\n    烟儿急忙说道，“我刚刚吃过，少爷你……”\n\n    烟儿的话没有继续说下去，他看见少爷走到她的木板床边，慢慢的弯下腰，从枕头边拿起一个被啃了一小半的漆黑馒头。\n\n    莫无忌没有说话，他捏着这漆黑坚硬的馒头，心里堵得慌。刚才烟儿说吃过的时候，他就看见了烟儿嘴角边的馒头碎屑。\n\n    他缓缓将馒头送到鼻下，一股淡淡的馊味传来。这散发出馊味的发黑馒头，和那一大碗白米饭形成了鲜明的对比。难怪这丫头正在长身体的时候，还是一头黄发了。\n\n    也许是那馊味刺激了莫无忌的鼻子，他的鼻子有些酸，眼眶也有些发涩。\n\n    “少爷，那你不能吃……”烟儿还以为莫无忌要吃黑馒头，赶紧叫道。\n\n    莫无忌轻轻的抓住了烟儿比他更加粗糙的双手，缓慢的说道，“烟儿，以后只要有大哥一口吃的，就不会饿着你。今天起就不要再出去通宵摆地摊了，记住大哥的话，从现在开始有大哥养你。”\n\n    这一饭之恩，让莫无忌永世不会忘却。\n\n    “少爷……”烟儿惶恐的叫了一句，她有些担心少爷今天是怎么了。\n\n    莫无忌不敢再说下去，他拍了拍烟儿的手，走到了外面擦去了眼角的潮湿，急忙洗漱。\n\n    ……\n\n    吃过早饭，莫无忌走出住处。尽管他强迫烟儿和他一起分了早餐，也强迫烟儿上床休息后才出来，莫无忌心里并不好受。他要尽快找到一份工作，好让烟儿过的轻松一些。\n\n    ……\n\n    在整个承宇领主国，饶州城是最大最繁华的一个城市。走在这繁华的街道上，莫无忌可以感受到饶州城的生活节奏并不会比地球上的大都市慢。\n\n    饶州公会，这里是莫无忌要来找工作的地方。在这里无论是找工作，还是雇工，大家都会选择饶州公会。\n\n    走进公会，莫无忌就可以看见众多招聘的窗口。有些是一些大的工坊长期招工的地方，还有一些是临时招工的地方。除此之外，还有许多招人海报，以及各类求聘的信息。相对地球来说，这里就是一个多功能的人才市场。\n\n    公会实在是太大了，一千多人在其中转悠，依然让人感觉到很是空旷。\n\n    莫无忌稍微转悠了半圈，就看出来了，在公会里面两种人最吃香。栽种药材的，还有一种是勘探矿物的。\n\n    莫无忌摇了摇头，难怪这里虽然也算是科技世界，到现在还没有各种高级家电产品。敢情这里对电子科技人才并不重视，从聘请报酬就能看出。一个专门从事机械的技术工，酬劳是从事栽种药材酬劳的三分之一，是从事勘探矿物酬劳的五分之一。\n\n    莫无忌对这个并不在意，在地球他是生物学专家，又精通植物学。想在这里找一份工作，对他来说，那是轻松无比。\n\n    简单对比了一下，莫无忌很快就发现有很多工作都适合他。\n\n    饶都植药场，招收一名药材嫁接师，酬劳是每月三十银币。招收药园勤工若干，每月酬劳十枚银币，要求精通药材，有经验者优先。铜山矿坊招收探矿师一名，矿物分解师一名，酬劳均是每月五十银币。\n\n    作为一个顶级生物学家，莫无忌认为他完全可以应聘矿物分解师。现在他找工作，并不是看这个工作是不是适合他，也不是看这个工坊是好是坏。对他来说，酬劳最高的就是最好的。因为这些工作，他都不会做很长时间。\n\n    就是矿物分解师了，莫无忌走向了铜山矿坊。就在他要站在后面排队应聘的时候，在铜山矿坊旁边的一个窗口突然挂出一个新的牌子，“承灵极丹工坊，紧急招收炼药助理师数名，酬劳每月十枚金币……”\n\n    莫无忌顿时止住了脚步，他知道在这里通用的货币就是金币、银币和铜币。一枚金币可是能换取一百枚银币，一万枚铜币。一个月酬劳十枚金币，甩掉了别的工种几十条街道了。这种好工作不去做，那实在是对不住他这个一流生物学家。\n\n    炼药不就是制药吗？在地球上，因为病毒抵抗能力增强，西药渐渐被富豪们放弃，相反各种中药，或者是植物提取的药液更受青睐。作为一个顶级生物学家和制药高手，他不知道为多少家公司制过成品药材。他每次出手都是以指导者身份出现的，哪一次不是上百万的报酬？\n\n    莫无忌近水楼台，在承灵极丹工坊的招收告示刚刚挂出，就顺势在承灵极丹工坊的招聘窗口前一坐，同时面带微笑，尽量让自己有气场一些的说道，“我要应聘贵公司……呃，是贵工坊的工作。”\n\n    承灵极丹工坊招聘的人是一名中年妇女，看起来很是精明能干。她看见莫无忌就这样坐在窗口前，并没有拿出什么东西，略微有些疑惑的问道，“请问你要应聘我们工坊的哪一个工种？”\n\n    “我就应聘贵工坊刚刚挂出来的炼药助理师……”\n\n    莫无忌话还没有说完，就感觉到了不对，因为他说出这句话后，周围明显的寂静了起来。这一刻，似乎所有的目光都集中在了他的身上。\n\n    那名中年妇女也一脸震撼的看着莫无忌，好一会才醒悟过来，语气恭谨的问道，“请您出示炼药助理师资格证书原件……”\n命运的转换本来就被够催悲的了，但他并不在意。哪怕是成了一个落魄王子，莫无忌依然没有放在心上。最让他失落和不甘的是他只是一个凡人，一个只有凡根的凡人。\n\n    在地球上，大家都是一样，哪怕不能修炼，他还可以从别的地方击败对手。在这里，不能修炼，那就意味着永远不会再有机会。\n\n    就算是他铁定没有灵根，在没有亲自去测试一下，他还是不会放弃的。怀着这种患得患失的心情，莫无忌也不知道自己是什么时候睡着的。\n\n    莫无忌是被饭香诱醒的，睁开眼睛发现天早已大亮。他一坐起来，就看见了门口破旧的方桌上摆着一大碗米饭，还有一碟咸菜和半根黄瓜。\n\n    “少爷，你醒了，洗漱后来吃饭吧。”不等莫无忌说话，一直关注着他的烟儿就连忙欣喜叫道。\n\n    “你昨晚没睡？”莫无忌看着烟儿苍白的脸色，还有那黑眼圈和疲惫的眼神，就知道烟儿一晚上都没有睡觉。\n\n    “我昨晚帮路姨去摆地摊了，昨天生意很好呢。”尽管烟儿很疲惫，莫无忌依然可以感受到烟儿的欣喜。\n\n    莫无忌明白这是什么欣喜，因为生意很好，路姨给她的报酬也多。\n\n    从木板床上走下来，莫无忌走到烟儿的身边，伸手摸了摸烟儿还是有些凌乱的头发，良久没有说话。\n\n    他肯定烟儿这样通宵出去摆摊赚钱养活他，不是第一次，她都已经习惯。那个白痴莫星河简直是一头猪，靠一个十几岁的小姑娘养活不说，还每天做着郡王梦。在烟儿做事回来后，还要用辛苦钱去买糖果陪他玩上朝游戏。\n\n    “少爷，你昨天没有吃多少，赶紧去洗洗吃饭吧。”烟儿感觉到少爷这次醒来后，是真的有了很多的改变，这让她感到欣喜。\n\n    “你先吃吧，我马上就去洗。”莫无忌心里升起一种怜惜和触动，两世为人，从未有人如此对他过。前世恋人因为性格冷漠，所以对他虽好，却没有这种触动，最后还暗算了他。\n\n    烟儿急忙说道，“我刚刚吃过，少爷你……”\n\n    烟儿的话没有继续说下去，他看见少爷走到她的木板床边，慢慢的弯下腰，从枕头边拿起一个被啃了一小半的漆黑馒头。\n\n    莫无忌没有说话，他捏着这漆黑坚硬的馒头，心里堵得慌。刚才烟儿说吃过的时候，他就看见了烟儿嘴角边的馒头碎屑。\n\n    他缓缓将馒头送到鼻下，一股淡淡的馊味传来。这散发出馊味的发黑馒头，和那一大碗白米饭形成了鲜明的对比。难怪这丫头正在长身体的时候，还是一头黄发了。\n\n    也许是那馊味刺激了莫无忌的鼻子，他的鼻子有些酸，眼眶也有些发涩。\n\n    “少爷，那你不能吃……”烟儿还以为莫无忌要吃黑馒头，赶紧叫道。\n\n    莫无忌轻轻的抓住了烟儿比他更加粗糙的双手，缓慢的说道，“烟儿，以后只要有大哥一口吃的，就不会饿着你。今天起就不要再出去通宵摆地摊了，记住大哥的话，从现在开始有大哥养你。”\n\n    这一饭之恩，让莫无忌永世不会忘却。\n\n    “少爷……”烟儿惶恐的叫了一句，她有些担心少爷今天是怎么了。\n\n    莫无忌不敢再说下去，他拍了拍烟儿的手，走到了外面擦去了眼角的潮湿，急忙洗漱。\n\n    ……\n\n    吃过早饭，莫无忌走出住处。尽管他强迫烟儿和他一起分了早餐，也强迫烟儿上床休息后才出来，莫无忌心里并不好受。他要尽快找到一份工作，好让烟儿过的轻松一些。\n\n    ……\n\n    在整个承宇领主国，饶州城是最大最繁华的一个城市。走在这繁华的街道上，莫无忌可以感受到饶州城的生活节奏并不会比地球上的大都市慢。\n\n    饶州公会，这里是莫无忌要来找工作的地方。在这里无论是找工作，还是雇工，大家都会选择饶州公会。\n\n    走进公会，莫无忌就可以看见众多招聘的窗口。有些是一些大的工坊长期招工的地方，还有一些是临时招工的地方。除此之外，还有许多招人海报，以及各类求聘的信息。相对地球来说，这里就是一个多功能的人才市场。\n\n    公会实在是太大了，一千多人在其中转悠，依然让人感觉到很是空旷。\n\n    莫无忌稍微转悠了半圈，就看出来了，在公会里面两种人最吃香。栽种药材的，还有一种是勘探矿物的。\n\n    莫无忌摇了摇头，难怪这里虽然也算是科技世界，到现在还没有各种高级家电产品。敢情这里对电子科技人才并不重视，从聘请报酬就能看出。一个专门从事机械的技术工，酬劳是从事栽种药材酬劳的三分之一，是从事勘探矿物酬劳的五分之一。\n\n    莫无忌对这个并不在意，在地球他是生物学专家，又精通植物学。想在这里找一份工作，对他来说，那是轻松无比。\n\n    简单对比了一下，莫无忌很快就发现有很多工作都适合他。\n\n    饶都植药场，招收一名药材嫁接师，酬劳是每月三十银币。招收药园勤工若干，每月酬劳十枚银币，要求精通药材，有经验者优先。铜山矿坊招收探矿师一名，矿物分解师一名，酬劳均是每月五十银币。\n\n    作为一个顶级生物学家，莫无忌认为他完全可以应聘矿物分解师。现在他找工作，并不是看这个工作是不是适合他，也不是看这个工坊是好是坏。对他来说，酬劳最高的就是最好的。因为这些工作，他都不会做很长时间。\n\n    就是矿物分解师了，莫无忌走向了铜山矿坊。就在他要站在后面排队应聘的时候，在铜山矿坊旁边的一个窗口突然挂出一个新的牌子，“承灵极丹工坊，紧急招收炼药助理师数名，酬劳每月十枚金币……”\n\n    莫无忌顿时止住了脚步，他知道在这里通用的货币就是金币、银币和铜币。一枚金币可是能换取一百枚银币，一万枚铜币。一个月酬劳十枚金币，甩掉了别的工种几十条街道了。这种好工作不去做，那实在是对不住他这个一流生物学家。\n\n    炼药不就是制药吗？在地球上，因为病毒抵抗能力增强，西药渐渐被富豪们放弃，相反各种中药，或者是植物提取的药液更受青睐。作为一个顶级生物学家和制药高手，他不知道为多少家公司制过成品药材。他每次出手都是以指导者身份出现的，哪一次不是上百万的报酬？\n\n    莫无忌近水楼台，在承灵极丹工坊的招收告示刚刚挂出，就顺势在承灵极丹工坊的招聘窗口前一坐，同时面带微笑，尽量让自己有气场一些的说道，“我要应聘贵公司……呃，是贵工坊的工作。”\n\n    承灵极丹工坊招聘的人是一名中年妇女，看起来很是精明能干。她看见莫无忌就这样坐在窗口前，并没有拿出什么东西，略微有些疑惑的问道，“请问你要应聘我们工坊的哪一个工种？”\n\n    “我就应聘贵工坊刚刚挂出来的炼药助理师……”\n\n    莫无忌话还没有说完，就感觉到了不对，因为他说出这句话后，周围明显的寂静了起来。这一刻，似乎所有的目光都集中在了他的身上。\n\n    那名中年妇女也一脸震撼的看着莫无忌，好一会才醒悟过来，语气恭谨的问道，“请您出示炼药助理师资格证书原件……”\n', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '1100', null, '1', '0', '1515418053', null);
INSERT INTO `hxb_book_chapter` VALUES ('5', '第五章 有些事情我很忌', '不怪这名招聘者一脸震惊，也不怪周围的人听说莫无忌要应聘炼药助理师后惊讶。\n\n    饶州虽然是承宇领主国的国都，可是炼药助理师并不多，至于炼药师，那就更少了。\n\n    就算是有一些炼药助理师，那都是年纪很大的老者。如莫无忌这样年轻的炼药助理师，不要说在承宇领主国，就算是在星汉帝国也是不多见的。这种年轻的炼药助理师，那是潜力巨大，前途不可限量的存在。可以说以莫无忌这样年轻的炼药助理师，只要不出意外，几十年后必定是一个真正的炼药师。\n\n    炼药助理师资格证书的原件？莫无忌心里一沉，还要这个东西，这东西他哪里有？\n\n    他下意识的看了看周围惊羡甚至疑惑的眼光，总算有了一些明白。咳嗽了一声，莫无忌若无其事的继续说道，“我还没有来得及去参加炼药助理师的考核，但是我已经达到了炼药助理师的水平。”\n\n    周围一阵牙疼的声音，莫无忌明显感觉到落在自己身上的目光有了一些变味。\n\n    那名招聘者的脸色也沉了下来，脸上谨慎的表情消失的无影无踪。她依然没有发火，而是平静的问道，“那你是哪一所学院炼药专业毕业的？导师是哪一位炼药前辈？”\n\n    莫无忌的目光再次扫了一下，他终于发现很多应聘者的手中似乎都抓着一个小本本。他看见了距离他最近的一个应聘者手中的本本上写着，饶州矿业研究学院完业证书。\n\n    这次轮到莫无忌自己牙疼了，他没想到这里应聘和地球上一样，也要毕业证书这个敲门砖。看样子，就算自己去应聘别的工作，没有毕业证书，一样行不通。\n\n    看见这名招聘的中年妇女脸色越来越不好看，莫无忌只好尴尬的笑了笑，“这个……我也是在家自学的，所以还没有什么完业证书，也没有导师。”\n\n    “哈哈哈……”这次终于有人大笑出声，敢情这个让众人觉得惊艳的炼药助理师仅仅是一个吹牛搞笑的家伙。\n\n    承灵极丹工坊的这名招聘者没有笑，而是脸色阴沉的可怕。承灵极丹工坊在整个承宇领主国也是数一数二的炼药工坊，竟然有人敢来这里戏弄她。\n\n    看见这名中年妇女脸色难看，似乎要发作的出来，莫无忌心道不好。就在这个时候，一个突兀的声音打断了想要说话的莫无忌，“咦，这不是我们的王上吗？我王在上，怎么我王亲自来公会了？今天不用去城外树林上朝？哦，对了，我王是来巡察这里。你看看我，可真是没有礼貌，赶紧参见王上。”\n\n    语气带着讥讽和轻佻，哪里有半分他嘴里说的礼貌。\n\n    莫无忌回头看见走过来的是一名身穿灰色休闲服的年轻男子，这男子他见过，昨天晚上似乎和闻曼珠走在一起的几个青年男女之一。长的还算是不错，人看起来有些浮华。\n\n    “他就是那个北秦郡国的……”招聘的中年妇女恍然明白过来，脸上的愤怒也消失不见。人家一个脑子不正常的人，你有什么好愤怒的？\n\n    周围旁观的人更是哈哈大笑，显然都觉得莫无忌是来搞笑的。\n\n    “赵旭，你觉得很好笑吗？”一个清冷的声音响起，公会里面大笑的声音就好像关起来了一般，突兀静止。\n\n    一名身穿紫裙的少女站在了公会门口，少女纤腰长发，俏丽的容颜让整个公会顿时失色。这些其实不是重点，重点是这个少女大部分都认识。承宇领主国饶闲郡侯闻莒的独生女闻曼珠。\n\n    一个郡侯在整个星汉帝国就好像大海中的一滴水，什么都不是。但在饶州这个公会里面，那绝对是有最顶级份量的。\n\n    “曼珠……”赵旭脸色尴尬的叫了一句，只是因为闻曼珠难看的脸色，他没有敢继续说下去。他心里有些后悔，因为刚才没有看见闻曼珠过来，之前在闻曼珠眼里留下来的好印象毁于一旦。曾经他可是在闻曼珠面前评价过莫无忌，说莫无忌是情有可原。今天的一句讥讽，让他原形毕露。\n\n    闻曼珠没有理睬赵旭，而是走到莫无忌身前，取出一个布袋递给莫无忌，“星河，这个你拿回去给烟儿吧。”\n\n    布袋中发出了的叮当声音，莫无忌就知道那是金币的碰撞。对于急需用钱的莫无忌来说，不要说这么多金币，就算是一枚银币那都是他极度渴望的。\n\n    对闻曼珠，莫无忌既没有好感也没有恶感。对于她抛弃落魄甚至已经疯了的莫星河，莫无忌也不会觉得愤怒无比。这种人在地球太多了，太现实。如果莫家落魄，莫星河疯掉，闻曼珠依然不弃不离，那才是怪事。这个世上能共富贵的太多，又有多少人可以共患难呢？\n\n    因为他的遭遇还有这个前身的遭遇，让他没有任何兴趣结交闻曼珠这种女人。哪怕闻曼珠再漂亮，他也不想和这种女人扯上关系。他渴望钱，也不会去拿闻曼珠这个女人的钱，他莫无忌有莫无忌的生存方式，他有他的傲骨。\n\n    莫无忌想到了烟儿，烟儿就对他不弃不离。如烟儿这样的女孩又有几个？一生中能遇见一个，已经是敲破了无数木鱼求来的。经历过，莫无忌才更加清楚，这有多么可贵。\n\n    “既然是给烟儿的，那你就自己去给吧。”莫无忌说完转身就走，走了两步后，他忽然停了下来，再次回头道，“对了，我叫莫无忌，不是莫星河。百无禁忌中的无忌，当然，我并不是百无禁忌，也有些事情是我很忌的。”\n\n    闻曼珠听到莫无忌的话，竟然有了瞬间的悸动，这是改名了吗？看见莫无忌继续要走，她反应过来，赶紧叫道，“星河……无忌，烟儿不愿意收，你带给……”\n\n    烟儿不愿意收？莫无忌忽然想要哈哈大笑。这丫头对我的脾气，好，就是好。\n\n    “我闻家的炼矿工坊需要雇人，你如果有兴趣的话，可以去……”闻曼珠忽然有一种发自心底的感觉，眼前的莫星河，不，是莫无忌，他再也不是那个颓废做着郡王梦疯了的落魄王子。他变了，那种粪土万户侯的傲气让她清晰的感受到了对方的改变。\n\n    莫无忌再次停了下来，他看着闻曼珠说道，“仰天大笑出门去，我辈岂是炼矿人？就是那个炼药助理师我也懒得去做了，要做就做炼药师。”\n\n    “哈哈哈……”莫无忌说完哈哈大笑，狂放豪迈尽显。\n\n    他不是故作豪迈，而是心有感触。比起那个暗算自己的女人，闻曼珠好了不知道多少倍。可叹他一心扑在了生物药学研究上，竟然连身边的女人是什么样的都不清楚。\n\n    重活一世，他莫无忌诀不允许这种事情再发生，诀不允许。\n\n    “哈哈……”公会大厅再次笑声一片，显然没有人认为莫无忌说的话是真的。更多的人都认为莫无忌并没有康复，只是他疯狂的方向从郡王换成了炼药师。\n\n    如果说莫无忌成为郡王还有一线希望的话，那他成为炼药师，那是半点希望都没有。', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '1330', null, '1', '0', '1515418053', null);
INSERT INTO `hxb_book_chapter` VALUES ('6', '第六章 丹炉炼药', '“年轻人，等等”就在莫无忌即将走出公会大门的时候，一个略显苍老的声音叫住了他。\n\n    莫无忌回头看见的是一名中年男子，他的容貌看起来远没有他的声音那么苍老。\n\n    “你叫我？”莫无忌打量了一番这个中年男子，这是一个眼神极为锐利的家伙，似乎一切东西在他的眼中都无可遁形。\n\n    “没错，是我叫你。如果你不介意的话，我们可以找一个安静的地方谈谈。”中年男子说完，指了指公会大厅边上的茶室。\n\n    莫无忌呵呵一笑，“我当然不在意。”\n\n    ……\n\n    看着莫无忌和那中年男子走向公会茶室，公会大厅中的人纷纷议论起来。\n\n    “刚才那是丹汉炼药的坊主陆九钧吧?谁都知道那家伙是假炼药助理师，难道他要招收那个疯子？他不怕自己的工坊再次倒闭？这脑子好像不大灵光啊。”\n\n    “嘿嘿，你也知道说再次。陆九钧如果脑子灵光的话，就不会将丹汉炼药带垮了。这家伙给人第一眼很精明，其实真不精明，看看丹汉炼药就知道了。”\n\n    “陆九钧脑子不大灵光谁不知道？可惜了丹汉炼药，那曾经是我承宇领主国的数一数二的大炼药工坊，就算是在星汉帝国也是排的上号的。现在只是一个小作坊而已，再过一段时间，就要销声匿迹咯……”\n\n    “谁让人家命好，能够继承丹汉炼药。就算是跨了，好歹也曾经拥有过。至少人家不用和我们一样，在这里找事做。”\n\n    ……\n\n    议论纷纷的话语，莫无忌是听不到的了。此刻他已经和陆九钧坐在了茶室当中，陆九钧叫了一壶饶城春晓。\n\n    淡绿的茶水倒出来，散发出淡淡的清香。莫无忌忍不住端起杯子喝了一口，满嘴余香，浑身舒畅，他有些忍不住的赞了一声，“好茶。”\n\n    前世莫无忌对喝茶不讲究，他毕竟处于那个高度，各种各样的好茶都喝过，还真没有几样能和这个茶相比。况且莫无忌还可以看出这茶是随便冲出来的，没有讲究各种细节，否则的话味道可能还会更上层楼。\n\n    陆九钧微微一笑，“饶城春晓是承宇国三大名茶之一，每年都会进贡一些给帝国王室。我们喝的仅仅是老叶做的，这老叶，也不是一般人可以喝得起的。我先自我介绍一下，鄙人陆九钧，现在是丹汉炼药的坊主……”\n\n    说到这里，陆九钧特意停下了话看着莫无忌。他相信莫无忌必定会接着说，原来是丹汉炼药，这我知道。\n\n    莫无忌听出来了陆九钧的意思，看样子这个丹汉炼药还真不简单，他还是略带歉意的说道，“抱歉，陆坊主，我还真没听说过丹汉炼药。”\n\n    陆九钧张大嘴巴，似乎有些不敢相信莫无忌的话。在饶州，还有谁没有听说过丹汉炼药？哪怕丹汉炼药已经垮掉，众多的商铺和各地工坊被别人吞掉，可这也是几年内的事情，名气不会跌的这么快吧？\n\n    讶异的片刻后，陆九钧就明白过来，自嘲的说道，“看样子是我自作多情了，莫公子一心复国，自然不会在意一些商业上的事情。”\n\n    莫无忌见陆九钧明白，也不再转弯，直接说道，“陆坊主知道我的来历，想必不会是来邀请我去做炼药师的吧？”\n\n    他刚才在公会里面狂言要做就做炼药师，但莫无忌很有自知之明，没有人会将他的狂言放在心上。\n\n    陆九钧端起茶杯喝了一口茶，不慌不忙的放下茶杯，面带笑容的说道，“莫公子还真说对了，我就是来请莫公子去担任我丹汉炼药首席炼药师的。”\n\n    这次轮到莫无忌惊诧了，他下意识的顿住了端茶杯的手，完全不明白的问道，“陆坊主难道没有听说过我的事情？你就确定我刚才不是在吹牛？或者是正常人说的话？”\n\n    陆九钧淡定的说道，“我炼药不行，但自认这双眼睛还不错。公子刚才狂言的时候，眼里的自信和浑身的精气神，那绝对不是吹牛可以做出来的，也不是一个脑子有问题的人可以做到的。再说，公子刚才的话更是给了我信心，那绝对不是一个不正常人说出来的。所以，我赌一把，莫公子说的是真的。怎么样，莫公子，有没有兴趣和我合作一次？”\n\n    说完，陆九钧又补充了一句道，“对了，我还有一个外号，叫陆疯子。我丹汉炼药之所以败的如此迅速，就因为我曾经疯狂的决定。”\n\n    陆九钧这话连他自己都不相信，他自然不会是因为莫无忌的自信就要聘请莫无忌。也许有这方面的一点点因素，更重要的是一个只有他清楚的原因。那就是莫无忌的爷爷莫天城还有一个别人都不知道的身份，炼药师身份，还是一个水平很高的炼药师。\n\n    他陆家的丹汉炼药之所以能在饶州成名，就是因为北秦郡王莫天城。尽管他爷爷和莫天城是朋友，陆家为了得到莫天城的炼药支持，也付出了极重的代价。\n\n    换句话说，如果不是因为丹汉炼药绝大多数利润都砸到莫天城身上去了，丹汉炼药也不会败的这么快。所以莫无忌说自己是炼药师，别人不相信，他心里是有点相信的。换成他自己是炼药师，他也会传给子孙后代，更何况莫无忌还如此自信。当然，这件事无论莫无忌知道不知道，他都不会去解释。\n\n    听到陆九钧的话，莫无忌反而淡定了下来，他不大相信陆九钧真的是因为判断自己没有说假话，就要聘请他做首席炼药师。天上不会平白掉馅饼，这个道理莫无忌很是清楚。若对方真是这样一个冒失的家伙，估计也没有丹汉炼药。\n\n    “怎么？难道莫公子真的是大话？我要聘请的时候，反而不敢了？”陆九钧见莫无忌不说话，再次挤兑了一句。\n\n    莫无忌并没有因为陆九钧的挤兑而有任何波动，喝了一口茶后才说道，“可以加入，不过我的报酬是多少？”\n\n    “每月五十枚金币，丹汉炼妖提供住处，除此之外，只要不是不合理的要求，丹汉炼药都会尽量满足你。”陆九钧说完，盯着莫无忌。他不相信莫无忌可以拒绝这么高的酬劳，这个酬劳自然比不上一个真正炼药师的酬劳，却比刚才承灵极丹工坊的助理炼药师高出数倍了。\n\n    按照他的猜想，他的这个报价一出来，莫无忌必定会激动的站起来。\n\n    可事实让他很是失落，莫无忌就好像没有听到他的话一般，淡定的喝了一口茶。在陆九钧有些沉不住气的时候，他才问了一句毫不相干的话，“陆坊主，我听说开灵是最耗钱的，不知道开灵要多少钱？”\n\n    莫无忌可不是没有见过钱的人，他之前为了十枚金币激动，那是因为那个时候没有人慧眼认识他，他先临时找一份工作养活他和烟儿而已。就算是应聘上了，他最多做一个月，也会走人。\n\n    而眼前的陆九钧肯定不会让他只做一个月走人，必定要签什么合约之类的玩意。\n\n    陆九钧疑惑的看着莫无忌，莫无忌去开过灵，结果是凡根他也听说过，现在莫无忌问这个是什么意思？\n\n    尽管不知道莫无忌的用意，他还是仔细的回答道，“开灵有三种方式，第一借助灵药开灵，这也是最常用的开灵方式。花费根据灵药的等级而不同，价格最低的都在一万金币往上，第二种是……”\n\n    莫无忌歉意的打断了陆九钧的话，“陆坊主，既然价格最低是一万金币，那肯定有一个价格最高的，请问最高的是多少啊？”\n\n    陆九钧呵呵一笑，“价格最高的是没有价格，听说最珍贵的灵药将整个承宇国卖了也买不起。”\n\n    原来是这样，不知道当初莫星河的父母为他开灵用了多少钱的灵药。这可以猜测的出来，灵药价格越低，那开灵的效果肯定是越差。\n\n    陆九钧继续说道，“第二种是借助强者帮忙开灵，这种收费那都是根据强者的心情了。一般都不会低于十万金币，开价数百万也不高。第三种是借助开灵阵开灵，这种手段在我承宇国是没有的，听说星汉帝国有开灵阵，那都是有一定明显资质的人才有机会进去开灵。”\n\n    莫无忌沉默不语，无论是哪一种开灵，就算是他一个月拿一百金币，那想要赚一笔钱去开灵要到哪一个猴年马月？\n\n    “陆坊主，你去开过灵吗？开灵一次最低要一万金币，估计没有几个人可以开的起吧？那穷人岂不是根本不能开灵？”良久之后莫无忌才说道。\n\n    陆九钧摇了摇头，“我去测试过，只是凡根而已。加上我对修炼并不在意，所以我没有去开灵。”\n\n    “开灵之前的测试也要钱？”莫无忌再问道。\n\n    “那自然要钱，一般是五百金币左右。就算是测试出来有灵根，开灵的时候，也不一定能让你拥有修炼资格。”\n\n    “那一旦测试出来是凡根可以开灵吗？”\n\n    陆九钧笑道，“虽然我对这个不是很懂，也知道凡根开灵成功的可能性百万分之一都没有。只有极少数不甘心没有灵根的人，明知道自己是凡根，还要再花钱去开灵。”\n\n    莫无忌完全明白过来，他那个便宜老爹最后走投无路，明知道他是凡根，也要花费金币去帮他开灵。', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '852', null, '1', '0', '1515418053', null);
INSERT INTO `hxb_book_chapter` VALUES ('7', '第七章 首席炼药师', '“陆坊主，你聘请我，是不是需要我为丹汉炼药研究新药？”莫无忌吸了口气，决定赌一把。他需要的金币太多，不是几十几百可以解决问题的。\n\n    陆九钧犹豫了一下，“也不完全是让你开发新药，我丹汉炼药之所以落魄下来，就是因为我将大量的资金拿去开发新药了。结果新药开发失败，无法及时出来，被对手趁机攻破。也许你可以在我们原来开发的基础上动手，这样可以节约很多的时间和精力。”\n\n    这话陆九钧倒是没有瞎说，莫天城失踪后，丹汉炼药多出了大量的利润。陆九钧可不是一个安稳的主，他立即招聘了数名炼药师开始研发新药。可惜的是丹汉炼药以前的利润被莫天城搜刮空了，靠莫天城走后的积蓄要开发新药还不行。最后竹篮打水一场空，新药没有弄出来，丹汉炼药破产了。\n\n    很多人都认为陆九钧疯狂败家，这才让丹汉炼药破落。只有陆九钧自己清楚，就算是他不研发新药，丹汉炼药迟早也会破落。\n\n    莫天城的心思根本就不在丹汉炼药上，丹汉炼药曾经的品种经过几十年，早已失去了竞争能力。承灵极丹工坊同样药效的药，价格远比丹汉有竞争力。他所做的，不过是拼一把而已。\n\n    “如果是我一个人主导新药研究，我希望能占据新药利润的七成。”莫无忌在知道自己需要大量资金后，直接大开口。他根本就没有打算借助丹汉炼药以前的成果。\n\n    “不可能，你没有炼药师资格证书，居然要七成利润，天下哪有这种道理？我就算是随便请一个炼药师，一个月最多不过两百到三百金币而已。”陆九钧站起来，有些愤怒的说道。\n\n    莫无忌没有半点慌忙，前世这种谈判他不知道经历了多少，岂能被一句话吓退，“陆坊主，你确定一个月两三百金币的炼药师可以为你开发新药？其次，就算是开发了新药，人家不干了再换一家药坊拿出这种新产品，你确信这新药还有利润？好，就算是你签订合约，若是开发周期长，你有那么多金币支付给人家？我不同，新药出来前，我一个月只要你十枚金币。”\n\n    莫无忌是看准了陆九钧身上没有多余的金币聘请真正的炼药师，这才想要碰碰运气，将主意打在他的头上。\n\n    “你除了要新药分成，一个月还要我付十枚金币？”陆九钧觉得自己说话都有些无力了。\n\n    “那当然，陆坊主，你能找到我，足以说明你英明神武。既然如此，你自然是了解我的情况，也知道我不能空着肚子为你研究新药。而且，就算是我无法研究出新药，你损失也不大。一旦你花几百金币请别的炼药师，最后研究不出来新药的话，呵呵，恐怕陆坊主会和我去做邻居了……”莫无忌笑吟吟的说道。\n\n    陆九钧看着莫无忌的笑脸，心里在发恨，如果他再听到谁说这个家伙是脑子坏掉的疯子，他恐怕会冲上去踹那家伙一脚。\n\n    “不行，最多给你五成。”陆九钧犹豫了良久，这才一字一句的说道。\n\n    莫无忌说的也有道理，就算是他研究不出来，几个月损失不过几十金币。一旦请别的炼药师，损失的恐怕是数千金币了。那个时候，也许他陆九钧真的要和这个落魄王子做邻居。\n\n    莫无忌淡定的摸了摸下巴，“五成也可以，不过不是新药的五成了。而是丹汉炼药的五成股份，如果同意的话，我们就签合约。如果不同意的话，我要去考炼药师证书了。别和我说没有毕业证不能考证书，我有的是办法。等我证书拿到，嘿嘿……”\n\n    陆九钧叹了口气，他知道莫无忌捏住了他的命门。如果不找这个莫无忌，就算是他对别的炼药师说将工坊八成给别人，估计也没有人会相信他。\n\n    “好，我同意了。莫公子，你可一定要尽心尽力啊，我的丹汉炼药全靠你了。从今天开始，你就是丹汉炼药的首席炼药师。”陆九钧没有再讨价还价。丹汉炼药新药出来了，新药就是全部利润。所以丹汉炼药工坊的分成和新药的分成，其实都是一个概念，甚至新药的分成可能会更多。\n\n    莫无忌拍了拍陆九钧的肩膀，“老兄是聪明人，看我如何带你发财，重振丹汉炼药。对了，老陆，先借我十块金币，我家里缺米。”\n\n    陆九钧嘴角抽搐了几下，还是取出了一个小布袋递给莫无忌说道，“莫兄弟，我们马上就是一家人了，哪里需要什么见外？金币不够，尽管预支月酬就可以。我回去准备一下，明天你直接来我工坊签约就行。”\n\n    莫无忌接过布袋捏了捏里面的确有将近十枚金币，心里一喜正想多说一句，就看见公会大厅中神情焦急的烟儿。感情这丫头担心自己，又找到公会来了。\n\n    “行行行，我明天去丹汉炼药……”说话间，莫无忌已经走出茶室，远远的摇手叫了一句，“丫头……”\n\n    经过初来时候的迷茫和担忧，在公会和陆九钧一番拉锯战后，莫无忌已经完全恢复了心态。既来之，则安之。\n\n    “少爷……”烟儿看见莫无忌后，眼里的焦急瞬间消失，赶紧一路小跑了过来。\n\n    “走吧，我们回家，顺便买点好吃的带着。”莫无忌一拉烟儿，匆忙和陆九钧打了个招呼，走出了公会。\n\n    说实在话，早上那半碗饭，他根本就没有吃饱。现在身上有金币，不去大吃一顿，那就是对不住他的肚子。\n\n    “承宇开灵塔？”莫无忌刚走出公会大门就停了下来，他看见了街对边有一座淡金色的高塔，高塔外竖着几个大字，承宇开灵塔。\n\n    他之前来找工作的时候，先看见饶州公会，倒是没有注意背后的承宇开灵塔。\n\n    烟儿见莫无忌停下来看着远处的开灵塔，心里叹息一声说道，“少爷，那里是开灵的地方，当初少爷也去过。”\n\n    烟儿在隐约提醒莫无忌，他是凡根。莫无忌听出来了烟儿的意思，还是说道，“烟儿，吃饭的事情等等，我们先去开灵塔看看。”\n\n    对莫无忌来说，现在最重要的就是能够拥有灵根，然后修炼。他和陆九钧一番拉锯战，也是为了修炼而费口舌，否则他哪里赚不到吃饭的钱？\n\n    “啊……”\n\n    烟儿啊了一声后，就已被莫无忌拉着走向了街道对面的开灵塔。\n\n    ……\n\n    “站住，测灵和开灵重地，闲人勿进。”莫无忌和烟儿刚刚走到开灵塔门口，就被人拦住。\n\n    “少爷，要去开灵，必须先登记，然后交纳金币，这才可以进去的。如果存在欺骗行为，会很严重……”烟儿担心莫无忌乱来，小声在莫无忌耳边解释。\n\n    莫无忌点点头，这里他自然不会乱来，这可是要命的事情。他来这里想要询问一下，开灵需要哪些手续，还要缴纳多少金币。\n\n    “咦？”没等莫无忌说话，一声惊咦在他身边响起。\n\n    “小丫头你是来开灵的？”跟着这个惊咦，一个略带沙哑的声音问道。\n\n    莫无忌和烟儿这才看见他们身边不知道何时多了一男一女，男子白须飘飘，看起来一副仙风道骨的模样。女子容颜俏丽，高贵清冷气息渗出，让人不敢逼视。问话的正是那名男子。\n\n    烟儿下意识的退后了一步，摇了摇头。', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '1000', null, '1', '0', '1515418053', null);
INSERT INTO `hxb_book_chapter` VALUES ('8', '第八章 极品灵根', '莫无忌连忙上前抱拳说道，“我们两人只是好奇来看看，听说开灵要很多金币，我们连饭都吃不饱，连银币都没有一枚，哪里来的金币？我两人现在就走。”\n\n    这白须男子的目光有些可怕，莫无忌有一种很难说清楚的感觉，不应该继续留在这里。\n\n    女子冷哼一声，目光从莫无忌手中装金币的布袋掠过，眼里闪过一丝厌恶。很显然，她知道莫无忌手中的布袋总有金币，这才对说谎的莫无忌很是不屑和厌恶。\n\n    莫无忌也算是老于世故，自然看出来了这女人眼里的厌恶，他甚至怀疑自己手中的布袋有没有破洞。否则的话，这女人的眼光难道能穿过他的布袋不成？\n\n    “不要忙着走，既然来了，那就进去看看有没有灵根好了。”白须男子沙哑着继续说道。\n\n    莫无忌冷静下来，他明显的感觉到这白须男子针对的不是他，而是他身边的烟儿。若是为了烟儿的灵根而来，可是烟儿明明没有测试灵根啊，难道他可以看的出来不成？若不是为了烟儿的灵根而来，那又是为什么？他将烟儿拉到了身后，语气慎重的说道，“两位大人，我兄妹二人没有金币，也不想现在去开灵，告辞了。”\n\n    白须男子呵呵一笑，“不是让你们去开灵，测试一下又要不了几个钱，进去吧……”\n\n    测试不要几个钱？莫无忌心里倒是有些疑惑，不是说开灵前的测试至少都要几百金币吗？\n\n    “这位老兄，我可是听说就是测试一下，也要花费五百金币。有些人测试是凡根，为了碰运气去开灵，至少都需要上万金币，用的还是最差的灵药。”如果不是看这两人气度不凡，莫无忌甚至怀疑这两人是人贩子。\n\n    白须男子哈哈一笑，“一般只有检测出来有灵根后，才需要考虑要不要开灵。一旦是凡根，他们就说开灵塔有些灵根检测不出来，索性蛊惑别人直接进行开灵，反正都是不吃亏。”\n\n    莫无忌完全明白过来，什么开灵需要上万金币到几百万金币的？这完全是开灵塔圈钱的勾当。你只要来检测，若是凡根的话，就说不敢肯定是否有灵根，只有开灵后才能确定。\n\n    既然要开灵，那好，金币拿来。甚至有些人和他一样的想法，以为第一次开灵用的灵药不够好，没有开发出隐藏灵根，然后再花钱去做第二次开灵。\n\n    到了这个时候，莫无忌百分之百的肯定他那个便宜老爹第一次带他来开灵被人骗了。\n\n    必定是他没有灵根，开灵塔的那些家伙又想要赚取这笔开灵费。加上莫光远迫切需要他能修炼，这才花费大笔的金币去开灵。当然，能被骗的都是有钱的主。\n\n    “站住。”开灵塔的守卫似乎并不认识这一男一女，同样拦住了两人。\n\n    白须男子冷哼一声，“这里谁负责，给我滚出来。”\n\n    白须男子的冷哼明明听起来就不是很响，莫无忌偏偏感受到自己的耳膜都要被震裂了，心里有一种慌乱。\n\n    只是数秒时间，一名肥胖的中年男子就冲了出来。他到了开灵塔门口，一样有些疑惑的看着这一男一女，显然他也不认识。\n\n    那男子抬手抓出一枚玉牌说道，“带我去这里最好的灵根测试房。”\n\n    肥胖男子看见这枚玉牌，手微微一抖，赶紧恭谨的说道，“是，是，我是这里的负责执事刘椿山，两位大人请跟我来。”\n\n    跟着莫无忌就感受到一股强大的力量带着他，让他身不由己的跟着那一男一女走进了开灵塔。\n\n    莫无忌脸色有些难看，他的确想要再来测试一下自己有没有灵根。但这种毫无反抗能力，被人胁迫进来，让他心里很是不爽。\n\n    开灵塔里面也算是宽敞，莫无忌看见数人正在交纳金币，看样子这也是来测灵或者开灵的人。\n\n    刘椿山很快就将几人带到了开灵塔的第二层，一进入第二层，莫无忌第一眼看见的就是一个约有一丈左右的水晶圆柱。\n\n    “你先去测试，就站在那后面。”白须男子语气敷衍，指了指水晶圆柱后面站立的地方对莫无忌说道。\n\n    莫无忌很清楚他的意思，让他去测试，这是看在烟儿的份上。到了此时莫无忌已然猜到这白须男子就是很看好烟儿的灵根，也不知道他随便看一眼是怎么知道的。\n\n    哪怕曾经测试过一遍，明知道自己是没有灵根的，莫无忌依然有些忐忑。他渴望上次测试错了，自己其实是有灵根的。这一刻，他甚至有些渴望之前说他没有灵根的人，是想要多骗点他那个便宜老爹的金币，让他来测试第二次。\n\n    莫无忌同样清楚，这种可能性很小。\n\n    “少爷，你去测试吧。”莫无忌昨天的话，让烟儿心里很明白，她的少爷是非常渴望有灵根的。\n\n    莫无忌点点头，深深的吸了口气，走到了测试灵根的地方，站了上去。\n\n    水晶圆柱上一道灰光闪了一下，随即就动也不动了。\n\n    莫无忌不知道怎么才有灵根，但是这水晶柱没有半点反应，他也知道这绝对不是有灵根的表现。\n\n    果然，那白须男子有些不耐烦的说道，“最普通的凡根，这辈子注定是凡人一个，下来。”\n\n    莫无忌整个人都是一冷，头有些发晕，强大的克制能力让他若无其事的走下测试台。他略微颤抖的手，表明了此刻他有多失望。哪怕是最差最差的灵根也好啊，为什么只是凡根？\n\n    “少爷，没有灵根也没有关系，老爷也没有灵根的。”烟儿赶紧上来护住了莫无忌。\n\n    莫无忌装作淡定的笑了笑，“没事，没有灵根我还有双手。将来我们照样吃香的喝辣的。”\n\n    他心里叹气，有一句话他没说。老爷就是因为没有灵根，结果在饶州被人吃的骨头渣子都没。\n\n    美貌女子连鄙视都懒得鄙视莫无忌了，吃香的喝辣的，只有蝼蚁一般的凡人才有这种高大理想。\n\n    白须男子根本就没有在意莫无忌，而是笑吟吟的看着烟儿说道，“小姑娘，你来测试。”\n\n    烟儿赶紧摇了摇头，“我不用测试的，我也不想修炼，我要和少爷走了。”\n\n    “既然来了，就测试一下。”随着白须男子的话，烟儿不由自主的落在了那测试灵根的位置。\n\n    莫无忌暗地里握紧了拳头，他的确是想要烟儿测试一下，现在又是这种强行让烟儿去测试，让他愈发不舒服。莫无忌缓缓的吸了口气，哪怕他再不舒服，在这一男一女面前，他也是蝼蚁一般渺小。\n\n    烟儿一落在测试台上，仅仅几秒钟的时间，那水晶柱就突然爆发出一阵阵的青色光芒。很快，这青色光芒就冲到了水晶柱的最上端，只差一尺左右就要冲到顶端了。青色光芒柔和闪亮，犹如一抹青色的光虹。\n\n    “极品灵根！”白须男子和美貌女子几乎是同时叫了出来，莫无忌在他们眼里看见了一种狂热的眼神。\n\n    果然是为了烟儿而来，莫无忌心里泛起一种无力的感觉。', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '1000', null, '1', '0', '1515418053', null);
INSERT INTO `hxb_book_chapter` VALUES ('9', '第九章 无实力无尊严', '“哈哈……”白须男子哈哈大笑，“没想到我离元化这次路过这个小地方，能找到如此资质的弟子……”\n\n    一直都没有说话的俏丽女子忽然出声打断了这白须男子的话，“离元化，她不适合你们离火宗，我决定收下她作为我的关门弟子。”\n\n    离元化犹如被掐住脖子的公鸭，声音戛然而止，片刻后他就怒声叫道，“景飞兰，你是什么意思？你已经有五个弟子了，我现在一个弟子都没有。”\n\n    景飞兰依然平静的说道，“收了她为弟子后，我不会再说起那件事……”\n\n    离元化脸色涨的通红，脖子都气红了，可硬是没有再能辩驳出一个字。\n\n    莫无忌算是看清楚了，离元化有什么把柄在景飞兰的手中。\n\n    足足过了十几秒，离元化这才一字一句的说道，“景飞兰，别的事情我可以答应你，但是这个弟子我必须要收。你不能不讲道理，如果不是我突然发现她有灵性，你能知道她的存在？”\n\n    景飞兰淡淡说道，“她没修炼过，眉心都有灵韵环绕。就算是你不带她进来测试，我也会。”\n\n    说完这句话，景飞兰没有再解释什么，而是取出一个纸折的红色飞鸟，似乎自言自语的说道，“我先发一道讯息去离火宗……”\n\n    离元化看见景飞兰的这个动作，用力的一跺脚，恨恨说道，“算你狠。”\n\n    说完这句话，他转身冲了出去，脚步很快就消失不见。”\n\n    离元化走后，景飞兰这才热切的看着烟儿说道，“你叫什么名字？做我的弟子，和我一起去修炼好不好？”\n\n    烟儿独自照顾莫无忌这么多年，心智远比她的年龄要成熟。经过刚才那一幕，她岂能不知道自己不但有灵根，而且灵根还很不错，这个女人要让她做弟子？\n\n    尽管知道自己有不错的灵根非常开心，但是烟儿却很清楚，一旦做了这个女人的弟子，她就要离开少爷。\n\n    饶州城也出现过有灵根的人，她还从未听说过，有灵根的人要去学艺，还会带着家人的。\n\n    “我叫烟儿，我不能和你一起去修炼，我要和少爷在一起。”烟儿毫不犹豫的摇了摇头。\n\n    “你知道什么是仙师吗？你知道多少人想要渴求这样的机会都得不到吗？不说别的，就是你家的这个少爷，也都很是渴望修炼吧？一旦你进入仙门，你将拥有悠长的寿命，你将主宰天下凡人。”景飞兰很是耐心的劝说道。\n\n    烟儿并不回答景飞兰的话，只是摇头。\n\n    景飞兰看出来了主导的人是莫无忌，她的目光落在莫无忌身上，虽然看起来很是和煦，但是那深处的鄙视却毫不掩饰。\n\n    “烟儿的灵根资质很好，如果留在这里是耽搁了她。若是和我一起走，将来有一天她会成为你们传说之中的仙师。和你在一起，是害了她。我想，你不会要强行留下她吧。如果你为她好，就告诉她，应该怎么做。”\n\n    景飞兰说完这句话后，直直的盯着莫无忌。\n\n    莫无忌吸了口气，他很不爽眼前这个女人的目光，烟儿资质很好，随便去什么地方都能修炼。刚才那个离元化不就想要抢着要烟儿做弟子吗？更何况，这个女人来历不明，谁知道她带走烟儿是干什么。\n\n    如果是对烟儿不利，莫无忌宁可烟儿在他身边。他相信以自己的学识和能力，一定不会浪费烟儿的灵根。\n\n    本来拜这个女人为师也没什么，只要这个女人带他们去看看师门，让他确信不是在骗烟儿就行了。但这个女人一副咄咄逼人的样子，根本就没有任何解释自己身份的想法，莫无忌自然不想让烟儿和一个陌生人一起走。\n\n    “不用这位大姐多费心，我和烟儿在这里过的好好的。丫头，我们走……”莫无忌说完，一拉烟儿的手就要离开。\n\n    景飞兰眼里的煞气一闪而逝，斜跨一步拦在了莫无忌和烟儿身前，“你留下烟儿，是自私。”\n\n    莫无忌冷笑，“我自己家的人，怎么做和你有个什么关系？”\n\n    烟儿不是什么都不懂，她感受到了景飞兰即将发作的怒火，连忙说道，“这位姐姐，请问我的灵根可以转给少爷吗？如果可以的话，我……”\n\n    “走……”烟儿这句话还没有说完，就被景飞兰抓住胳膊带了出去。\n\n    “少爷…….”烟儿惊叫的声音传来，莫无忌就好像心脏被挖走了一般，一阵空虚。\n\n    等他冲出去的时候，哪里还有烟儿和景飞兰的影子？\n\n    “嘭！”莫无忌一拳轰在了身边的一个木架之上，鲜血沿着木架流淌下来，他半点都感受不到疼痛。\n\n    这是他来到这个世界的第一天，他数次硬生生的感受到了自己的无力和软弱。之前和陆九钧谈判时候的优势，此刻荡然无存。在这里，没有实力，就没有尊严。\n\n    ……\n\n    一天后，莫无忌才将这种憋屈隐藏内心最深处，决定先去丹汉炼药。\n\n    丹汉炼药，这个曾经在承宇国第一流的炼药工坊，现在只有一个商铺和唯一的一处工坊。\n\n    莫无忌接连问了数人，这才找到了处于偏僻街道的丹汉炼药工坊。\n\n    陆九钧早已等候多时，看见莫无忌过来，连忙大笑着迎了过来，将莫无忌带进了工坊里面。\n\n    “老陆，你的丹汉炼药还真不小啊，我差点都没有找到。”看见丹汉炼药的落魄情景，莫无忌心里还是有些失望的。\n\n    因为没有实力，昨天的事情他不得不隐匿在心底。从内心最深处来说，莫无忌还是无比的渴望能修炼。前世他就曾经研究过能够拓展经脉的药液，经脉都可以拓展，谁敢说没有灵根的人就不能用药液拓展出灵根？\n\n    正因为存了这种侥幸的念头，莫无忌希望能得到一个更好的研究室，让他是试验拓展灵根的药。丹汉炼药这种状况，显然不可能有太好的研究室。\n\n    陆九钧尴尬的笑了笑，“莫兄弟，我们丹汉炼药除了这个工坊之外，还有一处门店。门店虽然不大，却还算是繁华地带。况且我这个工坊可不是表明看起来这么小，后面还有一个大院子。”\n\n    莫无忌点点头，没有继续就这件事说话。事实上他也清楚，如果不是陆九钧相信他，估计也不会有第二个人和陆九钧一般了，会拿出五成股份让他加入。\n\n    “老陆，我之前的情况你也知道，虽然我懂得炼药，但对市场需求并不是很清楚。我们能不能先谈谈？”\n\n    陆九钧很是豪爽的一扬手，“莫兄弟，就算是你不说，我也会和你谈谈这方面的情况，先里面请吧。”\n\n    跟着陆九钧走上工坊做研究的二楼，莫无忌看见了十几台还算新的机器放在工作桌上。莫无忌并没有接触过这个世界的科技产品，对这些机器的性能也不是很清楚。\n\n    能看见这不大的空间里面摆放了这么多机器，莫无忌心里倒是对陆九钧高看了一些。很显然，陆九钧也是认为研究出新的品种，才是丹汉炼药起死回生的根本。从二楼的窗口，莫无忌也看见了后面的院子，看样子陆九钧说的不错。院子四周有一些房屋，环境还算是安静。这让莫无忌很是满意，这里适合研究他的新药。\n\n    陆九钧看着这些机器，眼里闪过一丝失落，随即就振作精神说道，“莫兄弟，这些机器算是我丹汉炼药现在最值钱的东西了。”\n\n    莫无忌点点头，“看的出来。”\n', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '1000', null, '1', '0', '1515418053', null);
INSERT INTO `hxb_book_chapter` VALUES ('10', '第十章 一线曙光', '“我丹汉炼药原先的主要产品有三种，第一是补血丹，这种丹疗伤非常好。你是北秦郡国出身，自然知道无论是承宇国，还是承宇国的郡国基本上年年都有战争。所以我丹方炼药的补血丹很是畅销。”\n\n    莫无忌没有发表意见，他还真不知道周围国家之间年年都有战争。\n\n    陆九钧继续说道，“第二是壮骨丹，这种丹药是给没有灵根的武者所用，可以辅助武者修炼，提升武者的实力。”\n\n    “武者？”莫无忌疑惑的问道。\n\n    陆九钧点点头，“莫兄弟出身高贵，自然不会去打听这种事情。事实上有灵根的人很少。大部分只有凡根的人，为了增强自己的实力，就选择修武。听说修武到了极致一样可以和有灵根的强者媲美。”\n\n    在陆九钧看来，莫无忌的祖上是炼药师，自然是有灵根能修炼的人。这种家族，看不起武修，不会去打听也是正常。\n\n    莫无忌心里忽然一动，假如他真的无法衍生出灵根，选择修武，会不会行？\n\n    “第三种是我丹汉炼药最著名的，是为提灵丹。这种丹药是集中了许多珍稀药材，甚至还有一种灵药炼制而成。对开灵后的修士都有一定的用处，可以帮助修士快速凝聚天地元气。我丹汉炼药，就是因为提灵丹在整个星汉帝国也有一点点知名度。”陆九钧傲然说道，显然这个提灵丹名声不错。\n\n    莫无忌再次问道，“老陆，我听说丹药都有等级的，不知道丹汉炼药的提灵丹属于什么品级的丹药？”\n\n    听到莫无忌的话，陆九钧老脸一红，尴尬说道，“若是能够的上品级，那还了得。不过我丹汉炼药炼制的提灵丹虽然算不上真正的丹药，却是承宇国可以供修士使用为数不多的丹药。只是后来我们丹汉炼药的新品种跟不上，被别人在价格上打败。至于提灵丹，那是需要一味灵药的，灵药我们也不能时刻弄到，这才到了今天的模样。”\n\n    莫无忌明白过来，原来丹汉炼药最强大的提灵丹连品级都没有。换句话说，提灵丹连丹药都算不上。\n\n    “不知道陆兄对修士和修炼知道多少？”莫无忌并不在意丹汉炼药之前炼制出来的是什么东西，他在意的是自己以后在这里能研究出什么东西。只有研究出好东西，才能赚大钱。赚到大钱，才有机会继续去开灵。\n\n    陆九钧听到莫无忌的话，忽然感觉自己的选择是不是错了。莫无忌的炼药如果传承于莫天城，岂会询问自己这些东西？\n\n    莫无忌立即就感受到了陆九钧的疑惑，连忙说道，“老陆，我目前虽还无法炼制出修士用的丹药，我心里是想朝这方面发展。之前我因为一心复国，没有在意这些东西，现在既然决定再次将炼药当成职业，自然要多了解一些。”\n\n    陆九钧倒是赞同这话，莫无忌说的没错，真正赚钱的不是一般的丹药，而是修士用的东西。\n\n    略一沉吟后，陆九钧说道，“我对修士也了解的不多，听说我们承宇国的护国法师是无限接近地境的强者……”\n\n    “等等，什么是地境的强者？”莫无忌连忙问道。\n\n    陆九钧有些无奈的解释道，“我也不是很清楚，只知道很强很强。承宇国大多数修士都是人境而已，人境有三重天，拓脉、筑灵、脱凡。只有超越了脱凡之后，才是地境。拓脉是开灵后的第一境界，听说最强的天才能够在拓脉境拓展出九十九道灵络。\n\n    有灵根的人，身体中拓展的灵络越多，灵络越厚实和宽阔，修炼就越快。同样的实力也就越强，将来的成就也就越高。如我们凡根，一般是开辟不出来灵络的。”\n\n    莫无忌就好像抓到一道灵光一般，整个人都激动起来，“陆兄，是不是只有拥有灵根的人才可以拓展出经脉？”\n\n    陆九钧白了莫无忌一眼，“是灵络，不是经脉。”\n\n    “呵呵，灵络不就是经脉吗？脉络脉络，其实都是一样啦。”莫无忌呵呵一笑，他也不确定是不是，他猜测应该是这样。\n\n    陆九钧一样弄的不是很明白，也只能道，“那肯定的，凡人是没有脉络的，只有开灵后的灵根拥有者，才能开拓出一条又一条的脉络。”\n\n    似乎受到了莫无忌的引导，陆九钧也直接说脉络，没有再说灵络。\n\n    莫无忌暗地里握紧了拳头，他前世之所以被恋人暗算杀掉，就是因为他研究出来了开拓人体脉络的药液。这绝对是一个划时代的研究，如果他能在这里用这种药液开拓出自己体内的脉络，岂不是和拥有灵根一样能够修炼了？\n\n    这个念头一出来，就好像星星之火一般，在莫无忌心里就再也无法遏制，那种迫切的希望是越来越旺盛。\n\n    “陆兄，我肯定可以让丹汉炼药再次成名。不过我有两个要求。”莫无忌没有再问修炼的事情，他也看的出来陆九钧知道的并不多。\n\n    听到莫无忌的话，陆九钧精神一振，当即说道，“莫大师只要能让丹汉炼药再次起来，不要说两个要求，就是十个要求我也同意了。”\n\n    因为莫无忌的话太让陆九钧激动了，将莫无忌直接从莫兄弟上升到了莫大师。\n\n    莫无忌点点头，“第一，这个研究室从现在开始就是我一个人的地盘，没有我的命令任何人不允许进来。”\n\n    “这个没问题。”不要说莫无忌提了这个要求，就算是莫无忌不提这个要求，他也没有能力再聘请一个炼药师过来。\n\n    “第二个要求就是，我后面会研究一些需要各种药材的丹药，甚至可能是灵药。我希望丹汉炼药能随时提供给我需要的药材。”这一点才是莫无忌的重点。\n\n    莫无忌不知道他在地球上研究出来的东西能不能开辟出灵络，提供吸收灵气，他一定要试试看。万一他成功了，那就意味着他没有灵根，也一样可以修炼。\n\n    陆九钧犹豫了好一会才说道，“莫大师，如果丹汉炼药有利润了，这一条没有问题。”\n\n    莫无忌站起来笑着伸手说道，“老陆，合作愉快，你选择我做你们的炼药师非常明智。”\n\n    陆九钧并不明白莫无忌伸手是什么意思，还是苦笑着也伸出手和莫无忌握了一下。是不是明智，现在他一点底都没有。', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '1000', null, '1', '0', '1515418053', null);
INSERT INTO `hxb_book_chapter` VALUES ('11', '第十一章 就是你了', '和陆九钧签订了合约后，莫无忌并没有在丹汉炼药的实验室呆多久。他只是简单看了一下这里提炼药材的机器，心里有了一个大致的了解。\n\n    陆九钧的机器虽然不少，保存的也好，事实上这些机器远不如他在地球的实验室。在地球的实验室，他是直接可以进行基因提取以及组合凝练的，这里显然不行。\n\n    莫无忌也并不在意，上一世他可以提取出拓展脉络的药液，完全是一次偶然的机会。哪怕没有机器，他凭借千百次的熟练程度，也能再次提炼出那种药液。现在有了这些机器，他更是有把握。\n\n    那个女人不管是因为什么原因将他暗算了，她也无法获得这种药物的提取方法。因为他的药液更加注重的不是什么基因组合，而是提炼手段。他所记录下来的东西也少了很多关键性的数据，更是少了几种植物的部分精华，这些都在他的大脑中记着。\n\n    在没有加入这几种植物的部分精华之前，药液很是寻常。在加入了这些植物的精华之后，整个药液分子结构全部变坏，就算是用DNA检测，也是杂乱无章，甚至每次检测出来的结果都不同。\n\n    但偏偏可以达到拓展经脉的效果，这种道理到现在莫无忌都没有弄清楚。这让莫无忌相信，科学很神奇，天地之中还有更多无法用科学解释的东西。\n\n    莫无忌心里很清楚，无论是被女友暗算，还是烟儿被人带走。在他没有实力之前，都没有任何办法去找到答案。若是他注定了重活一世也无法修炼，他就认命。\n\n    在没有确定自己能不能修炼之前，哪怕还有一丝机缘，他都不会放过。\n\n    ……\n\n    和陆九钧签好合约之后，莫无忌就开始在饶州城转悠。在开始选择研究方向之前，他必须要做一个市场调查。什么药能赚钱，什么不能赚钱，这里最急需的是什么东西。\n\n    从早上转悠到晚上，莫无忌最初的满腔热情已是慢慢的冷却。他以为这个世界的科技水平不如地球，加上他又是顶级生物学家，想要拿出一两个赚钱的产品应该是很轻松的事情。\n\n    可是调查的结果让莫无忌清晰的明白了一个事实，这里的科技也许比不上地球，制药水平绝对不会比地球弱半点。甚至各种美颜产品、强身健体的产品简直和地球上的西药品种一般多。莫无忌没有去试验这些药的效果，人家可以光明正大的拿出来出售，效果差了才是怪事。\n\n    没有产品，再有推销手段，他也无法救活丹汉炼药。救不活丹汉炼药，他想要研究出让他可以修炼以及以后修炼需要的药，那是痴人说梦。\n\n    一天下来得到如此结果，让莫无忌有些失神。就连一名瘦弱少年和他擦身而过撞了他一下，也是没有察觉。\n\n    “偷了东西就想走？”一声冷喝惊醒了莫无忌，莫无忌下意识的抬头，他看见一瘦弱的少年被一名青年抓住手腕。这青年背着一个很大的包裹，脸如刀削，浑身带着一种凌厉的气息。\n\n    “你胡说，赶紧放手…….”瘦弱少年叫了一声，莫无忌这才赶紧摸了一下自己的口袋，装金币的钱袋已经消失不见。\n\n    此刻莫无忌哪里还不知道这个瘦弱少年刚才偷了他的钱包，他毫不犹豫的冲了上去，一把抓住少年的另外一只手，直接将手伸进了少年的胸口。\n\n    这少年没有口袋，如果偷了他的钱包必定在胸口。\n\n    一个软软的馒头小包被莫无忌摸到，随即他在边缘的地方抓出了自己的钱包。\n\n    少年脸色涨的通红，没有敢再挣扎。\n\n    “多谢这位朋友出手相助，若不是朋友，我恐怕连吃饭的钱都没有了。”莫无忌收起钱包，对那抓住小偷的青年抱拳感谢道。\n\n    那青年见莫无忌找回了钱包，点点头，放开了那个瘦弱的小偷。小偷被松开后微微一怔，几秒钟后就好像泥鳅一般钻入人群消失不见。\n\n    这个过程发生的很短，周围很多人甚至都不明白发生了什么事情。\n\n    青年并没有回应莫无忌的感谢，而是打量了莫无忌一番，这才问道，“为何要放走小偷？”\n\n    他刚才松开小偷，只要莫无忌有想法留下小偷，完全可以继续抓住那个小偷。\n\n    莫无忌笑了笑，“这小偷面黄肌瘦，显然是饿得很了，抓住她除了打她一顿又能如何？”\n\n    那个瘦弱的小偷是一个女孩，而且饿得厉害。莫无忌自己就是刚刚从温饱线挣扎过来的，那小女孩身上他看见了烟儿的影子，对那小女孩有些同情。只不过他天生就讨厌小偷，再同情也不会无缘无故拿出钱来去送给一个小偷。让他教训这个瘦弱的小女孩一顿，他也下不去手。\n\n    “朋友如果不介意，不如我做东，去附近酒馆坐坐？”莫无忌随意说了一句后，就转移了话题。\n\n    青年淡淡说道，“只是举手之劳而已，就此别过。”\n\n    莫无忌再次说道，“我是一个炼药师，朋友身上是不是有药材要出售，如果是的话，我正好要用到。”\n\n    作为一个常年和各种植物药材打交道的生物学家，莫无忌早就闻到了青年身上的药材味道。\n\n    “你怎么知道？”青年疑惑的看着莫无忌。\n\n    莫无忌微微一笑，“我闻到了你身上有药材味道，而且药材还很新鲜。”\n\n    青年点点头，“前面那家饶江仙酒店不错，我们就去那里。”\n\n    ……\n\n    饶江仙酒店的确不错，伙计送上来的酒，莫无忌还没有喝就闻到了一股清冽的香味。\n\n    “我叫莫无忌，还未请教朋友怎么称呼？”莫无忌端起酒杯说道。\n\n    “蓝羽。”青年话不是很多，只是报了一个名字后，就将酒杯里面的酒一饮而尽。\n\n    “我是丹汉炼药的炼药师，蓝兄以后如果有药材都可以直接送到我们丹汉炼药。如果找到我需要的特殊药材，价格还……”\n\n    莫无忌说到这里突然顿住，他现在还没有为丹汉炼药找到合适的产品，哪里有钱去购买他需要的药材？\n\n    蓝羽见莫无忌没有继续说下去，以为莫无忌的意思就是价格上涨，随即说道，“当然可以，不过我很快就要离开饶州了。承宇国和昌燕领主国又已开战，急需疗伤药材，我会去接近战场的地方。”\n\n    战争？疗伤药材？莫无忌脑海中就好像被闪电劈了一下般，突然清晰起来。\n\n    他在各大丹房和药铺中的确看见了许多疗伤药品，可再好的外伤药品，哪里有青霉素好？\n\n    事实上陆九钧早就说过疗伤药，之前他信心十足，才并没有在意。现在他在找不到产品的情况下，听到战争和疗伤药材，立即就冒出了青霉素。\n\n    可以说二战的战局被扭转，青霉素有极大的功劳。这可是在二战中救活了千万人的性命。\n\n    莫无忌一拍手，青霉素，就是你了。', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '1000', null, '1', '0', '1515418053', null);
INSERT INTO `hxb_book_chapter` VALUES ('12', '第十二章 将轰动炼药界', '见蓝羽疑惑的看着自己，莫无忌大笑说道，“蓝兄，你将药材卖给我们炼药坊价格甚至比接近战场的地方还要高，为何舍近求远？”\n\n    因为想到了青霉素这个产品，莫无极心情大好。再好的伤药，有青霉素这种阻止细菌感染的好？最重要的是，青霉素他利用丹汉炼药的设备就能生产出来。\n\n    蓝羽有些怀疑的看着莫无忌，刚才莫无忌说是丹汉炼药的炼药师他就有些怀疑了。\n\n    “莫药师，我听说丹汉炼药最近已经很是艰难……”蓝羽语气有些委婉，意思却很明显，那就是你有没有实力？\n\n    如果是在没有想到青霉素之前，莫无忌还不敢吹大牛，现在他毫不犹豫的说道，“蓝兄，你很快就会发现丹汉炼药将再次震惊承宇国所有人的眼球，因为有一个大商人直接入股了数百万金币到丹汉炼药。”\n\n    蓝羽没有去深究这是真是假，就算是假的他也不在意，大不了再将药卖给别人，“好，那我就不求远了。”\n\n    “除了一般的药材外，我还需要一些特别的药材，一会我写好，麻烦你帮我找来。两个月后，无论你有多少药材，都可以拿到丹汉炼药来，我给你比前线边城还要高一成的价格。”莫无忌说着从桌边拿起便签纸和笔。\n\n    青霉素生产可要不了什么药材，他的药材除了用作研究外，还有是给丹汉炼药弄几个附带产品，总不能只出售青霉素一样。当然，最主要的是为了继续炼制出他那种开拓经脉的药液。\n\n    蓝羽微微皱眉，“等等，两个月时间太长，我等不了。”\n\n    莫无忌呵呵一笑，“蓝兄，你急着用钱吗？如果你不是急着用钱，有什么等不了的？战争又不是马上结束。”\n\n    蓝羽略一思索，觉得莫无忌说的对。他去边境交易虽然价格会稍微高一些，也更加危险，反正就两个月而已。\n\n    “就如你所说，你写下你需要的药材吧。”蓝羽是很干脆的人，既然同意了，就不会再犹豫。他感受到了莫无忌说话时候的那种强烈自信。\n\n    莫无忌早就知道蓝羽肯定会答应，不是没办法，没有谁会去战争边城。在蓝羽承诺后，他直接在便笺纸上写下数十种药材递给蓝羽。\n\n    “龙延草、百花果、艾藤、鸡血藤、暗黄参、千年松树脉、火精石……”\n\n    蓝羽看着莫无忌写的东西，顿时皱眉不已，因为很多药材他从未听说过。\n\n    “莫药师，也许你写的药材等级太高，很多我不认识。比如这百花果、艾藤、夜针花……”看了两遍后，蓝羽这才有些无奈的将便签纸还给莫无忌。\n\n    莫无忌瞬间就明白了是怎么回事，应该是这里和地球上起的名字不同。\n\n    想到这里，他赶紧再取了一张便签，开始画图，同时开始解释每一种药材的生长特性。比如说火精石一般在干枯的火山岩的底部有，艾藤是生长在潮湿的地方，夜针花是半夜才开紫色的花……\n\n    “原来你说的百花果就是彩果、夜针花就是夜紫啊……”蓝羽明白过来，随即有些疑惑的看着莫无忌。\n\n    莫无忌是一个药师，岂能认错药材？难道是他弄错了？也不对啊，这么多年来，他可从未听人将彩果叫成百花果的。\n\n    “呵呵，是我的不对。我和导师学的都是上古制药，都忘了将这些名字更改一下。现今的药材很多我还真叫不出名字，我要去购买一本药材辨认才行。”莫无忌面不改色心不跳的说道。\n\n    原来是这么回事，蓝羽倒是没有怀疑莫无忌。毕竟刚才莫无忌将不同名字的药材特性都写出来了，甚至都画出了图形，应该不会有假。\n\n    “好，我先走了，两个月后我来丹汉炼药。”蓝羽点点头，收起了莫无忌留下来的便签，和莫无忌告辞。\n\n    莫无忌心里松了口气，也缓缓走出酒店。一天的市场调查，总算不是一无所获。\n\n    他不怕蓝羽得到他要研究的药液配方，不说他手中有数种重要的药材没有交给蓝羽，就算是全部给了蓝羽，那种药液也不是别人能配出来的。\n\n    ……\n\n    莫无忌刚回到丹汉炼药，陆九钧就急切的找了过来，“莫兄弟，你什么时候开始研制新药？我这里倒是有几个半成品的配方……”\n\n    莫无忌摆摆手，“我已经有了新配方，那些半成品就算了。我倒是要看看丹汉炼药的一些制药典籍。对了，我还缺少一些金币购买部分药材来研究。”\n\n    “这都没有问题，我先带你去丹汉只要的药籍室。”陆九钧毫不犹豫的说道，他也算是急乱投医。\n\n    ……\n\n    丹汉制药虽是没落了，一些药材典籍却不少，很多甚至是市面上买不到的。\n\n    莫无忌一看这些制药书籍，就知道他不需要再去购买什么药材辨认大全了。他随口对身边殷勤的陆九钧说道，“老陆，你帮我去找几个可靠的人手。一旦新药出来，就在我们工坊的院子里面生产。记住，我要炼制的药必须要保密，所以人手一定也要可靠才行。两天后，我们开工。两个月后，就是我们丹汉制药崛起的时候。不，是将轰动整个炼药界。”\n\n    陆九钧的丹汉炼药破落了，也不是没有留下东西，这个药坊的院子就足够大。\n\n    其实莫无忌很清楚，哪怕他再保密，收集青霉发酵青霉素的手段也保不了多久，因为这些实在是太简单了。唯一可以保密的，那就是营养液的制作。营养液也太简单，一旦泄露同样会被模仿出来。但莫无忌不在乎，能赚取到第一桶金，他有的是生钱的手段。不过青霉素轰动整个炼药界，莫无忌相信他是半点都没有说谎，这本来就是一项划时代的产品。\n\n    这句话就好像仙丹一般让陆九钧瞬间恢复了活力，连忙拍着胸脯说道，“莫兄弟你放心，我保证不会有人泄露我丹汉炼药的半点秘密。”\n\n    “等等……”不等陆九钧走，莫无忌找到了纸笔，又写了一大堆的各种器皿交给陆九钧，“还有，要购买这些东西，为制作新药准备。”\n\n    陆九钧疑惑的接过莫无忌递给他的纸，不解问道，“要这么多的各种各样玻璃瓶还有试管做什么？”\n\n    莫无忌一挥手，“这里不用管，你只要听我的就行。两个月后，必定会让丹汉制药起死回生。”\n\n    这些不是为了青霉素，而是为了他的开拓经脉药液。', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '1000', null, '1', '0', '1515418053', null);
INSERT INTO `hxb_book_chapter` VALUES ('13', '第十三章 莫无忌的担忧', '饶州城并不会因为少了一个莫无忌而有半点变化，就算是莫无忌的房东路姨，也只是有些牵挂，而无法去做出什么寻人的举动。?〔{八［一小说{网\n\n　　自从开始生产青霉素以来，莫无忌就一直留在丹汉炼药。青霉素生产这种东西，对他来说实在是太轻松了。不要说是他，就是一些大学毕业设计都有过这种课题。更何况，承宇国的科技也还算是可以，该要的机器都可以找到，最多只是略修改一下而已。\n\n　　摆在莫无忌面前的唯一难处，就是将肌注和静脉注射改成口服青霉素。注射一个太麻烦，第二个也涉及到普及注射的问题。\n\n　　口服青霉素，类似于阿莫西林、氨卞西林、青霉素V钾片等这些东西他不是弄不出来，只不过要弄出这些东西，简单两个月那绝对不行的。\n\n　　普通的注射用青霉素口服，胃酸会破坏几乎百分之九十九的药理成分。对别的人来说也许这很难，莫无忌也只是略微感觉有些麻烦而已。在地球上的时候，他就已经站在了整个药剂学的顶端。他虽然不能完全遏制青霉素口服药效被挥霍，却能保住三到四成药效成功。\n\n　　在一个从未出现过抗生素的地方来说，不要说三四成药效，就算是半成药效，那体现出来的效果也是惊人的。\n\n　　至于青霉素皮试和抗生素反应，直接被莫无忌丢在了一边。相比起青霉素的作用，那些许的副作用根本不算什么。他最多在说明书上解释一下，仅此而已。\n\n　　……\n\n　　对饶州城的人来说，最近最大的变化就是丹汉炼药。丹汉炼药本来被压缩到只有一个店铺和一个工坊，现在就是这唯一的店铺也在一个月前关闭了，不再出售任何药品。\n\n　　按理说丹汉炼药这是要倒闭，然后出售这个店铺。让人疑惑的是，丹汉炼药不但没有出售这个店铺，最近反而在整个饶州城打出了新的广告。\n\n　　九命疗伤液，丹汉炼药花费数千万巨资研出来的最新产品。这是人类的福音，有了九命疗伤液再也不用担心伤口的病毒感染，效用远远出之前丹汉炼药任何产品的数倍。九命疗伤液的口号是，对于伤口感染，只要你还有一口气，就能再给你一条命。当然，这数千万巨资是金币、银币还是铜币，广告上是半个字都不会说的。\n\n　　丹汉炼药之前的疗伤药品，效果并不会比别的疗伤药品差很多。现在九命疗伤液打出的广告说远胜之前丹汉疗伤产品的数倍，这言外之意就是说，九命远胜市面上的任何疗伤产品数倍。\n\n　　一言激起千层浪，这个广告顿时让饶州城所有的目光都集中在了丹汉炼药。饶州是承宇国国都，这就是让整个承宇国的目光都集中在了丹汉炼药。\n\n　　一时间，无数的人涌往丹汉炼药的店铺，想要购买一瓶九命疗伤液，想要知道这是不是真的。\n\n　　承宇国几乎是每天都在战争，每天不知道多少人死于伤口感染。哪怕不说军队，雇佣军、猎人、采药者、寻矿者……这些人都时时刻刻面临着受伤后，面临病毒感染的死亡威胁。要真有九命这种疗伤药液，谁不想要？\n\n　　……\n\n　　丹汉炼药工坊的二楼，6九钧一脸欣喜的冲了进来，“莫兄弟，我们的宣传效果太好。九月十号我们的九命疗伤液真有这种效果，不，只要有三分之一的效果，丹汉炼药就成功……”\n\n　　6九钧的话陡然停住，他看着莫无忌的表情并不欣喜，甚至有些担忧。6九钧心里咯噔一下，难道新药失败了？可是莫无忌不是在院子里面弄了一个又一个的大铁桶，甚至还有一些锅炉吗？这些该不会有假吧？\n\n　　对新药是不是成功，他并没有把握。全力宣传，也是莫无忌的意思，莫无忌说铁定不会失败。可是现在……\n\n　　莫无忌站了起来，走到6九钧身边拍了拍6九钧的肩膀，“老6，你不用担心，药没有问题。”\n\n　　“那你为何看起来忧心忡忡？”6九钧听到药没有问题，心里已是放下了一大半。只要药没有问题，别的都可以解决。\n\n　　莫无忌叹了口气，“老6，你有没有想过，一旦九命疗伤液上市，给我们带来了大量的金钱，那必定就有人觊觎。我们加在一起恐怕都没有自保能力啊……”\n\n　　说心里话，莫无忌也没有想到他稍微宣传一下，会引起这么大的轰动。也没想到，这个地方的人对可以治病毒感染的疗伤药渴求到了这种地步。他很怀疑，这样下去，等药品上市，他有没有能力拿到这第一桶金。\n\n　　青霉素工艺很容易被模仿，他知道，别人不知道啊。万一第一桶金还没弄到手，就有人暗地里对丹汉炼药动手，那可不妙。\n\n　　6九钧听了莫无忌的话微微一怔，随即就是哈哈大笑，“莫兄弟，你因为出身郡王家族，不了解我承宇国的制度也是正常。不要说我承宇国，在星汉帝国整个范围内，工坊生意都是受到保护的，当然前提条件是你要纳一定比例的税额。当初我丹汉炼药的名声都进入星汉帝国了，你看有谁敢在丹汉动手脚？丹汉之所以垮掉，是因为我自己没有能力。”\n\n　　莫无忌并没有因为6九钧的话有半分轻松，丹汉炼药那种强算个屁的强大。都没有能拿出自己的拳头产品，这种生意有人觊觎才是怪事。就好像曾经地球上一家为水果手机代工的加工厂一般，看起来很大，人又很多。事实上那只是做点辛苦钱，还要看别人挑鼻子瞪眼。一旦哪天人家不要他代工了，那就是下一个丹汉炼药。\n\n　　至于6九钧说的受到星汉帝国保护，莫无忌更是不屑。有足够的利益面前，一切都有可能爆。从相恋多少年的恋人可以背后暗算他，他就知道有些事情不是制度和道德可以约束的。\n\n　　可是他要强大，必须要赚钱，根本就没有韬光的可能。\n\n　　按理说就算是有人要抢夺丹汉炼药的利益，也应该是先软后硬。只要他掌控最核心的东西，到时候要点钱卖出去，就不会有生命危险。\n\n　　想到这里，“老6，我丑话先说在前面。万一有人要分一杯羹，拿我的股份可以，钱可不能少我的。”\n\n　　“好，没有问题，你放心。”6九钧几乎没有半点犹豫就同意了莫无忌的话。\n\n　　莫无忌心里暗叹一声，也没有要求6九钧立下合约。有时候自己的实力不到，就算是有一纸合约又如何？\n\n　　他孤单一个人在饶州城，出了什么事情，不会有人帮他。\n\n　　……\n\n　　和6九钧商量好了后，莫无忌没日没夜的在酵青霉素。为了将主动权捏在手中，除了收集青霉这种需要大量人手的事情，别的事情他几乎都是亲力而为。两个月没有回住处，甚至除了购买集中开拓经脉的药材，他都没有好好休息过。\n\n　　两个月过去，他总算是生产出来了一批青霉素药液。\n饶州城并不会因为少了一个莫无忌而有半点变化，就算是莫无忌的房东路姨，也只是有些牵挂，而无法去做出什么寻人的举动。?〔{八［一小说{网\n\n　　自从开始生产青霉素以来，莫无忌就一直留在丹汉炼药。青霉素生产这种东西，对他来说实在是太轻松了。不要说是他，就是一些大学毕业设计都有过这种课题。更何况，承宇国的科技也还算是可以，该要的机器都可以找到，最多只是略修改一下而已。\n\n　　摆在莫无忌面前的唯一难处，就是将肌注和静脉注射改成口服青霉素。注射一个太麻烦，第二个也涉及到普及注射的问题。\n\n　　口服青霉素，类似于阿莫西林、氨卞西林、青霉素V钾片等这些东西他不是弄不出来，只不过要弄出这些东西，简单两个月那绝对不行的。\n\n　　普通的注射用青霉素口服，胃酸会破坏几乎百分之九十九的药理成分。对别的人来说也许这很难，莫无忌也只是略微感觉有些麻烦而已。在地球上的时候，他就已经站在了整个药剂学的顶端。他虽然不能完全遏制青霉素口服药效被挥霍，却能保住三到四成药效成功。\n\n　　在一个从未出现过抗生素的地方来说，不要说三四成药效，就算是半成药效，那体现出来的效果也是惊人的。\n\n　　至于青霉素皮试和抗生素反应，直接被莫无忌丢在了一边。相比起青霉素的作用，那些许的副作用根本不算什么。他最多在说明书上解释一下，仅此而已。\n\n　　……\n\n　　对饶州城的人来说，最近最大的变化就是丹汉炼药。丹汉炼药本来被压缩到只有一个店铺和一个工坊，现在就是这唯一的店铺也在一个月前关闭了，不再出售任何药品。\n\n　　按理说丹汉炼药这是要倒闭，然后出售这个店铺。让人疑惑的是，丹汉炼药不但没有出售这个店铺，最近反而在整个饶州城打出了新的广告。\n\n　　九命疗伤液，丹汉炼药花费数千万巨资研出来的最新产品。这是人类的福音，有了九命疗伤液再也不用担心伤口的病毒感染，效用远远出之前丹汉炼药任何产品的数倍。九命疗伤液的口号是，对于伤口感染，只要你还有一口气，就能再给你一条命。当然，这数千万巨资是金币、银币还是铜币，广告上是半个字都不会说的。\n\n　　丹汉炼药之前的疗伤药品，效果并不会比别的疗伤药品差很多。现在九命疗伤液打出的广告说远胜之前丹汉疗伤产品的数倍，这言外之意就是说，九命远胜市面上的任何疗伤产品数倍。\n\n　　一言激起千层浪，这个广告顿时让饶州城所有的目光都集中在了丹汉炼药。饶州是承宇国国都，这就是让整个承宇国的目光都集中在了丹汉炼药。\n\n　　一时间，无数的人涌往丹汉炼药的店铺，想要购买一瓶九命疗伤液，想要知道这是不是真的。\n\n　　承宇国几乎是每天都在战争，每天不知道多少人死于伤口感染。哪怕不说军队，雇佣军、猎人、采药者、寻矿者……这些人都时时刻刻面临着受伤后，面临病毒感染的死亡威胁。要真有九命这种疗伤药液，谁不想要？\n\n　　……\n\n　　丹汉炼药工坊的二楼，6九钧一脸欣喜的冲了进来，“莫兄弟，我们的宣传效果太好。九月十号我们的九命疗伤液真有这种效果，不，只要有三分之一的效果，丹汉炼药就成功……”\n\n　　6九钧的话陡然停住，他看着莫无忌的表情并不欣喜，甚至有些担忧。6九钧心里咯噔一下，难道新药失败了？可是莫无忌不是在院子里面弄了一个又一个的大铁桶，甚至还有一些锅炉吗？这些该不会有假吧？\n\n　　对新药是不是成功，他并没有把握。全力宣传，也是莫无忌的意思，莫无忌说铁定不会失败。可是现在……\n\n　　莫无忌站了起来，走到6九钧身边拍了拍6九钧的肩膀，“老6，你不用担心，药没有问题。”\n\n　　“那你为何看起来忧心忡忡？”6九钧听到药没有问题，心里已是放下了一大半。只要药没有问题，别的都可以解决。\n\n　　莫无忌叹了口气，“老6，你有没有想过，一旦九命疗伤液上市，给我们带来了大量的金钱，那必定就有人觊觎。我们加在一起恐怕都没有自保能力啊……”\n\n　　说心里话，莫无忌也没有想到他稍微宣传一下，会引起这么大的轰动。也没想到，这个地方的人对可以治病毒感染的疗伤药渴求到了这种地步。他很怀疑，这样下去，等药品上市，他有没有能力拿到这第一桶金。\n\n　　青霉素工艺很容易被模仿，他知道，别人不知道啊。万一第一桶金还没弄到手，就有人暗地里对丹汉炼药动手，那可不妙。\n\n　　6九钧听了莫无忌的话微微一怔，随即就是哈哈大笑，“莫兄弟，你因为出身郡王家族，不了解我承宇国的制度也是正常。不要说我承宇国，在星汉帝国整个范围内，工坊生意都是受到保护的，当然前提条件是你要纳一定比例的税额。当初我丹汉炼药的名声都进入星汉帝国了，你看有谁敢在丹汉动手脚？丹汉之所以垮掉，是因为我自己没有能力。”\n\n　　莫无忌并没有因为6九钧的话有半分轻松，丹汉炼药那种强算个屁的强大。都没有能拿出自己的拳头产品，这种生意有人觊觎才是怪事。就好像曾经地球上一家为水果手机代工的加工厂一般，看起来很大，人又很多。事实上那只是做点辛苦钱，还要看别人挑鼻子瞪眼。一旦哪天人家不要他代工了，那就是下一个丹汉炼药。\n\n　　至于6九钧说的受到星汉帝国保护，莫无忌更是不屑。有足够的利益面前，一切都有可能爆。从相恋多少年的恋人可以背后暗算他，他就知道有些事情不是制度和道德可以约束的。\n\n　　可是他要强大，必须要赚钱，根本就没有韬光的可能。\n\n　　按理说就算是有人要抢夺丹汉炼药的利益，也应该是先软后硬。只要他掌控最核心的东西，到时候要点钱卖出去，就不会有生命危险。\n\n　　想到这里，“老6，我丑话先说在前面。万一有人要分一杯羹，拿我的股份可以，钱可不能少我的。”\n\n　　“好，没有问题，你放心。”6九钧几乎没有半点犹豫就同意了莫无忌的话。\n\n　　莫无忌心里暗叹一声，也没有要求6九钧立下合约。有时候自己的实力不到，就算是有一纸合约又如何？\n\n　　他孤单一个人在饶州城，出了什么事情，不会有人帮他。\n\n　　……\n\n　　和6九钧商量好了后，莫无忌没日没夜的在酵青霉素。为了将主动权捏在手中，除了收集青霉这种需要大量人手的事情，别的事情他几乎都是亲力而为。两个月没有回住处，甚至除了购买集中开拓经脉的药材，他都没有好好休息过。\n\n　　两个月过去，他总算是生产出来了一批青霉素药液。\n饶州城并不会因为少了一个莫无忌而有半点变化，就算是莫无忌的房东路姨，也只是有些牵挂，而无法去做出什么寻人的举动。?〔{八［一小说{网\n\n　　自从开始生产青霉素以来，莫无忌就一直留在丹汉炼药。青霉素生产这种东西，对他来说实在是太轻松了。不要说是他，就是一些大学毕业设计都有过这种课题。更何况，承宇国的科技也还算是可以，该要的机器都可以找到，最多只是略修改一下而已。\n\n　　摆在莫无忌面前的唯一难处，就是将肌注和静脉注射改成口服青霉素。注射一个太麻烦，第二个也涉及到普及注射的问题。\n\n　　口服青霉素，类似于阿莫西林、氨卞西林、青霉素V钾片等这些东西他不是弄不出来，只不过要弄出这些东西，简单两个月那绝对不行的。\n\n　　普通的注射用青霉素口服，胃酸会破坏几乎百分之九十九的药理成分。对别的人来说也许这很难，莫无忌也只是略微感觉有些麻烦而已。在地球上的时候，他就已经站在了整个药剂学的顶端。他虽然不能完全遏制青霉素口服药效被挥霍，却能保住三到四成药效成功。\n\n　　在一个从未出现过抗生素的地方来说，不要说三四成药效，就算是半成药效，那体现出来的效果也是惊人的。\n\n　　至于青霉素皮试和抗生素反应，直接被莫无忌丢在了一边。相比起青霉素的作用，那些许的副作用根本不算什么。他最多在说明书上解释一下，仅此而已。\n\n　　……\n\n　　对饶州城的人来说，最近最大的变化就是丹汉炼药。丹汉炼药本来被压缩到只有一个店铺和一个工坊，现在就是这唯一的店铺也在一个月前关闭了，不再出售任何药品。\n\n　　按理说丹汉炼药这是要倒闭，然后出售这个店铺。让人疑惑的是，丹汉炼药不但没有出售这个店铺，最近反而在整个饶州城打出了新的广告。\n\n　　九命疗伤液，丹汉炼药花费数千万巨资研出来的最新产品。这是人类的福音，有了九命疗伤液再也不用担心伤口的病毒感染，效用远远出之前丹汉炼药任何产品的数倍。九命疗伤液的口号是，对于伤口感染，只要你还有一口气，就能再给你一条命。当然，这数千万巨资是金币、银币还是铜币，广告上是半个字都不会说的。\n\n　　丹汉炼药之前的疗伤药品，效果并不会比别的疗伤药品差很多。现在九命疗伤液打出的广告说远胜之前丹汉疗伤产品的数倍，这言外之意就是说，九命远胜市面上的任何疗伤产品数倍。\n\n　　一言激起千层浪，这个广告顿时让饶州城所有的目光都集中在了丹汉炼药。饶州是承宇国国都，这就是让整个承宇国的目光都集中在了丹汉炼药。\n\n　　一时间，无数的人涌往丹汉炼药的店铺，想要购买一瓶九命疗伤液，想要知道这是不是真的。\n\n　　承宇国几乎是每天都在战争，每天不知道多少人死于伤口感染。哪怕不说军队，雇佣军、猎人、采药者、寻矿者……这些人都时时刻刻面临着受伤后，面临病毒感染的死亡威胁。要真有九命这种疗伤药液，谁不想要？\n\n　　……\n\n　　丹汉炼药工坊的二楼，6九钧一脸欣喜的冲了进来，“莫兄弟，我们的宣传效果太好。九月十号我们的九命疗伤液真有这种效果，不，只要有三分之一的效果，丹汉炼药就成功……”\n\n　　6九钧的话陡然停住，他看着莫无忌的表情并不欣喜，甚至有些担忧。6九钧心里咯噔一下，难道新药失败了？可是莫无忌不是在院子里面弄了一个又一个的大铁桶，甚至还有一些锅炉吗？这些该不会有假吧？\n\n　　对新药是不是成功，他并没有把握。全力宣传，也是莫无忌的意思，莫无忌说铁定不会失败。可是现在……\n\n　　莫无忌站了起来，走到6九钧身边拍了拍6九钧的肩膀，“老6，你不用担心，药没有问题。”\n\n　　“那你为何看起来忧心忡忡？”6九钧听到药没有问题，心里已是放下了一大半。只要药没有问题，别的都可以解决。\n\n　　莫无忌叹了口气，“老6，你有没有想过，一旦九命疗伤液上市，给我们带来了大量的金钱，那必定就有人觊觎。我们加在一起恐怕都没有自保能力啊……”\n\n　　说心里话，莫无忌也没有想到他稍微宣传一下，会引起这么大的轰动。也没想到，这个地方的人对可以治病毒感染的疗伤药渴求到了这种地步。他很怀疑，这样下去，等药品上市，他有没有能力拿到这第一桶金。\n\n　　青霉素工艺很容易被模仿，他知道，别人不知道啊。万一第一桶金还没弄到手，就有人暗地里对丹汉炼药动手，那可不妙。\n\n　　6九钧听了莫无忌的话微微一怔，随即就是哈哈大笑，“莫兄弟，你因为出身郡王家族，不了解我承宇国的制度也是正常。不要说我承宇国，在星汉帝国整个范围内，工坊生意都是受到保护的，当然前提条件是你要纳一定比例的税额。当初我丹汉炼药的名声都进入星汉帝国了，你看有谁敢在丹汉动手脚？丹汉之所以垮掉，是因为我自己没有能力。”\n\n　　莫无忌并没有因为6九钧的话有半分轻松，丹汉炼药那种强算个屁的强大。都没有能拿出自己的拳头产品，这种生意有人觊觎才是怪事。就好像曾经地球上一家为水果手机代工的加工厂一般，看起来很大，人又很多。事实上那只是做点辛苦钱，还要看别人挑鼻子瞪眼。一旦哪天人家不要他代工了，那就是下一个丹汉炼药。\n\n　　至于6九钧说的受到星汉帝国保护，莫无忌更是不屑。有足够的利益面前，一切都有可能爆。从相恋多少年的恋人可以背后暗算他，他就知道有些事情不是制度和道德可以约束的。\n\n　　可是他要强大，必须要赚钱，根本就没有韬光的可能。\n\n　　按理说就算是有人要抢夺丹汉炼药的利益，也应该是先软后硬。只要他掌控最核心的东西，到时候要点钱卖出去，就不会有生命危险。\n\n　　想到这里，“老6，我丑话先说在前面。万一有人要分一杯羹，拿我的股份可以，钱可不能少我的。”\n\n　　“好，没有问题，你放心。”6九钧几乎没有半点犹豫就同意了莫无忌的话。\n\n　　莫无忌心里暗叹一声，也没有要求6九钧立下合约。有时候自己的实力不到，就算是有一纸合约又如何？\n\n　　他孤单一个人在饶州城，出了什么事情，不会有人帮他。\n\n　　……\n\n　　和6九钧商量好了后，莫无忌没日没夜的在酵青霉素。为了将主动权捏在手中，除了收集青霉这种需要大量人手的事情，别的事情他几乎都是亲力而为。两个月没有回住处，甚至除了购买集中开拓经脉的药材，他都没有好好休息过。\n\n　　两个月过去，他总算是生产出来了一批青霉素药液。\n饶州城并不会因为少了一个莫无忌而有半点变化，就算是莫无忌的房东路姨，也只是有些牵挂，而无法去做出什么寻人的举动。?〔{八［一小说{网\n\n　　自从开始生产青霉素以来，莫无忌就一直留在丹汉炼药。青霉素生产这种东西，对他来说实在是太轻松了。不要说是他，就是一些大学毕业设计都有过这种课题。更何况，承宇国的科技也还算是可以，该要的机器都可以找到，最多只是略修改一下而已。\n\n　　摆在莫无忌面前的唯一难处，就是将肌注和静脉注射改成口服青霉素。注射一个太麻烦，第二个也涉及到普及注射的问题。\n\n　　口服青霉素，类似于阿莫西林、氨卞西林、青霉素V钾片等这些东西他不是弄不出来，只不过要弄出这些东西，简单两个月那绝对不行的。\n\n　　普通的注射用青霉素口服，胃酸会破坏几乎百分之九十九的药理成分。对别的人来说也许这很难，莫无忌也只是略微感觉有些麻烦而已。在地球上的时候，他就已经站在了整个药剂学的顶端。他虽然不能完全遏制青霉素口服药效被挥霍，却能保住三到四成药效成功。\n\n　　在一个从未出现过抗生素的地方来说，不要说三四成药效，就算是半成药效，那体现出来的效果也是惊人的。\n\n　　至于青霉素皮试和抗生素反应，直接被莫无忌丢在了一边。相比起青霉素的作用，那些许的副作用根本不算什么。他最多在说明书上解释一下，仅此而已。\n\n　　……\n\n　　对饶州城的人来说，最近最大的变化就是丹汉炼药。丹汉炼药本来被压缩到只有一个店铺和一个工坊，现在就是这唯一的店铺也在一个月前关闭了，不再出售任何药品。\n\n　　按理说丹汉炼药这是要倒闭，然后出售这个店铺。让人疑惑的是，丹汉炼药不但没有出售这个店铺，最近反而在整个饶州城打出了新的广告。\n\n　　九命疗伤液，丹汉炼药花费数千万巨资研出来的最新产品。这是人类的福音，有了九命疗伤液再也不用担心伤口的病毒感染，效用远远出之前丹汉炼药任何产品的数倍。九命疗伤液的口号是，对于伤口感染，只要你还有一口气，就能再给你一条命。当然，这数千万巨资是金币、银币还是铜币，广告上是半个字都不会说的。\n\n　　丹汉炼药之前的疗伤药品，效果并不会比别的疗伤药品差很多。现在九命疗伤液打出的广告说远胜之前丹汉疗伤产品的数倍，这言外之意就是说，九命远胜市面上的任何疗伤产品数倍。\n\n　　一言激起千层浪，这个广告顿时让饶州城所有的目光都集中在了丹汉炼药。饶州是承宇国国都，这就是让整个承宇国的目光都集中在了丹汉炼药。\n\n　　一时间，无数的人涌往丹汉炼药的店铺，想要购买一瓶九命疗伤液，想要知道这是不是真的。\n\n　　承宇国几乎是每天都在战争，每天不知道多少人死于伤口感染。哪怕不说军队，雇佣军、猎人、采药者、寻矿者……这些人都时时刻刻面临着受伤后，面临病毒感染的死亡威胁。要真有九命这种疗伤药液，谁不想要？\n\n　　……\n\n　　丹汉炼药工坊的二楼，6九钧一脸欣喜的冲了进来，“莫兄弟，我们的宣传效果太好。九月十号我们的九命疗伤液真有这种效果，不，只要有三分之一的效果，丹汉炼药就成功……”\n\n　　6九钧的话陡然停住，他看着莫无忌的表情并不欣喜，甚至有些担忧。6九钧心里咯噔一下，难道新药失败了？可是莫无忌不是在院子里面弄了一个又一个的大铁桶，甚至还有一些锅炉吗？这些该不会有假吧？\n\n　　对新药是不是成功，他并没有把握。全力宣传，也是莫无忌的意思，莫无忌说铁定不会失败。可是现在……\n\n　　莫无忌站了起来，走到6九钧身边拍了拍6九钧的肩膀，“老6，你不用担心，药没有问题。”\n\n　　“那你为何看起来忧心忡忡？”6九钧听到药没有问题，心里已是放下了一大半。只要药没有问题，别的都可以解决。\n\n　　莫无忌叹了口气，“老6，你有没有想过，一旦九命疗伤液上市，给我们带来了大量的金钱，那必定就有人觊觎。我们加在一起恐怕都没有自保能力啊……”\n\n　　说心里话，莫无忌也没有想到他稍微宣传一下，会引起这么大的轰动。也没想到，这个地方的人对可以治病毒感染的疗伤药渴求到了这种地步。他很怀疑，这样下去，等药品上市，他有没有能力拿到这第一桶金。\n\n　　青霉素工艺很容易被模仿，他知道，别人不知道啊。万一第一桶金还没弄到手，就有人暗地里对丹汉炼药动手，那可不妙。\n\n　　6九钧听了莫无忌的话微微一怔，随即就是哈哈大笑，“莫兄弟，你因为出身郡王家族，不了解我承宇国的制度也是正常。不要说我承宇国，在星汉帝国整个范围内，工坊生意都是受到保护的，当然前提条件是你要纳一定比例的税额。当初我丹汉炼药的名声都进入星汉帝国了，你看有谁敢在丹汉动手脚？丹汉之所以垮掉，是因为我自己没有能力。”\n\n　　莫无忌并没有因为6九钧的话有半分轻松，丹汉炼药那种强算个屁的强大。都没有能拿出自己的拳头产品，这种生意有人觊觎才是怪事。就好像曾经地球上一家为水果手机代工的加工厂一般，看起来很大，人又很多。事实上那只是做点辛苦钱，还要看别人挑鼻子瞪眼。一旦哪天人家不要他代工了，那就是下一个丹汉炼药。\n\n　　至于6九钧说的受到星汉帝国保护，莫无忌更是不屑。有足够的利益面前，一切都有可能爆。从相恋多少年的恋人可以背后暗算他，他就知道有些事情不是制度和道德可以约束的。\n\n　　可是他要强大，必须要赚钱，根本就没有韬光的可能。\n\n　　按理说就算是有人要抢夺丹汉炼药的利益，也应该是先软后硬。只要他掌控最核心的东西，到时候要点钱卖出去，就不会有生命危险。\n\n　　想到这里，“老6，我丑话先说在前面。万一有人要分一杯羹，拿我的股份可以，钱可不能少我的。”\n\n　　“好，没有问题，你放心。”6九钧几乎没有半点犹豫就同意了莫无忌的话。\n\n　　莫无忌心里暗叹一声，也没有要求6九钧立下合约。有时候自己的实力不到，就算是有一纸合约又如何？\n\n　　他孤单一个人在饶州城，出了什么事情，不会有人帮他。\n\n　　……\n\n　　和6九钧商量好了后，莫无忌没日没夜的在酵青霉素。为了将主动权捏在手中，除了收集青霉这种需要大量人手的事情，别的事情他几乎都是亲力而为。两个月没有回住处，甚至除了购买集中开拓经脉的药材，他都没有好好休息过。\n\n　　两个月过去，他总算是生产出来了一批青霉素药液。\n', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '1000', null, '1', '0', '1515418053', null);
INSERT INTO `hxb_book_chapter` VALUES ('14', '第十四章 轰动的九命疗伤', '“咦，九命疗伤液，效果是别的疗伤药品数倍？”饶州城的主街道上，几名身上带着血迹的士兵忽然停了下来，看着那大大的广告牌。\n\n    “是丹汉炼药的，这个工坊我听说过。断虎，你马上带人去将裘方几个兄弟抬到丹汉炼药。我们先去丹汉炼药店铺门口等你们。”领头的那名彪悍男子立即叫道。\n\n    “队长，上面写着9月10号才开店出售。”\n\n    “今天已经9号了，我们先去等着再说。如果是虚假广告，老子直接拆了他的店。”\n\n    “是！”\n\n    在饶州城，这样浑身血迹的士兵并不在少数。加上最近承宇国战事升级，更多的重伤兵退回了饶州。越来越多的重伤士兵，让人知道前线战事不妙，也使得饶州这种紧张的气氛越来越浓。\n\n    因为丹汉炼药的疗伤广告太过逆天，加上伤兵死亡率也太高，所以将伤兵抬到丹汉炼药店铺的一幕幕并不稀奇。\n\n    短短几天时间，丹汉炼药门口除了众多围观者之外，还多了众多从战场回来带着血煞气息的士兵。\n\n    士兵来的太多，饶州城卫都在开始指挥秩序了。丹汉炼药的店铺所在的位置还算好，加上最近聚集过来的兵士，几乎要将整条街道堵住。\n\n    ……\n\n    星汉历2930年9月10日。\n\n    饶州城丹汉炼药店铺门口，此刻这里几乎是人山人海。看热闹的，怀疑的，更多的是想要购买一瓶试试看的。\n\n    “开门了……”随着人群中有人叫出声，丹汉炼药商铺的大门缓缓打开。\n\n    被围了数天，陆九钧早已见怪不怪。他手中举着一个扩音器，对拥挤的人群说道，“各位朋友，请要购买九命疗伤液的按照秩序排队，我保证每个人都有。我丹汉炼药这次研究出来的新药，绝对是所有受伤患者的福音，不会有任何虚假成分。每购买一瓶九命疗伤液，我们赠送白纱绑带一副，赠送丹汉外伤药粉一包……”\n\n    莫无忌因为考虑到了注射的不方便，弄出来的都是口服青霉素。至于外伤药粉和绑带，那自然都是配合青霉素用的。\n\n    至于青霉素的过敏者，也仅仅是在说明书上稍微提一下而已。每天都有那么多武者因为受伤死去，一旦青霉素的疗效上来了，谁会在乎那些过敏？更何况，口服对过敏要求更低。\n\n    “多少钱一瓶？”杂七杂八的声音同时问了出来，打断了陆九钧的话。无一例外的，关注的都只有一件事，那就是九命疗伤液是多少钱一瓶。\n\n    陆九钧用手压了压，满脸堆笑的说道，“因为我们新药采用的材料是最顶级的药材，所以价格稍微高一些。现在是每瓶两枚金币……”\n\n    一阵阵倒吸凉气的声音传来，随着就有人破口大骂。一瓶疗伤药而已，竟然要卖两枚金币，怎么不去抢？别的疗伤药，好些的才几银币而已，甚至还有十几铜币的。\n\n    原本拥挤长长的队伍，在听到价格后，瞬间走掉一大半。两枚金币一瓶疗伤液，的确是太贵了。一个普通家庭，一年的收入也没有两枚金币。\n\n    看见站队的人变少，陆九钧嘴角也是抽搐了几下。按照他的意思是定价十块银币，可是莫无忌却要定价五金币。最后两金币，还是取了一个中间价。但同时他也不得不同意莫无忌，再用更精美的玻璃瓶包装部分纯度更高一些的青霉素，定价十金币。\n\n    也许是因为丹汉炼药将九命疗伤宣传的太过逆天，所以尽管价格高昂，依然有人来购买。有些是几个人合伙购买一瓶，只有极少数的人一次购买两瓶以上。至于那精装版的，那是一瓶都没有卖掉。\n\n    围观了几天的丹汉炼药，在短短的一个小时之后，人就散空。\n\n    “莫兄弟，我们这次……唉……”陆九钧叹了口气，如果不是想要等几天看看别人的疗效，他都劝莫无忌降价了。\n\n    这次可是生产了将近五万瓶啊，请了二十个人加班加点才弄出来。而且莫无忌在第一批成品出来后，没有半点停留就开始生产第二批，第二批的量比第一批还多。\n\n    他们吆喝了将近一个月，现在才卖出去两百瓶不到。这点东西，连广告费都不够。\n\n    “不用急，过几天再说，我先出去转转。”莫无忌摆摆手，示意陆九钧不用担心。\n\n    对青霉素能不能大卖的事情，莫无忌并不担心，他在意的是自己即将要炼制出来的药液。莫无忌有一种预感，青霉素无法长期获利，他还不如一次将价格定高一些，赚一笔再说。\n\n    再过几天，蓝羽就会将他需要的几十种药材送来，加上他自己这几天购买的，他就可以动手了。\n\n    一些寻常的东西，他反而需要自己出去寻找，现在除了等蓝羽的药材，他只缺少一样东西。哪怕明知道将药方公布，别人也炼制不出拓展经脉的药液，莫无忌还是决定小心为妙。\n\n    陆九钧只能点头，事情都到了这一步，他能如何？\n\n    莫无忌要找的东西甚至算不上药材，因为这种东西太常见了，就是藕丝，藕丝越新鲜越好。经过莫无忌无数次验证，他研究出来的配方如果不加藕丝，虽然对经脉也有效果，却要差一大截。但是加了藕丝，就会产生截然不同的效果。\n\n    不过这个藕丝也要挑选，必须是从根算起第二节藕的部分丝线。\n\n    这他亲自试验过，当初他喝下药液后，他甚至都可以清晰的感受到体内经脉在不断的清晰，整个人的力量都加大不少。要知道，他虽也学武，并不是什么学了内家拳法的高手。\n\n    可惜的是，在他的那条经脉还没有拓展完全，就被他的恋人暗算。\n\n    ……\n\n    如果说第一天，丹汉炼药还卖出去一两百瓶九命疗伤液，那第二天和第三天，丹方炼药的丹汉九命液是一点都卖不出去了。\n\n    陆九钧急的团团转，莫无忌却躲在实验室内不知道鼓捣什么东西，没有半丝着急。除了吃喝拉撒一下，就不见他出来过，甚至连吃饭，也是要人送进去。\n\n    “不行，再这样等下去就是等死。露露，你帮我去将莫兄弟叫出来，我要和他商量一下。”陆九钧终于忍不住门店的清冷。\n\n    “坊主，莫大师说没有事情不要去打搅他……”叫露露的店员，有些胆怯的说了一声。\n\n    “算了，我自己去……”\n\n    陆九钧一摆手，就要上楼，就在此时，一个高大的身影挡在了店门口，“这里谁是掌柜？”', '1509883801', '1', '1', '不朽凡人', '0', '1509883801', '1000', null, '1', '0', '1515418053', null);
INSERT INTO `hxb_book_chapter` VALUES ('15', '第十五章 火爆再现', '煞气和这声音一起冲击而来，陆九钧下意识的退后了数步，这才有些心惊的说道，“我就是丹汉炼药的坊主陆九钧，这位朋友……”\n\n    陆九钧的话突兀顿住，他看见了这名身材高大，浑身血气的大汉身后还有两人。这两人他认识，就是几天前丹汉炼药开业的时候，抬了一个重伤员来的家伙。\n\n    莫不是那家伙死了，现在人家找麻烦上门？\n\n    想到这里陆九钧背后顿时出了一道冷汗，正常情况下出售的药有问题，只要不是普遍，都不会有问题。但一旦别人追究，那就有问题了。若是军队里面的人来追究，那问题就大了。\n\n    “陆坊主，这是我们曲长。我们要购买三百瓶普通的九命疗伤液，不知道价格能不能优惠一点？”那名陆九钧认识的壮汉忽然说道。\n\n    “啊……”陆九钧在瞬间就明白过来，九命疗伤液的效果肯定不凡，否则不会有一个曲长专程前来购买\n\n    就好像喝了一大口****一般，陆九钧心里瞬间就轻松下来，作为一个经商多年的老油条，他太清楚这是怎么回事了。今后九命疗伤液必定会很好卖，他不用继续去担心销售问题。\n\n    “药液倒是还有一些，只是两金币是我们开业推销的价格，现在……”陆九钧自然清楚这些大兵有的是钱。\n\n    “打住，也不要你优惠了，就按照两金币，我们要五百瓶。我们来自铁飞军营，以后会经常惠顾贵店的生意。”那名高大的曲长毫不犹豫的抬手拦住了陆九钧的话。\n\n    “好，一言为定。”陆九钧本就没有打算涨价，铁飞营可是承宇国铁字三营之一。和铁飞营搞好了关系，还怕以后没有生意？\n\n    ……\n\n    承灵极丹工坊，当初丹汉炼药的店铺遍布整个承宇国各大城市的时候，还只是一个小小的作坊。\n\n    而现在，承灵极丹工坊早已成了承宇国数一数二的药品大鳄。承宇国的药品，有将近四成出自承灵极丹工坊。\n\n    此刻承灵极丹工坊的总部大楼的会议厅中，正有数人坐在里面，神态轻松的品茗闲聊着。如今承灵极丹工坊走上了正轨，开会其实就是闲聊。\n\n    “那丹汉炼药当初搞出这么大的声势，我还以为开发出来了多厉害的疗伤药品，没想到只是雷声大雨点小啊，哈哈。”说话的是一名将近六十的老者，也是负责承灵极丹工坊销售的执事，名为刘万胜。\n\n    说实在话，当初丹汉炼药的声势还真将他吓的不轻。现在发现丹汉炼药是纸老虎，他心里舒畅多了。\n\n    坐在上首的是一名年轻女子，正是承灵极丹工坊的坊主糜秀，在听到刘万胜的话后曼声说道，“刘执事，无论丹汉炼药弄出来的声势是真是假，我们做销售的都应该重视。更何况，这才几天时间而已，如果那九命……”\n\n    糜秀的话突兀顿住，一名精悍的青年急匆匆的走进来，将一封信递给了糜秀。\n\n    会议厅的声音为止一顿，都将目光落在了糜秀的身上。\n\n    糜秀撕开信封，将里面的信纸抽了出来，仅仅片刻时间，她的脸色就微微一变，突地站起说道，“今天有数个军营代表去了丹汉炼药。根据情报，这些军营代表最少的也购买了一百瓶九命疗伤液。除此之外，还有一些商人前去。而且这些人都是曾经在丹汉炼药购买过药液的人……”\n\n    一阵窒息的气息弥漫开来，所有的人都清楚糜秀这话是什么意思。九命疗伤液价格如此高昂，这些人还第二次过去做回头客，那就说明了一个问题。这种药液的疗伤效果是真的很厉害，甚至比宣传的还要厉害。\n\n    假如丹汉炼药研究出这么厉害的疗伤药液，他们承灵极丹工坊拿什么和人斗？\n\n    “糜坊主，我们怎么办？”一名略胖的中年男子也站了起来，有些急切的问了一句。\n\n    “马上去丹汉炼药看看，糜齐，你立即通知工坊的几个长老。”糜秀脸色有些严峻，说话的语气极为坚决。\n\n    “是。”那名送信来的青年回答了一声后，匆匆退了出去。\n\n    ……\n\n    蓝羽背着两个硕大的皮袋来到丹汉炼药店铺门口的时候，就怔住了。曾经门可罗雀的丹汉炼药，此刻竟然是人满为患。众多的人面带焦急，却又极为有秩序的站队。队伍已经从丹汉炼药的门口，排了半边街道。\n\n    难道那个九命疗伤液真的如此强大?他不是不知道九命疗伤液的广告，只是这种商家夸大其词的广告他见得多了，所以并没有在意。\n\n    “你干什么？后来的站队。”看见蓝羽要进丹汉炼药，一名男子立即拽住了他。\n\n    蓝羽微微一皱眉，“我不是来买东西的，而是来找莫药师。”\n\n    “您是蓝大哥吗？莫大师吩咐我就在这里等着你。”一名年轻少女急忙走了过来恭谨的说道。\n\n    蓝羽连忙点头，“对，我就是蓝羽，麻烦您带我去见莫药师。”\n\n    少女赶紧说道，“蓝大哥叫我露露就好了，莫大师不在店铺，他在工坊那边，您请跟我来。”\n\n    排队的众人，看着蓝羽被少女带走，都是露出了羡慕的目光。为什么认识莫大师的不是他们？\n\n    九命疗伤液的强大，早已传遍了周边。只要购买到一瓶九命疗伤，就算是自己不用，转手出去就是赚十数银币的事情。\n\n    ……\n\n    “莫大师，您等候的蓝大哥来了。”将蓝羽带到了丹汉炼药工坊莫无忌的实验室门口，露露很是恭谨的叫了一句。\n\n    每一个丹汉炼药的员工，都非常清楚，这几天丹汉炼药的火爆情况是什么原因。都是因为丹汉炼药来了一个了不起的炼药师，莫大师。这个莫大师研究出来了一种革命性的疗伤新药，九命疗伤。\n\n    只要丹汉炼药继续这样下去，可以预期，丹汉炼药将再次崛起，而且还将远胜之前。继续这样下去那是显然的事情，每天排队的人就可以看出来。甚至有人为了先购买到药液，主动要求加价。\n\n    露露作为处理莫无忌琐事的助手，对莫无忌自然是非常尊敬。\n\n    “蓝兄，终于把你等来了。”露露话音刚落下，一直紧闭的实验室小门就被打开了，头发乱糟糟的莫无忌一脸激动的站在门口。\n\n    蓝羽并没有在意莫无忌的样子和实验室里面各种药材的味道，他知道真正有本事的炼药师，大都是都是和莫无忌一样，一旦面对炼药，都是疯狂无比的。\n\n    “莫药师，你果然没有骗我，不但成功了，而且还如此成功。”蓝羽一脸钦佩的说道。\n\n    尽管当初他答应莫无忌留在了饶州，事实上对莫无忌的话并不是非常相信。今天他看见丹汉炼药的盛况，他就知道莫无忌的新药必定会让丹汉炼药再次崛起。\n\n    “多谢蓝兄相信我，蓝兄进来坐一坐吧。”莫无忌说话间目光已经是落在了蓝羽的皮袋上。\n\n    蓝羽看到莫无忌的目光，岂能不知道莫无忌根本就没有坐一坐的想法？他微微一笑，将左手的皮袋拎给莫无忌，“莫药师，这里面都是你需要的药材，一共十份，你查一下看看。”\n\n    莫无忌满脸堆笑的将皮袋抓过来，“不用，不用，岂能不相信蓝兄。”\n\n    说完，他对等在一边的露露说道，“露露，你一定要帮我好好招待蓝兄。蓝兄带来的药材不用清点，他说多少就是多少，另外价格比市面上提高一成。”\n\n    不是莫无忌不想招待蓝羽，而是他对追求修炼太过迫切了。莫无忌很清楚蓝羽为人不错，如果他想要长期留在丹汉炼药的话，他必定会想尽一切办法，将蓝羽弄到工坊来帮忙。\n\n    事实莫无忌心里知道，九命疗伤的价值体现出来，绝对不是小小的丹汉炼药能掌控的。对于陆九钧的话，他是一个字都不相信。在如此大的利益面前，大家还能遵守游戏规则才是怪事。\n\n    他也清楚这不怪陆九钧，因为陆九钧也没有料到九命疗伤会如此抢手。这还是才开始，等青霉素的价值真正体现出来后，恐怕陆九钧连半成都无法保证。', '1509883801', '1', '1', '不朽凡人', '0', '0', '3019', null, '1', '0', '1515418053', '44s');
INSERT INTO `hxb_book_chapter` VALUES ('16', '第十六章 开拓经脉', '五万瓶九命疗伤液，以陆九钧的想法，就算是半年时间能全部卖出去，也算是巨大的成功了。事实情况是第五天就去掉了一大半，这还是加价限购后。再限购，第十二天，九命疗伤也全部卖完。\n\n    加上那些精装版的九命疗伤液，丹汉炼药仅仅利润就将近二十五万金币。或者说，九命疗伤液的成本几乎可以忽略不计，能卖出来的钱都是利润。\n\n    看着店铺外面众多的求药者，而店铺里面没有丹药出售，陆九钧再次焦急起来。\n\n    “露露……算了，我还是自己去找莫兄弟……”陆九钧此刻已经完全相信莫无忌继承了莫天城的制药传承，还青出于蓝。否则的话，如何可以生产出九命疗伤？\n\n    和十天前急着要找莫无忌不同，这一次，他急着要莫无忌抓紧时间炼制第三批九命疗伤液。第二批九命疗伤液已在发酵中，快要出来了。但是莫无忌似乎并没有着手炼制第三批的想法，整天就缩在了研究室中，说要开发新药。\n\n    其实按照陆九钧的想法，丹汉炼药有九命疗伤液就可以了，根本不用开发别的新药。无奈的是，现在莫无忌是爷，他说要开发那就是要开发。\n\n    “坊主，莫大师说，他没有出来之前，任何人都不能去找他，就是送饭，也只能送到窗口就行。如果到餐没吃，就收走下一餐继续送新鲜的。”露露赶紧说道。\n\n    她担心坊主突兀的闯入莫大师的房间，让莫大师和坊主闹矛盾。莫大师可是她见过最有本事的炼药大师，这种大师可千万不能得罪。\n\n    “好吧。”陆九钧无奈的拍了一下自己的脑袋，几天前承灵极丹工坊的人过来找他商量九命疗伤液的事情，他推到了莫无忌的身上。说莫药师还在研究新药，这件事要等莫药师出来才行。莫药师的脾气是，只要在研究药，任何人打搅他，他都会大怒。\n\n    没想到报应不爽，现在轮到他自己头上来了。得罪谁，他也不敢得罪莫无忌这棵摇钱树。\n\n    ……\n\n    莫无忌头发乱糟糟的看着面前桌上的十个青色的玻璃瓶，心里全是满足，这是他连续数天的工作成果。只要将这十瓶中的一瓶喝了，他就可以去看看有没有灵根。如果有灵根，他就继续赚钱，准备开灵。\n\n    按照蓝羽和陆九钧的说法，只有灵络才能吸纳存储灵气。而只有有灵根的人才有灵络，他之所以没有灵根，那是因为灵络堵塞，或者是没有灵络。\n\n    在莫无忌想来，灵络就是脉络。他用自己研究出来的开脉药液，必定能够让脉络畅通，然后可以吸纳存储灵气。只要能吸纳存储灵气，那不就是代表他有灵根了吗？\n\n    “哈哈哈……以后这种药液就叫着开脉药液。”莫无忌忍不住哈哈大笑，这还是他被暗算后，第一次如此开心。\n\n    在楼下焦急不已的陆九钧，听到莫无忌的哈哈大笑，再也顾不得露露的劝说，急匆匆的就要上楼。\n\n    “哈哈，老陆，这几天是不是数钱数的手抽筋？”莫无忌手里拎着一个包，打开门看着想要上来的陆九钧笑道。\n\n    陆九钧如此开心，他又再次炼制出来了开脉药液，按理说他也应该开心才是。可是他心底深处总是有些不安，具体是哪一方面，也说不清楚。\n\n    还是不要管了，先将自己的经脉开拓出来再说。\n\n    陆九钧满脸欣喜的说道，“莫兄弟，我们大获成功了。九命疗伤液大卖，大卖啊……”\n\n    莫无忌很是平淡的一挥手，“这我知道，必须的。对了，我的那份子金币帮我换成最好的金票。”\n\n    “当然，当然，除了这个，我们店铺没……”\n\n    不等陆九钧将话说完，莫无忌就对他摆摆手，“我知道，再大的事情，等我洗完澡，休息一天后再说。”\n\n    说完这句话，莫无忌根本就不理陆九钧，急匆匆的从他身边擦过去，直接前往他的在丹汉炼药的住处。\n\n    拓展经脉的药液已经出来了，对莫无忌来说，现在最重要的是先沐浴更衣，然后睡一觉。等睡觉起来后，立即服用拓展经脉的药液将经脉拓展了，然后再说别的事情。\n\n    赚钱的事情，什么时候都可以。让他能够有灵根，吸纳灵气修炼的事情，才是刻不容缓的。\n\n    陆九钧呆呆的看着莫无忌远去的背影，没有半点脾气。在丹汉炼药，真正的大爷不是他陆九钧，而是莫药师。良久之后，陆九钧才垂着头离开。不要说督促莫无忌生产第三批九命疗伤了，就是和莫无忌商量再扩展几个店铺的事情都没有机会。\n\n    ……\n\n    清洗完毕大睡一觉后，莫无忌神清气爽的拿出了一个青色玻璃瓶。\n\n    这种拓展经脉的药液他喝过一次，知道喝完之后，身体中就会有一条线和火烧一般慢慢延伸。之后，就算是没有学过武的人，也能清晰的感受到自己体内一条经脉被慢慢拓展出来。经脉拓展虽很难受，那种难以言喻的轻松却是发自心底的。\n\n    可惜的是，上一世莫无忌喝了这种药液后，一条经脉还没有完全拓展彻底，就被暗算了。\n\n    平息了自己略有些躁动的心情，莫无忌抓起青色的瓶子，打开瓶口深深吸了口气，然后仰头一饮而尽。\n\n    和上一世一摸一样，略带苦涩的液体从他的咽喉流了下去，就好像一条火线在他体内不断延伸，所有阻拦在这条药液火线之前的堵塞，都会被药液火线烧穿贯通。莫无忌再次清晰的感受到自己体内一条经脉被开拓出来，随着这条药液火线，经脉缓慢的在他身体中伸展。\n\n    身体撕裂的痛楚中又有一种轻松愉悦的感觉，似乎每灼烧一点距离，他身体中的杂质就会被灼烧掉一些。\n\n    莫无忌握紧了拳头，如果经脉就是灵络，一瓶药液可以开拓他的一条经脉，那他通过开脉药液是不是很容易就能成为顶级资质的灵根修炼者？九十九条灵络，那又算什么？他可以轻松达到。', '1509883801', '1', '1', '不朽凡人', '0', '0', '2225', null, '0', '0', '1515418253', '16');
INSERT INTO `hxb_book_chapter` VALUES ('17', '第一章 陨落的天才', '“斗之力，三段！”\n\n　　望着测验魔石碑上面闪亮得甚至有些刺眼的五个大字，少年面无表情，唇角有着一抹自嘲，紧握的手掌，因为大力，而导致略微尖锐的指甲深深的刺进了掌心之中，带来一阵阵钻心的疼痛…\n\n　　“萧炎，斗之力，三段！级别：低级！”测验魔石碑之旁，一位中年男子，看了一眼碑上所显示出来的信息，语气漠然的将之公布了出来…\n\n　　中年男子话刚刚脱口，便是不出意外的在人头汹涌的广场上带起了一阵嘲讽的骚动。\n\n　　“三段？嘿嘿，果然不出我所料，这个“天才”这一年又是在原地踏步！”\n\n　　“哎，这废物真是把家族的脸都给丢光了。”\n\n　　“要不是族长是他的父亲，这种废物，早就被驱赶出家族，任其自生自灭了，哪还有机会待在家族中白吃白喝。”\n\n　　“唉，昔年那名闻乌坦城的天才少年，如今怎么落魄成这般模样了啊？”\n\n　　“谁知道呢，或许做了什么亏心事，惹得神灵降怒了吧…”\n\n　　周围传来的不屑嘲笑以及惋惜轻叹，落在那如木桩待在原地的少年耳中，恍如一根根利刺狠狠的扎在心脏一般，让得少年呼吸微微急促。\n\n　　少年缓缓抬起头来，露出一张有些清秀的稚嫩脸庞，漆黑的眸子木然的在周围那些嘲讽的同龄人身上扫过，少年嘴角的自嘲，似乎变得更加苦涩了。\n\n　　“这些人，都如此刻薄势力吗？或许是因为三年前他们曾经在自己面前露出过最谦卑的笑容，所以，如今想要讨还回去吧…”苦涩的一笑，萧炎落寞的转身，安静的回到了队伍的最后一排，孤单的身影，与周围的世界，有些格格不入。\n\n　　“下一个，萧媚！”\n\n　　听着测验人的喊声，一名少女快速的人群中跑出，少女刚刚出场，附近的议论声便是小了许多，一双双略微火热的目光，牢牢的锁定着少女的脸颊…\n\n　　少女年龄不过十四左右，虽然并算不上绝色，不过那张稚气未脱的小脸，却是蕴含着淡淡的妩媚，清纯与妩媚，矛盾的集合，让得她成功的成为了全场瞩目的焦点…\n\n　　少女快步上前，小手轻车熟路的触摸着漆黑的魔石碑，然后缓缓闭上眼睛…\n\n　　在少女闭眼片刻之后，漆黑的魔石碑之上再次亮起了光芒…\n\n　　“斗之气：七段！”\n\n　　“萧媚，斗之气：七段！级别:高级！”\n\n　　“耶！”听着测验员所喊出的成绩，少女脸颊扬起了得意的笑容…\n\n　　“啧啧，七段斗之气，真了不起，按这进度，恐怕顶多只需要三年时间，她就能称为一名真正的斗者了吧…”\n\n　　“不愧是家族中种子级别的人物啊…”\n\n　　听着人群中传来的一阵阵羡慕声，少女脸颊上的笑容更是多了几分，虚荣心，这是很多女孩都无法抗拒的诱惑…\n\n　　与平日里的几个姐妹互相笑谈着，萧媚的视线，忽然的透过周围的人群，停在了人群外的那一道孤单身影上…\n\n　　皱眉思虑了瞬间，萧媚还是打消了过去的念头，现在的两人，已经不在同一个阶层之上，以萧炎最近几年的表现，成年后，顶多只能作为家族中的下层人员，而天赋优秀的她，则将会成为家族重点培养的强者，前途可以说是不可限量。\n\n　　“唉…”莫名的轻叹了一口气，萧媚脑中忽然浮现出三年前那意气风发的少年，四岁练气，十岁拥有九段斗之气，十一岁突破十段斗之气，成功凝聚斗之气旋，一跃成为家族百年之内最年轻的斗者！\n\n　　当初的少年，自信而且潜力无可估量，不知让得多少少女对其春心荡漾，当然，这也包括以前的萧媚。\n\n　　然而天才的道路，貌似总是曲折的，三年之前，这名声望达到巅峰的天才少年，却是突兀的接受到了有生以来最残酷的打击，不仅辛辛苦苦修炼十数载方才凝聚的斗之气旋，一夜之间，化为乌有，而且体内的斗之气，也是随着时间的流逝，变得诡异的越来越少。\n\n　　斗之气消失的直接结果，便是导致其实力不断的后退。\n\n　　从天才的神坛，一夜跌落到了连普通人都不如的地步，这种打击，让得少年从此失魂落魄，天才之名，也是逐渐的被不屑与嘲讽所替代。\n\n　　站的越高，摔得越狠，这次的跌落，或许就再也没有爬起的机会。\n\n　　“下一个，萧薰儿！”\n\n　　喧闹的人群中，测试员的声音，再次响了起来。\n\n　　随着这有些清雅的名字响起，人群忽然的安静了下来，所有的视线，豁然转移。\n\n　　在众人视线汇聚之处，一位身着紫色衣裙的少女，正淡雅的站立，平静的稚嫩俏脸，并未因为众人的注目而改变分毫。\n\n　　少女清冷淡然的气质，犹如清莲初绽，小小年纪，却已初具脱俗气质，难以想象，日后若是长大，少女将会如何的倾国倾城…\n\n　　这名紫裙少女，论起美貌与气质来，比先前的萧媚，无疑还要更胜上几分，也难怪在场的众人都是这般动作。\n\n　　莲步微移，名为萧薰儿的少女行到魔石碑之前，小手伸出，镶着黑金丝的紫袖滑落而下，露出一截雪白娇嫩的皓腕，然后轻触着石碑…\n\n　　微微沉静，石碑之上，刺眼的光芒再次绽放。\n\n　　“斗之气：九段！级别：高级！”\n\n　　望着石碑之上的字体，场中陷入了一阵寂静。\n\n　　“…竟然到九段了，真是恐怖！家族中年轻一辈的第一人，恐怕非薰儿小姐莫属了。”寂静过后，周围的少年，都是不由自主的咽了一口唾沫，眼神充满敬畏…\n\n　　斗之气，每位斗者的必经之路，初阶斗之气分一至十段，当体内斗之气到达十段之时，便能凝聚斗之气旋，成为一名受人尊重的斗者！\n\n　　人群中，萧媚皱着浅眉盯着石碑前的紫裙少女，脸颊上闪过一抹嫉妒…\n\n　　望着石碑上的信息，一旁的中年测验员漠然的脸庞上竟然也是罕见的露出了一丝笑意，对着少女略微恭声道：“薰儿小姐，半年之后，你应该便能凝聚斗气之旋，如果你成功的话，那么以十四岁年龄成为一名真正的斗者，你是萧家百年内的第二人！”\n\n　　是的，第二人，那位第一人，便是褪去了天才光环的萧炎。\n\n　　“谢谢。”少女微微点了点头，平淡的小脸并未因为他的夸奖而出现喜悦，安静的回转过身，然后在众人炽热的注目中，缓缓的行到了人群最后面的那颓废少年面前…\n\n　　“萧炎哥哥。”在经过少年身旁时，少女顿下了脚步，对着萧炎恭敬的弯了弯腰，美丽的俏脸上，居然露出了让周围少女为之嫉妒的清雅笑容。\n\n　　“我现在还有资格让你怎么叫么?”望着面前这颗已经成长为家族中最璀璨的明珠，萧炎苦涩的道，她是在自己落魄后，极为少数还对自己依旧保持着尊敬的人。\n\n　　“萧炎哥哥，以前你曾经与薰儿说过，要能放下，才能拿起，提放自如，是自在人！”萧薰儿微笑着柔声道，略微稚嫩的嗓音，却是暖人心肺。\n\n　　“呵呵，自在人？我也只会说而已，你看我现在的模样，象自在人吗？而且…这世界，本来就不属于我。”萧炎自嘲的一笑，意兴阑珊的道。\n\n　　面对着萧炎的颓废，萧薰儿纤细的眉毛微微皱了皱，认真的道：“萧炎哥哥，虽然并不知道你究竟是怎么回事，不过，薰儿相信，你会重新站起来，取回属于你的荣耀与尊严…”话到此处，微顿了顿，少女白皙的俏脸，头一次露出淡淡的绯红：“当年的萧炎哥哥，的确很吸引人…”\n\n　　“呵呵…”面对着少女毫不掩饰的坦率话语，少年尴尬的笑了一声，可却未再说什么，人不风liu枉少年，可现在的他，实在没这资格与心情，落寞的回转过身，对着广场之外缓缓行去…\n\n　　站在原地望着少年那恍如与世隔绝的孤独背影，萧薰儿踌躇了一会，然后在身后一干嫉妒的狼嚎声中，快步追了上去，与少年并肩而行…', '1509883801', '1', '2', '斗破苍穹', '0', '1515418053', '1234', null, '1', '0', '1515418053', '4');
INSERT INTO `hxb_book_chapter` VALUES ('18', '第一章 险死还生', '“咣当”一声，一扇锈迹斑斑的的铁门被打开，铁门的余音被周围古老的建筑挡了回来，袅袅不绝。 \n\n    十几个呼吸之后，一名耷拉着脑袋的年轻男子被两名狱卒拽着胳膊，从铁门内拖了出来。这耷拉着脑袋的年轻男子，看似没有了呼吸，甚至连胳膊都瘦弱的只剩下骨头。从他身上血迹斑斑的破烂衣服就可以看出，这年轻男子受到了什么样的摧残和折磨。 \n\n    “篷”这瘦弱的男子被两名狱卒丢在地上，溅起了一些灰尘，然后再无动静。 \n\n    “这小子应该死了”一名矮胖的狱卒盯着扑在地上动也不动的年轻男子问了一句。 \n\n    另外一名狱卒嗯了一声，“应该是死了，谁让他不知死活，竟然敢对简小姐无礼。现在倒好，不但宁府被灭了，他自己也算是解脱了，算是活该。” \n\n    “老会，说话小心点，我们先将他拖出去处理了再说。”那名矮胖的狱卒连忙告诫了一句。 \n\n    听到这矮胖狱卒的话，另外那叫老会的狱卒不由自主的缩了一下头，就要去将地上已经没有气息的男子拉起来，准备拖走。 \n\n    就在这个时候，一个有些柔弱的声音在一边叫道，“两位大哥，我想将我夫君带走。” \n\n    两名说话的狱卒这才看见侧边正站着一名身穿粗布麻衣的女子，这女子虽然穿着粗布麻衣，却依然能隐约看出她的不俗风姿。唯一遗憾的是，她的脸已经毁容，被略微凌乱的头发遮掩，让人不愿再看第二眼。 \n\n    两名狱卒都认出了这个女子是谁，这是宁家仅存的两个活口之一了。 \n\n    宁家整个家都被抄，唯一活着的就是宁家的老管家宁高和这个眼前这个女子。宁高是宁府的管家，宁府被抄家的时候，他离开苍秦国外出有事，所以逃过一劫。而这个女子是宁府的媳妇，也是刚刚从牢狱中被丢出来这个瘦弱男子的妻子，叫纪洛妃。她是因为留在了苍秦二星学院，逃过了一劫。 \n\n    当然，这也是因为纪洛妃和宁家关联不大，她之所以嫁入宁家，是因为宁家和纪家是世交，她和宁城自小定亲。只是纪家以最莫名其妙的方式没落下来，最后只剩下了一个被毁容的纪洛妃。就算是这样，宁城的爷爷宁弘昌依然将纪洛妃收进府中。准备等宁河真正聚气后，再让两人成亲。 \n\n    可惜的是，宁城的资质极差，多少年来就是无法聚气，不但如此，他对容貌被毁的纪洛妃非打即骂。好在纪洛妃作为一个无家可归的媳妇，一直逆来顺受，从不多说什么。这次从学院回来，得知宁家被灭掉，她第一时间就来牢狱这边。本来她想要问问看，宁城能不能出来，却不曾想，宁城已经没有了气息，被狱卒从牢中丢出来了。 \n\n    纪洛妃看见两名狱卒的表情，不等这两名狱卒说话，就拿出了十几枚银币递了过去，“两位大哥，我夫君已经去了，我……” \n\n    “行了，行了，拖走，放在这里难看死了。”那名矮胖的狱卒收起银币，不耐烦的挥挥手说道。 \n\n    他们本来就要处理宁城的尸体，却想不到有人过来处理，而且还给银币，这种好事情，他们当然不会拒绝。 \n\n    纪洛妃听到这话，赶紧将宁城背起来，加快了速度，很快就从牢狱门口消失不见。 \n\n    “这纪洛妃如果不是被毁容了，倒也是也美人胚子。”看着纪洛妃的背影，另外一名狱卒将手中的银币抛了一下，用评判的语气说道。 \n\n    “别废话了，那纪洛妃好歹也是苍秦二星学院的人，就算是再丑，也轮不到我们。”矮胖狱卒拍了拍这说话狱卒的肩膀，拉着他再次走进了铁门。随着又一声咣当巨响，铁门再次关闭起来。 \n\n    …… \n\n    宁小城闻到了一股极为好闻的清香，他睁开眼睛第一眼看见的是一片洁白的颈脖。下一刻，宁小城就反应过来，他被一个女人背着，那清香就是从背着他的女人身上发出来的。 \n\n    不对啊，他记得被田慕琬拒绝后，心情极差，结果走错了路，一个人走到高架上去了。然后一道天边划来的黄芒轰中了他，然后...就没有然后了。 \n\n    难道说他还没有死？这到底是怎么回事？ \n\n    宁小城刚想到这里，各种信息纷沓的拥进了他的脑海中，他竟然重生了。 \n\n    让宁小城惊骇的是，他重生的这个身体原来叫宁城。 \n\n    他之所以惊骇，是因为他原来的名字也是宁城。只是因为他经常做一个噩梦，那就是他看见自己来到了一个极为古怪的地方，被人暗算关进牢狱中，然后受尽折磨而亡。虽然只是一个梦，但是宁城依然将自己的名字改成了宁小城。 \n\n    没想到转来转去，他还是叫了宁城，而且真的重生在他梦境的那个环境中。 \n\n    宁小城想要抬手擦一下额头的冷汗，背着他的那个女子却回头看了一眼，并没有多话。 \n\n    “抱歉啊，我没有力气走路。”见这女子回头看了自己一眼，宁小城有些歉意的说道。让一个女人背着，他确实是有些不自在。 \n\n    “宁家已经没了，你能活着已经很幸运了。”这女子语气平静，似乎就算是宁小城真的死了，她也是这种平静的态度。\n\n    宁小城沉默下来，刚才他已经将一些零碎的信息整理了一下。他现在应该再次叫回了宁城，是苍秦国宁家的独丁。叫宁城就宁城，反正他本来就叫宁城。 \n\n    宁家是苍秦国一个中等的贵族家族，但是人丁单薄。宁城的父母在宁城很小的时候去曼戈海域寻找灵物，然后就没有再回来。宁城一直跟随爷爷宁弘昌生活在苍秦国的国都苍勒城。\n\n    宁府虽大，人却不多。宁城当然也成了宁府的第一人，宁弘昌更是从来都不管宁城。宁城在苍勒城干的所有坏事，都被宁弘昌担了下来。苍勒城到处都是王公贵戚，宁弘昌虽然也是苍秦国的元老，但是比起那些王公贵戚来说，还差了一些。 \n\n    终于有一天，宁城惹下了连他也担不起来的篓子。他竟然冒犯了苍秦国简家的掌上明珠简素婕，不要说简家是宁家惹不起的，就是简素婕的未婚夫裘英光，宁家也惹不起。 \n\n    宁城想到这里，叹了口气。他惹不起的结果，当然是被关进了牢狱中，爷爷宁弘昌为了保出他，耗尽人脉和家财，结果他没有被保出来，反而将整个宁家都搭了进去。这个消息还是他的前身从狱卒的口中得知的，可以说宁家也是因为他被灭掉了。 \n\n    宁城将这些事情理清楚后，心里再次叹息了一声，这个世界是没有道理可言的，谁的拳头大，谁就是道理。 \n\n    同时他也明白了这个背着他的人是谁，这个毁容的女子是他的未婚妻，叫纪洛妃，有些类似于地球上的童养媳。 \n\n    纪家是苍秦国的外来住户，因为纪洛妃的爷爷和宁城的爷爷关系不错，两人就帮助宁城和纪洛妃定了娃娃亲。后来纪家莫名其妙的没落下来，只剩下了一个毁容的纪洛妃。但是宁城的爷爷宁弘昌还是将纪洛妃接到了宁家，准备等宁城聚气后，就帮助两人完婚。 \n\n    宁弘昌在别的事情上纵容宁城，可是在娶纪洛妃为妻的这件事上，他决不让步。原来的宁城对被毁容的纪洛妃很是不满意，经常非打即骂，好在纪洛妃逆来顺受，从来都不会对宁城反驳什么。 \n\n    而且纪洛妃和宁城不同，她有资质，可以修炼。不但如此，还在宁家的帮助下，进了苍秦二星学院，并且已经是一个聚气三层的修士。在苍秦国，只要是能修炼的人都叫修士。一旦修士修炼到聚气四层，那地位将直线上升。 \n\n    此时，纪洛妃已经将他背进了一个极为低矮的黑石屋。黑石屋虽然低矮，面积倒也不算很小，至少该有的都有。甚至还有一个小院，院子里面有一口深井。 \n\n    “宁家是怎么被灭掉的？”宁城突然再次问道。就算是关系到他自己，宁城也必须要将这件事弄清楚。尽管他猜测是简家和裘家干的，但是他必须要问明白一些。 \n\n    “你自己做的事情，何必问我？”纪洛妃简单的回答了一句，就将宁城放了下来。 \n\n    说完她又倒了一杯清水喂了宁城，然后说道：“我去烧水给你洗澡，有些事情就忘记了，你知道对你并没有好处。” \n\n    宁城看着纪洛妃进去烧水，并没有继续追问，他明白纪洛妃的意思。以他的能力，知道的越多死的越快而已。就算是他什么都不知道，在这个地方，能不能活下来也是另外一回事。\n\n    不知道纪洛妃是不是在那一杯水中加了一些东西，宁城感觉这一杯水喝下去后，整个人恢复了一些力气。 \n\n    宁城坐在古旧的木榻上，脑海中一会想到现在自己的处境，一会又想到自己的妹妹宁若兰。同时他实在是不明白田慕琬为什么要对他那样，按照他对田慕琬的了解，田慕琬绝对不是那种势利的女孩。', '1509883801', '0', '41', '造化之门', '0', '1509883805', '1024', null, '1', '0', '1515418053', '3');

