  `book_id` int(11) DEFAULT NULL COMMENT '作品id',
  `chapter_id` int(11) DEFAULT NULL COMMENT '章节id',
  `ctime` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='作品章节-订阅表';

