-- ----------------------------
-- Table structure for hxb_collection
-- ----------------------------
DROP TABLE IF EXISTS `hxb_collection`;
CREATE TABLE `hxb_collection` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '收藏表',
  `book_id` int(11) DEFAULT NULL COMMENT '作品id',
  `user_id` int(11) DEFAULT NULL COMMENT '收藏者id',
  `user_name` varchar(33) DEFAULT NULL COMMENT '收藏者姓名',
  `ctime` int(11) DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8mb4 COMMENT='收藏表';

