-- ----------------------------
-- Table structure for hxb_comment
-- ----------------------------
DROP TABLE IF EXISTS `hxb_comment`;
CREATE TABLE `hxb_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `user_id` int(11) DEFAULT NULL COMMENT '评论者id',
  `user_name` varchar(33) DEFAULT NULL COMMENT '评论者姓名',
  `user_img` varchar(255) DEFAULT NULL COMMENT '评论者头像',
  `author_id` int(11) DEFAULT NULL COMMENT '作家id',
  `author_name` varchar(33) DEFAULT NULL COMMENT '作家姓名',
  `book_id` int(11) DEFAULT NULL COMMENT '作品id',
  `book_name` varchar(50) DEFAULT NULL COMMENT '作品名称',
  `content` varchar(200) DEFAULT NULL COMMENT '评论内容',
  `reply` varchar(200) DEFAULT NULL COMMENT '回复内容',
  `content_time` int(11) DEFAULT NULL COMMENT '评论时间',
  `reply_time` int(11) DEFAULT NULL COMMENT '回复时间',
  `chapter_id` int(11) DEFAULT NULL COMMENT '章节id',
  `chapter_title` varchar(100) DEFAULT NULL COMMENT '章节标题',
  `read` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0表示未读,1表示已读',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8mb4 COMMENT='评论表';

