-- ----------------------------
-- Table structure for hxb_message
-- ----------------------------
DROP TABLE IF EXISTS `hxb_message`;
CREATE TABLE `hxb_message` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `user_id` int(11) DEFAULT NULL COMMENT '用户id',
  `user_name` varchar(33) DEFAULT NULL COMMENT '用户姓名',
  `title` varchar(100) DEFAULT NULL COMMENT '留言标题',
  `content` varchar(255) DEFAULT NULL COMMENT '留言内容',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '处理状态:1未处理,2已处理',
  `ctime` int(11) DEFAULT '0' COMMENT '添加时间',
  `tel` varchar(11) DEFAULT NULL COMMENT '手机号码',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8mb4 COMMENT='留言表';

