DROP TABLE IF EXISTS `hxb_upload_img`;
CREATE TABLE `hxb_upload_img` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
  `ext` varchar(8) DEFAULT NULL,
  `type` char(10) DEFAULT NULL,
  `savename` varchar(255) DEFAULT NULL,
  `savepath` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='图片上传表';

