-- ----------------------------
-- Table structure for hxb_user
-- ----------------------------
DROP TABLE IF EXISTS `hxb_user`;
CREATE TABLE `hxb_user` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `name` varchar(33) DEFAULT NULL COMMENT '用户姓名',
  `password` varchar(255) DEFAULT NULL COMMENT '用户密码',
  `img` varchar(100) DEFAULT NULL COMMENT '用户头像',
  `sex` tinyint(1) DEFAULT '1' COMMENT '用户性别 1表示男,2表示女',
  `tel` varchar(11) DEFAULT '0' COMMENT '手机号码',
  `email` varchar(50) DEFAULT NULL COMMENT '电子邮箱',
  `address` varchar(100) DEFAULT NULL COMMENT '用户地址',
  `ctime` int(11) DEFAULT NULL COMMENT '创建时间',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '0表示禁用，1表示启用',
  `intro` varchar(200) DEFAULT NULL COMMENT '个人简介',
  `birthday` int(11) DEFAULT NULL COMMENT '生日',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=42 DEFAULT CHARSET=utf8mb4 COMMENT='用户表';

